erated at https://developers.google.com/+/web/snippet/	\r\n	SNIPPET\r\n	\r\n	<!-- Update your html tag to include the itemscope and itemtype attributes. \r\n<html itemscope itemtype="http://schema.org/Organization">\r\n\r\n<!-- Add the following three tags inside head. \r\n<meta itemprop="name" content="BANK">\r\n<meta itemprop="description" content="a picture">\r\n<meta itemprop="image" content="http://www.gocodigo.com/temporarypages/forte/social_dev/bareJQM/images/Skyburst.jpg">\r\n-->\r\n\r\n<!DOCTYPE html>\r\n<html itemscope itemtype="http://schema.org/Organization">\r\n<head>\r\n  <title>Google+ JavaScript Quickstart</title>\r\n  <script src="https://apis.google.com/js/client:platform.js?onload=startApp" async defer></script>\r\n  <!-- JavaScript specific to this application that is not related to API\r\n     calls -->\r\n  <script src="//ajax.googleapis.com/ajax/libs/jquery/1.8.2/jquery.min.js" ></script>\r\n  <meta name="google-signin-client_id" content="283895304344-r0i49mk6qajn2i2mhpc7a0kqfqqj3mfj.apps.googleusercontent.com"></meta>\r\n  \r\n <!-- INSERT MEDIA    ??  L E F T    O F F   H E R E    -->\r\n  \r\n  \r\n</head>\r\n<body>\r\n  <div id="gConnect">\r\n    <div id="signin-button"></div>\r\n  </div>\r\n  <div id="authOps" style="display:none">\r\n    <h2>User is now signed in to the app using Google+</h2>\r\n    <button id="signOut" onclick="auth2.signOut()">Sign Out</button>\r\n    <p>If the user chooses to disconnect, the app must delete all stored\r\n    information retrieved from Google for the given user.</p>\r\n    <button id="disconnect" >Disconnect your Google account from this app</button>\r\n\r\n    <h2>User''s profile information</h2>\r\n    <div id="profile"></div>\r\n\r\n    <h2>User''s friends that are visible to this app</h2>\r\n    <div id="visiblePeople"></div>\r\n\r\n    <h2>Authentication Logs</h2>\r\n    <pre id="authResult"></pre>\r\n  </div>\r\n  <div id="loaderror">\r\n    This section will be hidden by JQuery. If you can see this message, you\r\n    may be viewing the file rather than running a web server.<br />\r\n    The sample must be run from http or https. See instructions at\r\n    <a href="https://developers.google.com/+/quickstart/javascript">\r\n    https://developers.google.com/+/quickstart/javascript</a>.\r\n  </div>\r\n  \r\n  <div>\r\n  	<p>share photo section:</p>\r\n   \r\n  	\r\n  </div>\r\n  \r\n<script type="text/javascript">\r\nvar auth2 = {};\r\nvar helper = (function() {\r\n  return {\r\n    /**\r\n     * Hides the sign in button and starts the post-authorization operations.\r\n     *\r\n     * @param {Object} authResult An Object which contains the access token and\r\n     *   other authentication information.\r\n     */\r\n    onSignInCallback: function(authResult) {\r\n      $(''#authResult'').html(''Auth Result:<br/>'');\r\n      for (var field in authResult) {\r\n        $(''#authResult'').append('' '' + field + '': '' +\r\n            authResult[field] + ''<br/>'');\r\n      }\r\n      if (authResult.isSignedIn.get()) {\r\n        $(''#authOps'').show(''slow'');\r\n        $(''#gConnect'').hide();\r\n        helper.profile();\r\n        helper.people();\r\n      } else if (authResult[''error''] ||\r\n          authResult.currentUser.get().getAuthResponse() == null) {\r\n        // There was an error, which means the user is not signed in.\r\n        // As an example, you can handle by writing to the console:\r\n        console.log(''There was an error: '' + authResult[''error'']);\r\n        $(''#authResult'').append(''Logged out'');\r\n        $(''#authOps'').hide(''slow'');\r\n        $(''#gConnect'').show();\r\n      }\r\n\r\n      console.log(''authResult'', authResult);\r\n    },\r\n\r\n    /**\r\n     * Calls the OAuth2 endpoint to disconnect the app for the user.\r\n     */\r\n    disconnect: function() {\r\n      // Revoke the access token.\r\n      auth2.disconnect();\r\n    },\r\n\r\n    /**\r\n     * Gets and renders the list of people visible to this app.\r\n     */\r\n    people: function() {\r\n      gapi.client.plus.people.list({\r\n        ''userId'': ''me'',\r\n        ''collection'': ''visible''\r\n      }).then(function(res) {\r\n        var people = res.result;\r\n        $(''#visiblePeople'').empty();\r\n        $(''#visiblePeople'').append(''Number of people visible to this app: '' +\r\n            people.totalItems + ''<br/>'');\r\n        for (var personIndex in people.items) {\r\n          person = people.items[personIndex];\r\n          $(''#visiblePeople'').append(''<img src="'' + person.image.url + ''">'');\r\n        }\r\n      });\r\n    },\r\n\r\n    /**\r\n     * Gets and renders the currently signed in user''s profile data.\r\n     */\r\n    profile: function(){\r\n      gapi.client.plus.people.get({\r\n        ''userId'': ''me''\r\n      }).then(function(res) {\r\n        var profile = res.result;\r\n        console.log(profile);\r\n        $(''#profile'').empty();\r\n        $(''#profile'').append(\r\n            $(''<p><img src=\\"'' + profile.image.url + ''\\"></p>''));\r\n        $(''#profile'').append(\r\n            $(''<p>Hello '' + profile.displayName + ''!<br />Tagline: '' +\r\n            profile.tagline + ''<br />About: '' + profile.aboutMe + ''</p>''));\r\n        if (profile.emails) {\r\n          $(''#profile'').append(''<br/>Emails: '');\r\n          for (var i=0; i < profile.emails.length; i++){\r\n            $(''#profile'').append(profile.emails[i].value).append('' '');\r\n          }\r\n          $(''#profile'').append(''<br/>'');\r\n        }\r\n        if (profile.cover && profile.coverPhoto) {\r\n          $(''#profile'').append(\r\n              $(''<p><img src=\\"'' + profile.cover.coverPhoto.url + ''\\"></p>''));\r\n        }\r\n      }, function(err) {\r\n        var error = err.result;\r\n        $(''#profile'').empty();\r\n        $(''#profile'').append(error.message);\r\n      });\r\n    }\r\n  };\r\n})();\r\n\r\n/**\r\n * jQuery initialization\r\n */\r\n$(document).ready(function() {\r\n  $(''#disconnect'').click(helper.disconnect);\r\n  $(''#loaderror'').hide();\r\n  if ($(''meta'')[0].content == ''YOUR_CLIENT_ID'') {\r\n    alert(''This sample requires your OAuth credentials (client ID) '' +\r\n        ''from the Google APIs console:\\n'' +\r\n        ''    https://code.google.com/apis/console/#:access\\n\\n'' +\r\n        ''Find and replace YOUR_CLIENT_ID with your client ID.''\r\n    );\r\n  }\r\n});\r\n\r\n/**\r\n * Handler for when the sign-in state changes.\r\n *\r\n * @param {boolean} isSignedIn The new signed in state.\r\n */\r\nvar updateSignIn = function() {\r\n  console.log(''update sign in state'');\r\n  if (auth2.isSignedIn.get()) {\r\n    console.log(''signed in'');\r\n    helper.onSignInCallback(gapi.auth2.getAuthInstance());\r\n  }else{\r\n    console.log(''signed out'');\r\n    helper.onSignInCallback(gapi.auth2.getAuthInstance());\r\n  }\r\n}\r\n\r\n/**\r\n * This method sets up the sign-in listener after the client library loads.\r\n */\r\nfunction startApp() {\r\n  gapi.load(''auth2'', function() {\r\n    gapi.client.load(''plus'',''v1'').then(function() {\r\n      gapi.signin2.render(''signin-button'', {\r\n          scope: ''https://www.googleapis.com/auth/plus.login'',\r\n          fetch_basic_profile: false });\r\n      gapi.auth2.init({fetch_basic_profile: false,\r\n          scope:''https://www.googleapis.com/auth/plus.login''}).then(\r\n            function (){\r\n              console.log(''init'');\r\n              auth2 = gapi.auth2.getAuthInstance();\r\n              auth2.isSignedIn.listen(updateSignIn);\r\n              auth2.then(updateSignIn());\r\n            });\r\n    });\r\n  });\r\n}\r\n</script>\r\n</body>\r\n</html>\r\n', '', 1),
(79, 'facebook: login, publish photo and text', '<div data-role="main" class="ui-content  content_wrapper">\r\n\r\n	<script>\r\n		// This is called with the results from from FB.getLoginStatus().\r\n		function statusChangeCallback(response) {\r\n			console.log(''statusChangeCallback'');\r\n			console.log(response);\r\n			// The response object is returned with a status field that lets the\r\n			// app know the current login status of the person.\r\n			// Full docs on the response object can be found in the documentation\r\n			// for FB.getLoginStatus().\r\n			if (response.status === ''connected'') {\r\n				// Logged into your app and Facebook.\r\n\r\n				//ASK FOR PERMISSIONS HERE \r\n				FB.login(function(response) {\r\n				   if (response.authResponse) {\r\n					     var access_token =   FB.getAuthResponse()[''accessToken''];\r\n					     console.log(''Access Token = ''+ access_token);\r\n					     FB.api(''/me'', function(response) {\r\n					     console.log(''Good to see you, '' + response.name + ''.'');\r\n					     reachAPI();\r\n				     });\r\n				   } else {\r\n				     console.log(''User cancelled login or did not fully authorize.'');\r\n				   }\r\n				 }, {scope: ''email,user_likes,user_friends''});\r\n				\r\n				//email,user_likes,publish_actions,user_friends\r\n				\r\n				\r\n				testAPI();\r\n				\r\n			} else if (response.status === ''not_authorized'') {\r\n				// The person is logged into Facebook, but not your app.\r\n				document.getElementById(''status'').innerHTML = ''Please log '' + ''into this app.'';\r\n			} else {\r\n				// The person is not logged into Facebook, so we''re not sure if\r\n				// they are logged into this app or not.\r\n				document.getElementById(''status'').innerHTML = ''Please log '' + ''into Facebook.'';\r\n			}\r\n		}\r\n\r\n		// This function is called when someone finishes with the Login\r\n		// Button.  See the onlogin handler attached to it in the sample\r\n		// code below.\r\n		function checkLoginState() {\r\n			FB.getLoginStatus(function(response) {\r\n				statusChangeCallback(response);\r\n			});\r\n		}\r\n\r\n\r\n		window.fbAsyncInit = function() {\r\n			FB.init({\r\n				appId : 1520107741591606,\r\n				cookie : true, // enable cookies to allow the server to access\r\n				// the session\r\n				xfbml : true, // parse social plugins on this page\r\n				version : ''v2.2'' // use version 2.2\r\n			});\r\n\r\n			// Now that we''ve initialized the JavaScript SDK, we call\r\n			// FB.getLoginStatus().  This function gets the state of the\r\n			// person visiting this page and can return one of three states to\r\n			// the callback you provide.  They can be:\r\n			//\r\n			// 1. Logged into your app (''connected'')\r\n			// 2. Logged into Facebook, but not your app (''not_authorized'')\r\n			// 3. Not logged into Facebook and can''t tell if they are logged into\r\n			//    your app or not.\r\n			//\r\n			// These three cases are handled in the callback function.\r\n\r\n			FB.getLoginStatus(function(response) {\r\n				statusChangeCallback(response);\r\n			});\r\n\r\n		};\r\n\r\n		// Load the SDK asynchronously\r\n		( function(d, s, id) {\r\n				var js, fjs = d.getElementsByTagName(s)[0];\r\n				if (d.getElementById(id))\r\n					return;\r\n				js = d.createElement(s);\r\n				js.id = id;\r\n				js.src = "//connect.facebook.net/en_US/sdk.js";\r\n				fjs.parentNode.insertBefore(js, fjs);\r\n			}(document, ''script'', ''facebook-jssdk''));\r\n\r\n		// Here we run a very simple test of the Graph API after login is\r\n		// successful.  See statusChangeCallback() for when this call is made.\r\n		function testAPI() {\r\n			console.log(''Welcome!  Fetching your information.... '');\r\n			\r\n			FB.api(''/me'', function(response) {\r\n				console.log(''Successful login for: '' + response.name);\r\n				document.getElementById(''status'').innerHTML = ''Thanks for logging in, '' + response.name + ''!'';\r\n			});\r\n			\r\n			\r\n		}\r\n\r\n		function reachAPI() {\r\n			console.log(''Reaching.... '');\r\n			FB.api("/me/friends", function(response) {\r\n				if (response && !response.error) {\r\n					/* handle the result */\r\n					console.log("REACH RESPONSE:" + response);\r\n					console.log(response);\r\n					console.log("friend count: "+ response.summary.total_count);\r\n					document.getElementById(''reach'').innerHTML = ''friend count: ''+ response.summary.total_count;\r\n				} else {\r\n					console.log("REACH RESPONSE ERROR:" + response.error);\r\n				}\r\n			});\r\n		}\r\n	</script>\r\n\r\n	<!--\r\n	Below we include the Login Button social plugin. This button uses\r\n	the JavaScript SDK to present a graphical Login button that triggers\r\n	the FB.login() function when clicked.\r\n	-->\r\n	<h1>Login and Reach</h1>\r\n	<fb:login-button scope="public_profile,email" onlogin="checkLoginState();"></fb:login-button>\r\n\r\n	<div id="status"></div>\r\n\r\n	<div id="reach"></div>\r\n	<hr>\r\n	<h1>Publish Photo</h1>\r\n	<img id="myImage" src="http://www.gocodigo.com/temporarypages/forte/social_media/bareJQM/images/ida_001.jpg">\r\n	 \r\n	<button id="publishPhotoBtn" style="padding-top: 20px">Post a Photo</button>\r\n<script>\r\ndocument.getElementById(''publishPhotoBtn'').onclick = function() {\r\n\r\n	var img_src= document.getElementById("myImage").src;\r\n  var wallPost = {\r\n      message : "testing photo post...",\r\n      picture: img_src\r\n  };\r\n\r\n  FB.api(''/me/feed'', ''post'', wallPost , function(response) {\r\n	  if (!response || response.error) {\r\n	    alert(''Error occured''+response.error);\r\n      console.log(response.error);\r\n	  } else {\r\n	    alert(''Post ID: '' + response);\r\n	  }\r\n	});\r\n  return false;\r\n} \r\n\r\n</script> \r\n<hr>\r\n<h1>Post Text</h1>\r\n<input type="text" id="myPostText" value="" placeholder="post something"/>\r\n\r\n <button id="publishBtn" style="padding-top: 20px">Post a Text</button>\r\n\r\n<script>\r\ndocument.getElementById(''publishBtn'').onclick = function() {\r\n\r\n	var msg = document.getElementById("myPostText").value;\r\n  FB.api(''/me/feed'', ''post'', {message: msg}, function(response) {\r\n    Log.info(''API response'', response);\r\n    document.getElementById(''publishBtn'').innerHTML = ''API response is '' + response.id;\r\n  });\r\n  return false;\r\n}  \r\n</script>\r\n\r\n\r\n</div>', '', 1),
(80, 'TWITTER API', '<?php\r\n \r\n/*\r\nTWITTER API : HOW TO : 2 FILES IN ONE\r\n\r\n\r\nhttp://aamirafridi.com/twitter/twitter-api-1-1-using-php-and-javascript\r\nhttp://stackoverflow.com/questions/17979901/twitter-api-1-1-returning-null\r\n\r\n*/\r\n\r\n\r\nheader(''Content-Type: application/json'');\r\n\r\nini_set(''display_errors'', 1);\r\n\r\nrequire_once(''TwitterAPIExchange.php'');\r\n\r\n/** Set access tokens here - see: https://dev.twitter.com/apps/ **/\r\n$settings = array(\r\n    ''oauth_access_token'' => "364828210-XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX",\r\n    ''oauth_access_token_secret'' => "XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX",\r\n    ''consumer_key'' => "XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX",\r\n    ''consumer_secret'' => "XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX"\r\n);\r\n\r\n/** URL for REST request, see: https://dev.twitter.com/docs/api/1.1/ **/\r\n$url = ''https://api.twitter.com/1.1/blocks/create.json'';\r\n\r\n$requestMethod = ''POST'';\r\n\r\n/** POST fields required by the URL above. See relevant docs as above **/\r\n$postfields = array(\r\n    ''screen_name'' => ''usernameToBlock'', \r\n    ''skip_status'' => ''1''\r\n);\r\n\r\n/** Perform a POST request and echo the response **/\r\n$twitter = new TwitterAPIExchange($settings);\r\necho $twitter->buildOauth($url, $requestMethod)\r\n             ->setPostfields($postfields)\r\n             ->performRequest();\r\n\r\n/** Perform a GET request and echo the response **/\r\n/** Note: Set the GET field BEFORE calling buildOauth(); **/\r\n//-1 $url = ''https://api.twitter.com/1.1/followers/ids.json'';\r\n//-2 $url = ''https://api.twitter.com/1.1/statuses/home_timeline.json'';\r\n$url = ''https://api.twitter.com/1.1/friends/list.json'';\r\n$getfield = ''?screen_name=J7mbo'';\r\n$requestMethod = ''GET'';\r\n \r\n $twitter = new TwitterAPIExchange($settings);\r\n echo $twitter->setGetfield($getfield)\r\n             ->buildOauth($url, $requestMethod)\r\n             ->performRequest();\r\n              \r\n?>\r\n\r\n<?php\r\n\r\n/**\r\n * Twitter-API-PHP : Simple PHP wrapper for the v1.1 API\r\n * \r\n * PHP version 5.3.10\r\n * \r\n * @category Awesomeness\r\n * @package  Twitter-API-PHP\r\n * @author   James Mallison <me@j7mbo.co.uk>\r\n * @license  MIT License\r\n * @link     http://github.com/j7mbo/twitter-api-php\r\n */\r\nclass TwitterAPIExchange\r\n{\r\n    private $oauth_access_token;\r\n    private $oauth_access_token_secret;\r\n    private $consumer_key;\r\n    private $consumer_secret;\r\n    private $postfields;\r\n    private $getfield;\r\n    protected $oauth;\r\n    public $url;\r\n\r\n    /**\r\n     * Create the API access object. Requires an array of settings::\r\n     * oauth access token, oauth access token secret, consumer key, consumer secret\r\n     * These are all available by creating your own application on dev.twitter.com\r\n     * Requires the cURL library\r\n     * \r\n     * @param array $settings\r\n     */\r\n    public function __construct(array $settings)\r\n    {\r\n        if (!in_array(''curl'', get_loaded_extensions())) \r\n        {\r\n            throw new Exception(''You need to install cURL, see: http://curl.haxx.se/docs/install.html'');\r\n        }\r\n        \r\n        if (!isset($settings[''oauth_access_token''])\r\n            || !isset($settings[''oauth_access_token_secret''])\r\n            || !isset($settings[''consumer_key''])\r\n            || !isset($settings[''consumer_secret'']))\r\n        {\r\n            throw new Exception(''Make sure you are passing in the correct parameters'');\r\n        }\r\n\r\n        $this->oauth_access_token = $settings[''oauth_access_token''];\r\n        $this->oauth_access_token_secret = $settings[''oauth_access_token_secret''];\r\n        $this->consumer_key = $settings[''consumer_key''];\r\n        $this->consumer_secret = $settings[''consumer_secret''];\r\n    }\r\n    \r\n    /**\r\n     * Set postfields array, example: array(''screen_name'' => ''J7mbo'')\r\n     * \r\n     * @param array $array Array of parameters to send to API\r\n     * \r\n     * @return TwitterAPIExchange Instance of self for method chaining\r\n     */\r\n    public function setPostfields(array $array)\r\n    {\r\n        if (!is_null($this->getGetfield())) \r\n        { \r\n            throw new Exception(''You can only choose get OR post fields.''); \r\n        }\r\n        \r\n        if (isset($array[''status'']) && substr($array[''status''], 0, 1) === ''@'')\r\n        {\r\n            $array[''status''] = sprintf("\\0%s", $array[''status'']);\r\n        }\r\n        \r\n        $this->postfields = $array;\r\n        \r\n        return $this;\r\n    }\r\n    \r\n    /**\r\n     * Set getfield string, example: ''?screen_name=J7mbo''\r\n     * \r\n     * @param string $string Get key and value pairs as string\r\n     * \r\n     * @return \\TwitterAPIExchange Instance of self for method chaining\r\n     */\r\n    public function setGetfield($string)\r\n    {\r\n        if (!is_null($this->getPostfields())) \r\n        { \r\n            throw new Exception(''You can only choose get OR post fields.''); \r\n        }\r\n        \r\n        $search = array(''#'', '','', ''+'', '':'');\r\n        $replace = array(''%23'', ''%2C'', ''%2B'', ''%3A'');\r\n        $string = str_replace($search, $replace, $string);  \r\n        \r\n        $this->getfield = $string;\r\n        \r\n        return $this;\r\n    }\r\n    \r\n    /**\r\n     * Get getfield string (simple getter)\r\n     * \r\n     * @return string $this->getfields\r\n     */\r\n    public function getGetfield()\r\n    {\r\n        return $this->getfield;\r\n    }\r\n    \r\n    /**\r\n     * Get postfields array (simple getter)\r\n     * \r\n     * @return array $this->postfields\r\n     */\r\n    public function getPostfields()\r\n    {\r\n        return $this->postfields;\r\n    }\r\n    \r\n    /**\r\n     * Build the Oauth object using params set in construct and additionals\r\n     * passed to this method. For v1.1, see: https://dev.twitter.com/docs/api/1.1\r\n     * \r\n     * @param string $url The API url to use. Example: https://api.twitter.com/1.1/search/tweets.json\r\n     * @param string $requestMethod Either POST or GET\r\n     * @return \\TwitterAPIExchange Instance of self for method chaining\r\n     */\r\n    public function buildOauth($url, $requestMethod)\r\n    {\r\n        if (!in_array(strtolower($requestMethod), array(''post'', ''get'')))\r\n        {\r\n            throw new Exception(''Request method must be either POST or GET'');\r\n        }\r\n        \r\n        $consumer_key = $this->consumer_key;\r\n        $consumer_secret = $this->consumer_secret;\r\n        $oauth_access_token = $this->oauth_access_token;\r\n        $oauth_access_token_secret = $this->oauth_access_token_secret;\r\n        \r\n        $oauth = array( \r\n            ''oauth_consumer_key'' => $consumer_key,\r\n            ''oauth_nonce'' => time(),\r\n            ''oauth_signature_method'' => ''HMAC-SHA1'',\r\n            ''oauth_token'' => $oauth_access_token,\r\n            ''oauth_timestamp'' => time(),\r\n            ''oauth_version'' => ''1.0''\r\n        );\r\n        \r\n        $getfield = $this->getGetfield();\r\n        \r\n        if (!is_null($getfield))\r\n        {\r\n            $getfields = str_replace(''?'', '''', explode(''&'', $getfield));\r\n            foreach ($getfields as $g)\r\n            {\r\n                $split = explode(''='', $g);\r\n                $oauth[$split[0]] = $split[1];\r\n            }\r\n        }\r\n        \r\n        $base_info = $this->buildBaseString($url, $requestMethod, $oauth);\r\n        $composite_key = rawurlencode($consumer_secret) . ''&'' . rawurlencode($oauth_access_token_secret);\r\n        $oauth_signature = base64_encode(hash_hmac(''sha1'', $base_info, $composite_key, true));\r\n        $oauth[''oauth_signature''] = $oauth_signature;\r\n        \r\n        $this->url = $url;\r\n        $this->oauth = $oauth;\r\n        \r\n        return $this;\r\n    }\r\n    \r\n    /**\r\n     * Perform the actual data retrieval from the API\r\n     * \r\n     * @param boolean $return If true, returns data.\r\n     * \r\n     * @return string json If $return param is true, returns json data.\r\n     */\r\n    public function performRequest($return = true)\r\n    {\r\n        if (!is_bool($return)) \r\n        { \r\n            throw new Exception(''performRequest parameter must be true or false''); \r\n        }\r\n        \r\n        $header = array($this->buildAuthorizationHeader($this->oauth), ''Expect:'');\r\n        \r\n        $getfield = $this->getGetfield();\r\n        $postfields = $this->getPostfields();\r\n\r\n        $options = array( \r\n            CURLOPT_HTTPHEADER => $header,\r\n            CURLOPT_HEADER => false,\r\n            CURLOPT_URL => $this->url,\r\n            CURLOPT_RETURNTRANSFER => true,\r\n            CURLOPT_TIMEOUT => 10,\r\n            CURLOPT_SSL_VERIFYPEER => false,\r\n        );\r\n\r\n        if (!is_null($postfields))\r\n        {\r\n            $options[CURLOPT_POSTFIELDS] = $postfields;\r\n        }\r\n        else\r\n        {\r\n            if ($getfield !== '''')\r\n            {\r\n                $options[CURLOPT_URL] .= $getfield;\r\n            }\r\n        }\r\n\r\n        $feed = curl_init();\r\n        curl_setopt_array($feed, $options);\r\n        $json = curl_exec($feed);\r\n        curl_close($feed);\r\n\r\n        if ($return) { return $json; }\r\n    }\r\n    \r\n    /**\r\n     * Private method to generate the base string used by cURL\r\n     * \r\n     * @param string $baseURI\r\n     * @param string $method\r\n     * @param array $params\r\n     * \r\n     * @return string Built base string\r\n     */\r\n    private function buildBaseString($baseURI, $method, $params) \r\n    {\r\n        $return = array();\r\n        ksort($params);\r\n        \r\n        foreach($params as $key=>$value)\r\n        {\r\n            $return[] = "$key=" . $value;\r\n        }\r\n        \r\n        return $method . "&" . rawurlencode($baseURI) . ''&'' . rawurlencode(implode(''&'', $return)); \r\n    }\r\n    \r\n    /**\r\n     * Private method to generate authorization header used by cURL\r\n     * \r\n     * @param array $oauth Array of oauth data generated by buildOauth()\r\n     * \r\n     * @return string $return Header used by cURL for request\r\n     */    \r\n    private function buildAuthorizationHeader($oauth) \r\n    {\r\n        $return = ''Authorization: OAuth '';\r\n        $values = array();\r\n        \r\n        foreach($oauth as $key => $value)\r\n        {\r\n            $values[] = "$key=\\"" . rawurlencode($value) . "\\"";\r\n        }\r\n        \r\n        $return .= implode('', '', $values);\r\n        return $return;\r\n    }\r\n\r\n}\r\n', '', 1),
