  //echo "<br>" . $posted_date;\r\n           if(!is_null($posted_date)){\r\n               array_push($result,$posted_date);\r\n               //$array_of_posted_dates = array();\r\n          }\r\n           \r\n        }\r\n        if (strpos($line, ''updated:'') !== false) {\r\n           // echo "<br>" . $line;\r\n        }\r\n\r\n    }\r\n    return $result;\r\n}\r\n//craigslist.org//count($array_of_links)\r\nfor($i=22;$i<50;$i++){\r\n    if (strpos($array_of_links[$i], ''/grd/'') !== false  && strpos($array_of_links[$i], ''craigslist.org'') == false) {\r\n           // echo "<br> - " . $array_of_links[$i];\r\n            $result = getOne($array_of_links[$i]);\r\n            array_push($final_array,$result);\r\n        }\r\n   // getOne($array_of_links[$i]);\r\n}\r\n//echo("<pre>");print_r($array_of_posted_dates); echo("</pre>"); \r\n$res = getOne($array_of_links[110]);\r\necho(''<br>got results:'');\r\nprint_r($res);\r\necho("<pre>");print_r($final_array); echo("</pre>"); \r\necho("<hr><p>Total List of Links:</p>");\r\n\r\necho("<pre>");\r\nprint_r($array_of_links);\r\necho("</pre>");\r\n?>', '', 3),
