(215, 'basic api', '<?php\r\n/*\r\nTRY THIS: http://localhost:8888/00_api_communitywall.php/documents/\r\n//I CAN GET\r\n//ICAN GET BY ID\r\n// I CAN NOT PUT\r\n\r\n//I HAVE NOT TRIED DELETE\r\n\r\nRunning\r\nSave this file in your (Apache) document root and call it using:\r\n\r\nhttp://localhost:8888/api.php/{$table}/{$id}\r\n\r\n\r\n\r\n/////////////////////////////////////////////////////////////////////////////////////////////\r\n\r\n/00_api_communitywall.php/documents/\r\n\r\n/// snippet 1\r\n//curl -v http://localhost:8888/00_api_communitywall.php/documents/23\r\n\r\n//curl -v http://localhost:8888/00_api_communitywall.php/documents/\r\n\r\n//curl -v -X PUT http://localhost:8888/00_api_communitywall.php/documents/15 -d ''{"type":"BatMobile" }\r\n\r\n\r\n//curl -v -X DELETE http://localhost:8888/00_api_communitywall.php/documents/anne\r\n///////////////////////////////////////////////////////////////////////////////////////////\r\n*/\r\n\r\n$HOST="localhost";\r\n$USER="root";\r\n$PASS="";\r\n$DB="communitywall";\r\n\r\n// get the HTTP method, path and body of the request\r\n$method = $_SERVER[''REQUEST_METHOD''];\r\n$request = explode(''/'', trim($_SERVER[''PATH_INFO''],''/''));\r\n\r\n$input = json_decode(file_get_contents(''php://input''),true);\r\n\r\n// connect to the mysql database\r\n$link = mysqli_connect($HOST,$USER,$PASS, $DB);\r\nmysqli_set_charset($link,''utf8'');\r\n\r\n// retrieve the table and key from the path\r\n$table = preg_replace(''/[^a-z0-9_]+/i'','''',array_shift($request));\r\n$key = array_shift($request)+0;\r\n\r\n// escape the columns and values from the input object\r\n$columns = preg_replace(''/[^a-z0-9_]+/i'','''',array_keys($input));\r\n$values = array_map(function ($value) use ($link) {\r\n  if ($value===null) return null;\r\n  return mysqli_real_escape_string($link,(string)$value);\r\n},array_values($input));\r\n\r\n// build the SET part of the SQL command\r\n$set = '''';\r\nfor ($i=0;$i<count($columns);$i++) {\r\n  $set.=($i>0?'','':'''').''`''.$columns[$i].''`='';\r\n  $set.=($values[$i]===null?''NULL'':''"''.$values[$i].''"'');\r\n}\r\n\r\n// create SQL based on HTTP method\r\nswitch ($method) {\r\n  case ''GET'':\r\n    $sql = "select * from `$table`".($key?" WHERE id=$key":''''); break;\r\n  case ''PUT'':\r\n    $sql = "update `$table` set $set where id=$key"; break;\r\n  case ''POST'':\r\n    $sql = "insert into `$table` set $set"; break;\r\n  case ''DELETE'':\r\n    $sql = "delete `$table` where id=$key"; break;\r\n}\r\n\r\n// excecute SQL statement\r\n$result = mysqli_query($link,$sql);\r\n\r\n// die if SQL statement failed\r\nif (!$result) {\r\n  http_response_code(404);\r\n  die(mysqli_error());\r\n}\r\n\r\n// print results, insert id or affected row count\r\nif ($method == ''GET'') {\r\n  if (!$key) echo ''['';\r\n  for ($i=0;$i<mysqli_num_rows($result);$i++) {\r\n    echo ($i>0?'','':'''').json_encode(mysqli_fetch_object($result));\r\n  }\r\n  if (!$key) echo '']'';\r\n} elseif ($method == ''POST'') {\r\n  echo mysqli_insert_id($link);\r\n} else {\r\n  echo mysqli_affected_rows($link);\r\n}\r\n\r\n// close mysql connection\r\nmysqli_close($link);\r\n\r\n?>\r\n', '', 3),
