(463, 'angular http notes', 'notes specific to angular4 tour of hero data.\r\n0. app.module.ts\r\n    HttpModule\r\n    HeroService\r\n    InMemoryDataService\r\n     and\r\n    InMemoryDataService\r\n\r\n\r\nI. hero.ts\r\n\r\nII. hero.service.ts\r\n    Injectable\r\n    Headers, Http\r\n    rxjs/add/operator/toPromise\r\n    Hero\r\n    @Injectable.....\r\n\r\n    heroesUrl = ''api/heroes'';// ?\r\n    crud methods and \r\n   error handler code\r\n\r\n\r\n\r\nIII. in-memory-data.service.ts\r\n     array of hero objects with data.\r\n        ---> src/systemjs.config.js ''angular-in-memory-web-api''\r\n\r\n    InMemoryDbService\r\n    export class InMemoryDataService implements InMemoryDbService\r\n\r\nIV. dashboard.component.ts\r\n    Hero\r\n    HeroService\r\n    export class....\r\n           constructor(private heroService: HeroService){}\r\n    ngOnInit():void{\r\n        this.heroService.getHeroes()\r\n        .then(heroes => this.heroes = heroes.slice(0,4));\r\n}\r\n\r\nV. dashboard.component.html\r\n     <a *ngFor="let hero of heroes" [routerLink]="[''/detail'',hero.id]" >\r\n          <p>{{hero.name}}</p>\r\n      </a>\r\n\r\n////////////////////////////////////////////////////////\r\n=======================================================\r\n== app.module.ts\r\n========================================================\r\nimport { NgModule }      from ''@angular/core'';\r\nimport { BrowserModule } from ''@angular/platform-browser'';\r\nimport { FormsModule }   from ''@angular/forms'';\r\nimport { HttpModule }    from ''@angular/http'';\r\n\r\nimport { AppRoutingModule } from ''./app-routing.module'';\r\n\r\n// Imports for loading & configuring the in-memory web api\r\nimport { InMemoryWebApiModule } from ''angular-in-memory-web-api'';\r\nimport { InMemoryDataService }  from ''./in-memory-data.service'';\r\n\r\nimport { AppComponent }         from ''./app.component'';\r\nimport { DashboardComponent }   from ''./dashboard.component'';\r\nimport { HeroesComponent }      from ''./heroes.component'';\r\nimport { HeroDetailComponent }  from ''./hero-detail.component'';\r\nimport { HeroService }          from ''./hero.service'';\r\nimport { HeroSearchComponent }  from ''./hero-search.component'';\r\n\r\n@NgModule({\r\n  imports: [\r\n    BrowserModule,\r\n    FormsModule,\r\n    HttpModule,\r\n    InMemoryWebApiModule.forRoot(InMemoryDataService),\r\n    AppRoutingModule\r\n  ],\r\n  declarations: [\r\n    AppComponent,\r\n    DashboardComponent,\r\n    HeroDetailComponent,\r\n    HeroesComponent,\r\n    HeroSearchComponent\r\n  ],\r\n  providers: [ HeroService ],\r\n  bootstrap: [ AppComponent ]\r\n})\r\nexport class AppModule { }\r\n=======================================================\r\n== hero.ts\r\n========================================================\r\n"use strict";\r\nObject.defineProperty(exports, "__esModule", { value: true });\r\nvar Hero = (function () {\r\n    function Hero() {\r\n    }\r\n    return Hero;\r\n}());\r\nexports.Hero = Hero;\r\n//# sourceMappingURL=hero.js.map\r\n=======================================================\r\n== hero.service.ts\r\n========================================================\r\nimport { Injectable }    from ''@angular/core'';\r\nimport { Headers, Http } from ''@angular/http'';\r\n\r\nimport ''rxjs/add/operator/toPromise'';\r\n\r\nimport { Hero } from ''./hero'';\r\n\r\n@Injectable()\r\nexport class HeroService {\r\n\r\n  private headers = new Headers({''Content-Type'': ''application/json''});\r\n  private heroesUrl = ''api/heroes'';  // URL to web api\r\n\r\n  constructor(private http: Http) { }\r\n\r\n  getHeroes(): Promise<Hero[]> {\r\n    return this.http.get(this.heroesUrl)\r\n               .toPromise()\r\n               .then(response => response.json().data as Hero[])\r\n               .catch(this.handleError);\r\n  }\r\n\r\n\r\n  getHero(id: number): Promise<Hero> {\r\n    const url = `${this.heroesUrl}/${id}`;\r\n    return this.http.get(url)\r\n      .toPromise()\r\n      .then(response => response.json().data as Hero)\r\n      .catch(this.handleError);\r\n  }\r\n\r\n  delete(id: number): Promise<void> {\r\n    const url = `${this.heroesUrl}/${id}`;\r\n    return this.http.delete(url, {headers: this.headers})\r\n      .toPromise()\r\n      .then(() => null)\r\n      .catch(this.handleError);\r\n  }\r\n\r\n  create(name: string): Promise<Hero> {\r\n    return this.http\r\n      .post(this.heroesUrl, JSON.stringify({name: name}), {headers: this.headers})\r\n      .toPromise()\r\n      .then(res => res.json().data as Hero)\r\n      .catch(this.handleError);\r\n  }\r\n\r\n  update(hero: Hero): Promise<Hero> {\r\n    const url = `${this.heroesUrl}/${hero.id}`;\r\n    return this.http\r\n      .put(url, JSON.stringify(hero), {headers: this.headers})\r\n      .toPromise()\r\n      .then(() => hero)\r\n      .catch(this.handleError);\r\n  }\r\n\r\n  private handleError(error: any): Promise<any> {\r\n    console.error(''An error occurred'', error); // for demo purposes only\r\n    return Promise.reject(error.message || error);\r\n  }\r\n}\r\n\r\n\r\n=======================================================\r\n== in-memory-data.service.ts\r\n========================================================\r\nimport { InMemoryDbService } from ''angular-in-memory-web-api'';\r\nexport class InMemoryDataService implements InMemoryDbService {\r\n  createDb() {\r\n    let heroes = [\r\n\r\n      {id: 12, name: ''Brief'', description: ''9 years of Professional Experience''},\r\n      {id: 13, name: ''Technical'', description: ''PHP Javascript MySql''},\r\n      {id: 14, name: ''Experience'',description:''JPPA, Codigo, WestIPC, Indatus, Aircom'' },\r\n      {id: 15, name: ''Examples'', description:''http://www.forteworks.com'' },\r\n\r\n    ];\r\n    return {heroes};\r\n  }\r\n}\r\n\r\n\r\n=======================================================\r\n== dashboard.component.ts\r\n========================================================\r\nimport { Component, OnInit } from ''@angular/core'';\r\n\r\nimport { Hero }        from ''./hero'';\r\nimport { HeroService } from ''./hero.service'';\r\n\r\n@Component({\r\n  selector: ''my-dashboard'',\r\n  templateUrl: ''./dashboard.component.html'',\r\n  styleUrls: [ ''./dashboard.component.css'' ]\r\n})\r\nexport class DashboardComponent implements OnInit {\r\n  heroes: Hero[] = [];\r\n\r\n  constructor(private heroService: HeroService) { }\r\n\r\n  ngOnInit(): void {\r\n    this.heroService.getHeroes()\r\n      .then(heroes => this.heroes = heroes.slice(0, 4));//\r\n  }\r\n}\r\n\r\n\r\n=======================================================\r\n== dashboard.component.html\r\n========================================================\r\n<h3>Dashboard</h3>\r\n<div class="grid grid-pad">\r\n  <a *ngFor="let hero of heroes"  [routerLink]="[''/detail'', hero.id]"  class="col-1-4">\r\n    <div class="module hero">\r\n      <h4>{{hero.name}}</h4>\r\n    </div>\r\n  </a>\r\n</div>\r\n<hero-search></hero-search>\r\n\r\n\r\n\r\n', '', 3),
