(440, 'PDO php mysql CRUD', '<?php\r\n// Simple PHP PDO CRUD example \r\n\r\ndefine("HOSTNAME","localhost");\r\ndefine("USERNAME","root");\r\ndefine("PASSWORD","root");\r\ndefine("DATABASE","students");\r\ndefine("CHARSET","utf8");\r\n\r\n$dbCon = new PDO(''mysql:host=''.HOSTNAME.'';dbname=''.DATABASE.'';charset=''.CHARSET.'';'',''''.PASSWORD.'''',''''.USERNAME.'''');\r\n\r\n//var_dump($dbCon);//check connection\r\n\r\n//INSERT\r\n$studid = ''909'';\r\n$studname = ''Casper'';\r\n$sql = ''INSERT INTO students(studid,studname) VALUES(:studid,:studname)'';\r\n$stmt = $dbCon->prepare($sql);\r\n$stmt->bindValue('':studid'', $studid, PDO::PARAM_STR);\r\n$stmt->bindValue('':studname'', $studname, PDO::PARAM_STR);\r\n$stmt->execute();\r\n//$stmt->execute(array(''777'',"monkey"));\r\n\r\n//SELECT \r\n$stmt = $dbCon->query(''SELECT * FROM students'');\r\nwhile($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\r\n 	$data[] = $row;\r\n}\r\necho("<pre>"); print_r($data); echo("</pre>");\r\n\r\n//UPDATE\r\n$studname="muchamacha";\r\n$studid=13;\r\n$stmt = $dbCon->prepare("UPDATE students SET studname=? WHERE studid=?");\r\n$stmt->execute(array($studname, $studid));\r\n$affected_rows = $stmt->rowCount();\r\n\r\n//DELETE\r\n$studid=888;\r\n$stmt = $dbCon->prepare("DELETE FROM students WHERE studid=:studid");\r\n$stmt->bindValue('':studid'', $studid, PDO::PARAM_STR);\r\n$stmt->execute();\r\n$affected_rows = $stmt->rowCount();\r\n\r\n\r\n?>', '', 3),
