(443, 'wiki pedia api call example', '<!DOCTYPE html>\r\n<html>\r\n\r\n<head>\r\n    <meta charset=''utf-8''>\r\n    <meta name="viewport" content="width=device-width, initial-scale=1.0, user-scalable=no">\r\n    <!-- <link rel=''stylesheet'' type=''text/css'' href=''style.css''> -->\r\n    <script src="https://code.jquery.com/jquery-3.2.1.min.js" integrity="sha256-hwg4gsxgFZhOsEEamdOYGBf13FyQuiTwlAQgxVSNgt4=" crossorigin="anonymous">\r\n    </script>\r\n    <title>Wiki-P API</title>\r\n\r\n</head>\r\n\r\n<body>\r\n    <div class="container">\r\n        <h1>wikipedia search</h1>\r\n        <h4>FCC Front End Development</h4>\r\n        <a href="https:;//en.wikipedia.org/wiki/Special:Random" target="_blank">\r\n    W wikipedia image here\r\n    </a>\r\n\r\n        <input class="form-control" id="searchTerm" value="monkey">\r\n        <button id="search" type="button" class="btn btn-primary">submit</button>\r\n\r\n        <ul id="output">\r\n\r\n        </ul>\r\n\r\n    </div>\r\n    <script>\r\n        $(document).ready(function(){\r\n\r\n            $("#search").click(function(){\r\n\r\n                var searchTerm = $("#searchTerm").val();\r\n                var url = "https://en.wikipedia.org/w/api.php?action=opensearch&search="+searchTerm+"&format=json&callback=?;";\r\n\r\n\r\n                $.ajax({\r\n                    type:"GET",\r\n                    url : url,\r\n                    async : false,\r\n                    dataType: "jsonp",\r\n                    success: function(data){\r\n                        //console.log(data);\r\n                        for (var i=0, max=data.length; i < max; i++) {\r\n                                //console.log("-->"+data[i][''id'']);\r\n                                //etc...\r\n                                //console.log(data[i]);\r\n                            for (var j=0, tot=data.length; j < tot; j++) {\r\n                                //console.log("-->"+data[i][''id'']);\r\n                        