(720, 'intersection', '<script>\r\n/* Intersection Observvation Lazy Loading Work in progress..\r\ntrying to add functionality to add necessary classes to html not currently supplied with necessary \r\nclasses...etc.\r\n*/\r\n//Regular IMG elements\r\n//echo(''<div class="row "><img class="lazyloadimg"      src="''.$url.''" height=1000px width=auto alt="''.$url.''" /></div>''); \r\n// BAKCGROUND IMAGE URL \r\n// echo(''<div class="lazyloadimg"  style="background-image:url(''.$url.'');height:100px;width:auto;" ></div>'');\r\n\r\nfunction loadTheImgs(e){\r\n	if(e.hasAttribute(''data-lzbgimg'')){\r\n		let bgImg = e.dataset.lzbgimg;\r\n		e.style.backgroundImage = ''url('' + bgImg + '')'';\r\n	}\r\n	if(e.hasAttribute(''data-lzimg'')){\r\n		e.setAttribute(''src'', e.getAttribute(''data-lzimg''));\r\n	}\r\n}\r\n\r\nfunction prepareTheImgs(e){\r\n        /* handle background images*/\r\n        /* No luck with background images yet.\r\n        // vanilla js get the background image url()\r\n        style = e.currentStyle || window.getComputedStyle(e, false),\r\n        bi = style.backgroundImage.slice(4, -1).replace(/"/g, "");\r\n        //console.log(style.backgroundImage);\r\n        var att_bg = document.createAttribute("data-lzbgimg"); \r\n        att_bg.value="";\r\n        e.setAttribute(''data-lzbgimg'', bi);\r\n        //NEEDED FOR BACKGROUND IMAGE: ?\r\n        if(e.hasAttribute(''data-lzbgimg'')){\r\n            let bgImg = e.dataset.lzbgimg;\r\n            e.style.backgroundImage = ''url('' + bgImg + '')'';\r\n        } \r\n        */\r\n\r\n    //handle image elements\r\n    /* THIS CODE WORKS FOR REGULAR IMAGE ELEMENTS: */\r\n	if(e.hasAttribute(''src'') && !e.hasAttribute(''data-lzimg'')){\r\n        var att = document.createAttribute("data-lzimg");       // Create a "class" attribute\r\n        att.value = "";  \r\n        e.setAttribute(''data-lzimg'', e.getAttribute(''src''));\r\n        e.setAttribute(''src'',  '''');\r\n    }\r\n   \r\n}\r\n\r\ndocument.addEventListener("DOMContentLoaded", function(){\r\n    var isUsingIO = true;\r\n    if(isUsingIO){\r\n            const obsrvConfig = {\r\n                rootMargin: ''50px 0px'',\r\n                threshold: 0.01\r\n            };\r\n            //get images wish to be lazyloaded\r\n            const lazyImages = document.querySelectorAll(''.lazyloadimg'');\r\n            //make sure they are really there\r\n            if(lazyImages.length > 0){\r\n                console.log(''number of images: 2.0.7  ''+ lazyImages.length);\r\n                lazyImages.forEach(lazyImage => prepareTheImgs(lazyImage));\r\n                //if IntersectionObserver not supported load all images\r\n                if(!window.IntersectionObserver){\r\n                    lazyImages.forEach(lazyImage => loadTheImgs(lazyImage));\r\n                }else{\r\n                    let observer = new IntersectionObserver((entries) => {\r\n                        entries.forEach(entry => {\r\n                            if(entry.intersectionRatio > 0){\r\n                                loadTheImgs(entry.target)\r\n                            }\r\n                        })\r\n                    }, obsrvConfig);\r\n                    lazyImages.forEach(lazyImage => {observer.observe(lazyImage)});\r\n                }\r\n            }\r\n        }//end isUsingIO\r\n});\r\n/*\r\nFor CSS background images add a data attribute of "lzbgimg" to the element and remove any CSS rules that apply a background image, the result should look something like this:\r\n\r\n<div class="lazyloadimg" data-lzbgimg="/dir/someimage.jpg"></div>\r\n*/\r\n</script>', '', 3),
(721, 'virtual dom like react', '/** @jsx h */\r\n\r\nfunction h(type, props, ...children) {\r\n  return { type, props, children };\r\n}\r\n\r\nfunction createElement(node) {\r\n  if (typeof node === ''string'') {\r\n    return document.createTextNode(node);\r\n  }\r\n  const $el = document.createElement(node.type);\r\n  node.children\r\n    .map(createElement)\r\n    .forEach($el.appendChild.bind($el));\r\n  return $el;\r\n}\r\n\r\nfunction changed(node1, node2) {\r\n  return typeof node1 !== typeof node2 ||\r\n         typeof node1 === ''string'' && node1 !== node2 ||\r\n         node1.type !== node2.type\r\n}\r\n\r\nfunction updateElement($parent, newNode, oldNode, index = 0) {\r\n  if (!oldNode) {\r\n    $parent.appendChild(\r\n      createElement(newNode)\r\n    );\r\n  } else if (!newNode) {\r\n    $parent.removeChild(\r\n      $parent.childNodes[index]\r\n    );\r\n  } else if (changed(newNode, oldNode)) {\r\n    $parent.replaceChild(\r\n      createElement(newNode),\r\n      $parent.childNodes[index]\r\n    );\r\n  } else if (newNode.type) {\r\n    const newLength = newNode.children.length;\r\n    const oldLength = oldNode.children.length;\r\n    for (let i = 0; i < newLength || i < oldLength; i++) {\r\n      updateElement(\r\n        $parent.childNodes[index],\r\n        newNode.children[i],\r\n        oldNode.children[i],\r\n        i\r\n      );\r\n    }\r\n  }\r\n}\r\n\r\n// ---------------------------------------------------------------------\r\n\r\nconst a = (\r\n  <ul>\r\n    <li>item 1</li>\r\n    <li>item 2</li>\r\n  </ul>\r\n);\r\n\r\nconst b = (\r\n  <ul>\r\n    <li>item 1</li>\r\n    <li>hello!</li>\r\n  </ul>\r\n);\r\n\r\nconst $root = document.getElementById(''root'');\r\nconst $reload = document.getElementById(''reload'');\r\n\r\nupdateElement($root, a);\r\n$reload.addEventListener(''click'', () => {\r\n  updateElement($root, b, a);\r\n});\r\n', '', 3),
