(82, '&lt;?php echo(__FILE__); ?&gt;\r\n\r\n&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n&lt;meta charset=&quot;UTF-8&quot;&gt;\r\n&lt;!--\r\n&lt;link rel=&quot;stylesheet&quot; href=&quot;dialog.css&quot;&gt;\r\n &lt;script src=&quot;dialog.js&quot;&gt;&lt;/script&gt; --&gt;\r\n&lt;style&gt;\r\n	#dialogoverlay{\r\n	display: none;\r\n	opacity: .8;\r\n	position: fixed;\r\n	top: 0px;\r\n	left: 0px;\r\n	background: #FFF;\r\n	width: 100%;\r\n	z-index: 10;\r\n}\r\n#dialogbox{\r\n	display: none;\r\n	position: fixed;\r\n	background: #000;\r\n	border-radius:7px; \r\n	width:550px;\r\n	z-index: 10;\r\n}\r\n#dialogbox &gt; div{ background:#FFF; margin:8px; }\r\n#dialogbox &gt; div &gt; #dialogboxhead{ background: #666; font-size:19px; padding:10px; color:#CCC; }\r\n#dialogbox &gt; div &gt; #dialogboxbody{ background: #333; padding:20px; color:#FFF; }\r\n#dialogbox &gt; div &gt; #dialogboxfoot{ background: #666; padding:10px; text-align:right; }\r\n&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n&lt;div id=&quot;dialogoverlay&quot;&gt;&lt;/div&gt;\r\n&lt;div id=&quot;dialogbox&quot;&gt;\r\n  &lt;div&gt;\r\n    &lt;div id=&quot;dialogboxhead&quot;&gt;&lt;/div&gt;\r\n    &lt;div id=&quot;dialogboxbody&quot;&gt;&lt;/div&gt;\r\n    &lt;div id=&quot;dialogboxfoot&quot;&gt;&lt;/div&gt;\r\n  &lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;p id=&quot;post_1&quot;&gt;\r\n  Today is a lovely day ...\r\n  &lt;button onclick=&quot;Confirm.render(''Delete Post?'',''delete_post'',''post_1'')&quot;&gt;Delete&lt;/button&gt;\r\n&lt;/p&gt;\r\n&lt;p id=&quot;post_2&quot;&gt;\r\n  I like pickles ... \r\n  &lt;button onclick=&quot;Confirm.render(''Delete Post?'',''delete_post'',''post_2'')&quot;&gt;Delete&lt;/button&gt;\r\n&lt;/p&gt;\r\n&lt;button onclick=&quot;Alert.render(''Hello World'')&quot;&gt;Custom Alert&lt;/button&gt;\r\n&lt;/body&gt;\r\n&lt;/html&gt;\r\n\r\n&lt;script&gt;\r\n	function CustomAlert(){\r\n	this.render = function(dialog){\r\n		var winW = window.innerWidth;\r\n	    var winH = window.innerHeight;\r\n		var dialogoverlay = document.getElementById(''dialogoverlay'');\r\n	    var dialogbox = document.getElementById(''dialogbox'');\r\n		dialogoverlay.style.display = &quot;block&quot;;\r\n	    dialogoverlay.style.height = winH+&quot;px&quot;;\r\n		dialogbox.style.left = (winW/2) - (550 * .5)+&quot;px&quot;;\r\n	    dialogbox.style.top = &quot;100px&quot;;\r\n	    dialogbox.style.display = &quot;block&quot;;\r\n		document.getElementById(''dialogboxhead'').innerHTML = &quot;Acknowledge This Message&quot;;\r\n	    document.getElementById(''dialogboxbody'').innerHTML = dialog;\r\n		document.getElementById(''dialogboxfoot'').innerHTML = ''&lt;button onclick=&quot;Alert.ok()&quot;&gt;OK&lt;/button&gt;'';\r\n	}\r\n	this.ok = function(){\r\n		document.getElementById(''dialogbox'').style.display = &quot;none&quot;;\r\n		document.getElementById(''dialogoverlay'').style.display = &quot;none&quot;;\r\n	}\r\n}\r\nvar Alert = new CustomAlert();\r\nfunction deletePost(id){\r\n	var db_id = id.replace(&quot;post_&quot;, &quot;&quot;);\r\n	// Run Ajax request here to delete post from database\r\n	document.body.removeChild(document.getElementById(id));\r\n}\r\nfunction CustomConfirm(){\r\n	this.render = function(dialog,op,id){\r\n		var winW = window.innerWidth;\r\n	    var winH = window.innerHeight;\r\n		var dialogoverlay = document.getElementById(''dialogoverlay'');\r\n	    var dialogbox = document.getElementById(''dialogbox'');\r\n		dialogoverlay.style.display = &quot;block&quot;;\r\n	    dialogoverlay.style.height = winH+&quot;px&quot;;\r\n		dialogbox.style.left = (winW/2) - (550 * .5)+&quot;px&quot;;\r\n	    dialogbox.style.top = &quot;100px&quot;;\r\n	    dialogbox.style.display = &quot;block&quot;;\r\n		\r\n		document.getElementById(''dialogboxhead'').innerHTML = &quot;Confirm that action&quot;;\r\n	    document.getElementById(''dialogboxbody'').innerHTML = dialog;\r\n		document.getElementById(''dialogboxfoot'').innerHTML = ''&lt;button onclick=&quot;Confirm.yes(\\''''+op+''\\'',\\''''+id+''\\'')&quot;&gt;Yes&lt;/button&gt; &lt;button onclick=&quot;Confirm.no()&quot;&gt;No&lt;/button&gt;'';\r\n	}\r\n	this.no = function(){\r\n		document.getElementById(''dialogbox'').style.display = &quot;none&quot;;\r\n		document.getElementById(''dialogoverlay'').style.display = &quot;none&quot;;\r\n	}\r\n	this.yes = function(op,id){\r\n		if(op == &quot;delete_post&quot;){\r\n			deletePost(id);\r\n		}\r\n		document.getElementById(''dialogbox'').style.display = &quot;none&quot;;\r\n		document.getElementById(''dialogoverlay'').style.display = &quot;none&quot;;\r\n	}\r\n}\r\nvar Confirm = new CustomConfirm();\r\n&lt;/script&gt;', 'custom confirm alert box', 'javascript'),
