(123, '<?php /*\r\n * dataStorageSystem1-1.php\r\n * A System for storing data in a text file.\r\n * note: The file responsible for storing data must start out with a set of these {}.\r\n *\r\n */\r\n //CONFIGURATIONS\r\ndefine(''DATA_FILE_EMAILS'', ''emails.txt'');\r\ndefine(''DATA_FILE_DOMAINS'', ''domains.txt'');\r\n?>\r\n<!-- VIEW -------------------------------------- -->\r\n<html>\r\n	<head>\r\n\r\n	</head>\r\n	<body>\r\n\r\n		<h3>Maintain a simple list</h3>\r\n		<p>\r\n			Managing data from a text file.\r\n		</p>\r\n		<ul>\r\n			<li>\r\n				one for emails\r\n			</li>\r\n			<li>\r\n				one for domains\r\n			</li>\r\n			<li>\r\n				and one for authorized users,select pulled from DB\r\n			</li>\r\n		</ul>\r\n		<hr>\r\n		<form name="form1" method="post" action="<?=$_SERVER[''PHP_SELF'']; ?>">\r\n			Email:\r\n			<input type="text" name="mail">\r\n			<input type="submit" name="method" value="add_email">\r\n		</form>\r\n		<?php displayCurrentEmailData(DATA_FILE_EMAILS); ?>\r\n		<hr>\r\n		<form name="form2" method="post" action="<?=$_SERVER[''PHP_SELF'']; ?>">\r\n			Domain:\r\n			<input type="text" name="domain">\r\n			<input type="submit" name="method" value="add_domain">\r\n		</form>\r\n		<?php displayCurrentDomainData(DATA_FILE_DOMAINS); ?>\r\n		<div id="domains_list"></div>\r\n		<hr>\r\n\r\n	</body>\r\n</html>\r\n<?php\r\n\r\n\r\n//--- CONTROLLER CODE -----------------------------------\r\n//--- Email Controller:    \r\n$phpData_emails = getStoredJSONDataASPHPArray(DATA_FILE_EMAILS);\r\nif (isset($_POST[''method''])) {\r\n	if ($_POST[''method''] == ''add_email'') {\r\n		if ($_POST[''mail''] != '''') {\r\n			//remove for later comaprison of data with different methods.\r\n			unset($_POST[''method'']);\r\n			$appendedData = addNewArrayToCopyOFStoredData($phpData_emails, $_POST);\r\n			//convert php array to json string\r\n			$data = json_encode($appendedData);\r\n			//write all new data to file\r\n			updateStoredData($data,DATA_FILE_EMAILS);\r\n			refreshPage();\r\n		}\r\n	}\r\n}\r\nif (isset($_POST[''method''])) {	 \r\n	if ($_POST[''method''] == ''remove_email'') {\r\n		unset($_POST[''method'']);\r\n		//see if data array contains this $_POST without method\r\n		if (in_array($_POST, $phpData_emails)) {\r\n			$index = array_search($_POST, $phpData_emails);\r\n			//remove it from data\r\n			unset($phpData_emails[$index]);\r\n			//rebase indexes\r\n			$array = array_values($phpData_emails);\r\n			//save it\r\n			$data = json_encode($array);\r\n			//write all new data to file\r\n			updateStoredData($data,DATA_FILE_EMAILS);\r\n		 \r\n			refreshPage();\r\n			\r\n		}\r\n	}\r\n}		 \r\n\r\n//--- Domain Controller:  \r\n$phpData_domains = getStoredJSONDataASPHPArray(DATA_FILE_DOMAINS);\r\nif (isset($_POST[''method''])) {\r\n	if ($_POST[''method''] == ''add_domain'') {\r\n		if ($_POST[''domain''] != '''') {\r\n			//remove for later comaprison of data with different methods.\r\n			unset($_POST[''method'']);\r\n			$appendedData = addNewArrayToCopyOFStoredData($phpData_domains, $_POST);\r\n			//convert php array to json string\r\n			$data = json_encode($appendedData);\r\n			//write all new data to file\r\n			updateStoredData($data,DATA_FILE_DOMAINS);\r\n			refreshPage();\r\n		}\r\n	}\r\n}\r\nif (isset($_POST[''method''])) {	 \r\n	if ($_POST[''method''] == ''remove_domain'') {\r\n		unset($_POST[''method'']);\r\n		//see if data array contains this $_POST without method\r\n		if (in_array($_POST, $phpData_domains)) {\r\n			$index = array_search($_POST, $phpData_domains);\r\n			//remove it from data\r\n			unset($phpData_domains[$index]);\r\n			//rebase indexes\r\n			$array = array_values($phpData_domains);\r\n			//save it\r\n			$data = json_encode($array);\r\n			//write all new data to file\r\n			updateStoredData($data,DATA_FILE_DOMAINS);\r\n			 refreshPage();\r\n		}\r\n	}\r\n}	 \r\n///////////////////////////////////////////////////////////////\r\n//--- VIEW HELPERS --------------------------\r\n//--- Email View Helper\r\nfunction displayCurrentEmailData($dataStoreFile) {\r\n	$currentList = getStoredJSONDataASPHPArray($dataStoreFile);\r\n	$html = "<table border=1 width=100%>";\r\n	$html .= "<tr><th>email</th> <th>action</th></tr>";\r\n	foreach ($currentList as $list) {\r\n		if ($list[''mail'']) {\r\n			$html .= "<form method=''post'' action=''" . $_SERVER[''PHP_SELF''] . "''> <tr>";\r\n			$html .= "<td><input type=''text'' name=''mail'' value=''" . $list[''mail''] . "''   /></td>";\r\n			$html .= "<td><input type=''submit'' name=''method'' value=''remove_email''></button></td>";\r\n			$html .= "</form></tr>";\r\n		}\r\n	}\r\n	$html .= "</table>";\r\n	echo($html);\r\n}\r\n//--- Domain View Helper\r\nfunction displayCurrentDomainData($dataStoreFile) {\r\n	$currentList = getStoredJSONDataASPHPArray($dataStoreFile);\r\n	$html = "<table border=1 width=100%>";\r\n	$html .= "<tr><th>domain</th> <th>action</th></tr>";\r\n	foreach ($currentList as $list) {\r\n		if ($list[''domain'']) {\r\n			$html .= "<form method=''post'' action=''" . $_SERVER[''PHP_SELF''] . "''> <tr>";\r\n			$html .= "<td><input type=''text'' name=''domain'' value=''" . $list[''domain''] . "''   /></td>";\r\n			$html .= "<td><input type=''submit'' name=''method'' value=''remove_domain''></button></td>";\r\n			$html .= "</form></tr>";\r\n		}\r\n	}\r\n	$html .= "</table>";\r\n	echo($html);\r\n}\r\n\r\n\r\n\r\n// SYSTEM FUNCTIONS=============================================================\r\n\r\n//DATA AGNOSTIC FUNCTIONS\r\nfunction getStoredJSONDataASPHPArray($dataStoreFile) {\r\n	if (!filesize($dataStoreFile)) {\r\n		//file does NOT yet exist, create it and \r\n		//file needs to contain an array at first.\r\n		updateStoredData("{}",$dataStoreFile);\r\n	}\r\n	$request = file_get_contents($dataStoreFile);\r\n	$phpFromJson = json_decode($request, true);\r\n	return $phpFromJson;\r\n}\r\nfunction addNewArrayToCopyOFStoredData($oldData, $newDataArray) {\r\n	//add new data to stored data\r\n	array_push($oldData, $newDataArray);\r\n	return $oldData;\r\n}\r\nfunction updateStoredData($data,$dataStoreFile) {\r\n	//this will create a file if it does not yet exist.\r\n	$fh = fopen($dataStoreFile, "w"); // the "w" paramenter overwrites previous data.\r\n	fwrite($fh, $data);\r\n	fclose($fh);\r\n}\r\nfunction refreshPage(){\r\n	echo("<script>window.location=''".$_SERVER[''PHP_SELF'']."'';</script>");\r\n}\r\n\r\n\r\n?>', 'A System for storing data in a text file.', 'php'),
