(169, '<html>\r\n	<head>\r\n		<?php include_once("00_head.php"); ?>\r\n		\r\n		\r\n		\r\n		 \r\n		<script src="animatrix.js"></script>\r\n		<script>\r\n		\r\n			window.onload = function(){\r\n				\r\n				var canvasColor = "black";\r\n				\r\n				var canvas_array = canvasOnly(400,400,canvasColor);\r\n				var canvas = canvas_array[0];\r\n				var c = canvas_array[1];\r\n				\r\n				//basicMotion(canvas,c,elementW,elementH,startX,startY,vx,vy,bounce,canvasColor,gravity,friction )\r\n					//basicMotion(canvas,c,3,3,0,0,20,5,1.1,canvasColor,.3,.94 );\r\n				\r\n				//basicImage(canvas,c,elementW,elementH,startX,startY,vx,vy,bounce,canvasColor,imageName   )\r\n					//basicImage(  canvas,c,3       ,       3,     0,     0,3,0,   1.1,canvasColor,''teeth.png''  );\r\n				 \r\n				 \r\n				// particlesPrecipitation(canvas,c);\r\n				 \r\n				 \r\n				 \r\n				 //smoothHorizontalImage(canvas,c,startX,startY,vx,vy,canvasColor,imageName)\r\n				smoothHorizontalImage(  canvas,c,0,10,.2,0,canvasColor,''teeth.png''  );\r\n						\r\n				particlesOnly(canvas,c);\r\n				particlesPrecipitation(canvas,c);\r\n				\r\n				// L E F T   O F F   H E R E   \r\n				//couds need higher z-index, need precipitation with images, snow, lighting , rotating sun, scaling, more +++\r\n				\r\n				//-------------animateImage(canvas,c,canvasColor,''teeth.png''); interferes\r\n				\r\n				// -------------horizontalImage(c,canvas,canvasColor,''teeth.png'');\r\n			}\r\n		</script>\r\n		\r\n	</head>\r\n	<body>\r\n		<?php include_once("00_nav.php"); ?>\r\n		 \r\n	 \r\n    <script>\r\n     \r\n    </script>\r\n	</body>\r\n	\r\n</html>\r\n////////////////////////////////////////////////animatrix.js////////\r\nfunction canvasOnly(w,h,bgcolor){\r\n	var canvas = document.createElement("canvas"), c = canvas.getContext("2d");\r\n	// up here he creates an object for deleting particles after they leave the screen\r\n	\r\n	canvas.width = w;\r\n	canvas.height = h;\r\n	document.body.appendChild(canvas);\r\n	c.fillStyle = bgcolor;\r\n	c.fillRect(0, 0, canvas.width, canvas.height);\r\n	\r\n	var canvas_array = [canvas,c];\r\n	\r\n	return canvas_array;\r\n}\r\n\r\n\r\nfunction particlesOnly(canvas,c){\r\n	var particles = {}, particleIndex = 0, particleNum = 2;\r\n	// BASIC MOTION\r\n	function Particle() {\r\n		this.x = canvas.width / 2;\r\n		this.y = canvas.height / 2;\r\n		this.vx = Math.random() * 10 - 5;\r\n		this.vy = Math.random() * 10 - 5;\r\n		//here is code for keeping track of particles for deletion later.\r\n		particleIndex++;\r\n		particles[particleIndex] = this;\r\n		this.id = particleIndex;\r\n		this.gravity = .3;\r\n		// give the particle a life\r\n		this.life = 0;\r\n		this.maxLife = 100;\r\n		this.color = "hsla(" + parseInt(Math.random() * 360, 10) + ",100%,50%,0.1)";\r\n\r\n	}\r\n\r\n	Particle.prototype.draw = function() {\r\n		this.x += this.vx;\r\n		this.y += this.vy;\r\n		this.vy += this.gravity;\r\n		// particle Life code\r\n		this.life++;\r\n		if (this.life >= this.maxLife) {\r\n			delete particles[this.id];\r\n		}\r\n		c.fillStyle = this.color;\r\n		c.fillRect(this.x, this.y, 10, 10);\r\n\r\n	};\r\n	\r\n	// AN EMITTER: take the above loop and ....put it inside the setIntervalFunction...\r\n	setInterval(function() {\r\n		//emitter:\r\n		for (var i = 0; i < particleNum; i++) {\r\n			new Particle();\r\n		}\r\n		// comment this out for looping particles   p.draw();\r\n		//insert this loop''\r\n		for (var i in particles) {\r\n			particles[i].draw();\r\n		}\r\n\r\n	}, 30);\r\n}\r\n\r\n\r\nfunction particlesPrecipitation(canvas,c){\r\n	var particles = {}, particleIndex = 0, particleNum = 10;\r\n	// BASIC MOTION\r\n	function Particle() {\r\n		 \r\n		this.x = parseInt(Math.random() * canvas.width, 10); //0;//canvas.width / 2;\r\n		this.y = 0;//canvas.height / 2;\r\n		this.vx = 0;//Math.random() * 10 - 5;\r\n		this.vy = Math.random() * 10 - 5;\r\n		//here is code for keeping track of particles for deletion later.\r\n		particleIndex++;\r\n		particles[particleIndex] = this;\r\n		this.id = particleIndex;\r\n		this.gravity = .1;\r\n		// give the particle a life\r\n		this.life = 0;\r\n		this.maxLife = 100;\r\n		//this.color = "hsla(" + parseInt(Math.random() * 360, 10) + ",100%,50%,0.9)";\r\n		this.color = "hsla(" + parseInt( 180, 10) + ",100%,50%,0.3)";\r\n\r\n	}\r\n\r\n	Particle.prototype.draw = function() {\r\n		this.x += this.vx;\r\n		this.y += this.vy;\r\n		this.vy += this.gravity;\r\n		// particle Life code\r\n		this.life++;\r\n		if (this.life >= this.maxLife) {\r\n			delete particles[this.id];\r\n		}\r\n		c.fillStyle = this.color;\r\n		c.fillRect(this.x, this.y, 4, 4);\r\n\r\n	};\r\n	\r\n	// AN EMITTER: take the above loop and ....put it inside the setIntervalFunction...\r\n	setInterval(function() {\r\n		//c.fillStyle = "black";\r\n			//c.fillRect(0,0,canvas.width,canvas.height);\r\n		//emitter:\r\n		for (var i = 0; i < particleNum; i++) {\r\n			new Particle();\r\n		}\r\n		// comment this out for looping particles   p.draw();\r\n		//insert this loop''\r\n		for (var i in particles) {\r\n			particles[i].draw();\r\n		}\r\n\r\n	}, 30);\r\n}\r\n\r\nfunction basicMotion(canvas,c,elementW,elementH,startX,startY,vx,vy,bounce,canvasColor ,gravity,friction){\r\n	// BASIC MOTION\r\n		var posX = startX,\r\n		posY = startY;\r\n		//add veloctiy vars \r\n		//var  gravity = .3;\r\n		\r\n		setInterval(function(){\r\n			// This bit "Erases" the path trail.\r\n			c.fillStyle = canvasColor;\r\n			c.fillRect(0,0,canvas.width,canvas.height);\r\n			//\r\n			posX +=vx; // the number here is vx , velocity in the x axis\r\n			posY +=vy; // likewise vy, velocity on the y-axis\r\n			//now by adjusting vx and vy over time we get intereesting stuff\r\n			//this is a friction effect\r\n				vx *= friction;//0.94;\r\n				vy *= friction; // 0.94;\r\n			//another example of box shooting up then arcing back down:\r\n			// set vy = -10;\r\n			// remove vx and vy in the interval function and replace vy = +=1; // this numeral could be called gravity\r\n			var gravityAcceleration = 10;\r\n			var paddingToBottom = 10;\r\n			//create a stopping point\r\n			if(posY > canvas.height-paddingToBottom){\r\n				posY = canvas.height-paddingToBottom;\r\n				// we could reverse the velocity by multiplying by -1 and get a bounce. ie. vy *= -1; // or -0.5;\r\n					vy *= -(bounce);\r\n				// multiply a negative to pos, gets a side to side bouncing effect.\r\n					//vx *= -0.5; \r\n			}\r\n			//Placement of gravity code can fix weird bouncing issues...\r\n			 vy += gravity*gravityAcceleration;\r\n			c.fillStyle = "white";\r\n			c.fillRect(posX,posY, elementW, elementH);\r\n		}, 30);\r\n			\r\n}\r\n \r\n \r\nfunction basicImage(canvas,c,elementW,elementH,startX,startY,vx,vy,bounce,canvasColor,imageName   ){\r\n	\r\n	var img = new Image;\r\n	img.src = imageName;\r\n	// BASIC MOTION\r\n		var posX = startX,\r\n		posY = startY;\r\n		//console.log(''vx''+vx);\r\n		//console.log(''vy''+vy);\r\n		//add veloctiy vars \r\n		var  gravity = .3; //.3 \r\n		var friction = 0.94;\r\n		setInterval(function(){\r\n			// This bit "Erases" the path trail.\r\n			c.fillStyle = canvasColor;\r\n			c.fillRect(0,0,canvas.width,canvas.height);\r\n			//\r\n			posX += vx; // the number here is vx , velocity in the x axis\r\n			posY += vy; // likewise vy, velocity on the y-axis\r\n			//now by adjusting vx and vy over time we get intereesting stuff\r\n			//this is a friction effect\r\n				// IF NO FRICTION , FOR CLOUDS\r\n				vx *= .94;//0.94;\r\n				\r\n				vy *= friction; //0.94;\r\n			//another example of box shooting up then arcing back down:\r\n			// set vy = -10;\r\n			// remove vx and vy in the interval function and replace vy = +=1; // this numeral could be called gravity\r\n			var gravityAcceleration = 10;\r\n			var paddingToBottom = 10;\r\n			//create a stopping point\r\n			if(posY > canvas.height-paddingToBottom){\r\n				posY = canvas.height-paddingToBottom;\r\n				// we could reverse the velocity by multiplying by -1 and get a bounce. ie. vy *= -1; // or -0.5;\r\n					vy *= -(bounce);\r\n				// multiply a negative to pos, gets a side to side bouncing effect.\r\n					//vx *= -0.5; \r\n			}\r\n			//Placement of gravity code can fix weird bouncing issues...\r\n			 vy += gravity*gravityAcceleration;\r\n			//c.fillStyle = "blue";\r\n			//c.fillRect(posX,posY, elementW, elementH);\r\n			//console.log(''posX''+posX);\r\n		//console.log(''posY''+posY);\r\n			c.drawImage(img, posX, posY);\r\n			\r\n			\r\n		}, 30);\r\n			\r\n}\r\n \r\n \r\nfunction smoothHorizontalImage(canvas,c,startX,startY,vx,vy,canvasColor,imageName){\r\n	// NO GRAVITY and NO FRICTION for smooth horizontal\r\n	var img = new Image;\r\n	img.src = imageName;\r\n		var posX = startX,\r\n		posY = startY;\r\n		var  gravity = 0; \r\n		setInterval(function(){\r\n			// This bit "Erases" the path trail.\r\n			c.fillStyle = canvasColor;\r\n			c.fillRect(0,0,canvas.width,canvas.height);\r\n			posX += vx; \r\n			posY += vy; \r\n			//Placement of gravity code can fix weird bouncing issues...\r\n			vy += gravity;\r\n			c.drawImage(img, posX, posY);\r\n			\r\n		}, 30);\r\n			\r\n}\r\n \r\n \r\nfunction animateImage( canvas, c, canvasColor, imageName) {\r\n	var img = new Image;\r\n	img.src = imageName;\r\n	\r\n	 setInterval( function() {\r\n		c.fillStyle = canvasColor;\r\n		c.fillRect(0, 0, canvas.width, canvas.height);\r\n		var x = 0, y = 150;\r\n		return function() {\r\n			\r\n			//clear the image\r\n			c.clearRect(0, 0, c.canvas.width , c.canvas.height );\r\n			c.fillStyle = canvasColor;\r\n			//clear the canvas ( of training image)\r\n			c.fillRect(0, 0, canvas.width, canvas.height);\r\n			c.drawImage(img, x, y);\r\n\r\n			x += 1;\r\n			if (x > c.canvas.width) {\r\n				x = 0;\r\n			}\r\n		};\r\n	}(), 1000 / 40); //1000 / 40\r\n\r\n}\r\n\r\n \r\n\r\n \r\n\r\n\r\nfunction horizontal(c,canvas,canvasColor){\r\n	//PURPOSE: to examine using this style... requestAnimFrame\r\n	//var c = c;\r\n	//var canvas = canvas;\r\n	var canvasColor = canvasColor;\r\n	\r\n	 window.requestAnimFrame = (function(callback) {\r\n        return window.requestAnimationFrame || window.webkitRequestAnimationFrame || window.mozRequestAnimationFrame || window.oRequestAnimationFrame || window.msRequestAnimationFrame ||\r\n        function(callback) {\r\n          window.setTimeout(callback, 1000 / 60);\r\n        };\r\n      })();\r\n\r\n      function drawRectangle(myRectangle, c) {\r\n        c.beginPath();\r\n        c.rect(myRectangle.x, myRectangle.y, myRectangle.width, myRectangle.height);\r\n        c.fillStyle = ''#CCC'';\r\n        c.fill();\r\n        c.lineWidth = myRectangle.borderWidth;\r\n        c.strokeStyle = ''black'';\r\n        c.stroke();\r\n      }\r\n      function animate(myRectangle, canvas, c, startTime,canvasColor) {\r\n        // update\r\n        var time = (new Date()).getTime() - startTime;\r\n\r\n        var linearSpeed = 200;\r\n        // pixels / second\r\n        var newX = linearSpeed * time / 1000;\r\n\r\n        if(newX < canvas.width - myRectangle.width - myRectangle.borderWidth / 2) {\r\n          myRectangle.x = newX;\r\n        }\r\n\r\n        // clear\r\n        c.clearRect(0, 0, c.canvas.width , c.canvas.height );\r\n			c.fillStyle = canvasColor;\r\n        drawRectangle(myRectangle, c);\r\n\r\n        // request new frame\r\n        requestAnimFrame(function() {\r\n          animate(myRectangle, canvas, c, startTime);\r\n        });\r\n      }\r\n      //var canvas = document.getElementById(''myCanvas'');\r\n      //var context = canvas.getContext(''2d'');\r\n\r\n      var myRectangle = {\r\n        x: 0,\r\n        y: 75,\r\n        width: 100,\r\n        height: 50,\r\n        borderWidth: 5\r\n      };\r\n\r\n      drawRectangle(myRectangle, c);\r\n\r\n      // wait one second before starting animation\r\n      setTimeout(function() {\r\n        var startTime = (new Date()).getTime();\r\n        animate(myRectangle, canvas, c, startTime,canvasColor);\r\n      }, 500);\r\n}\r\n', 'animatrixJS backup', 'javascript'),
