(176, '<SCRIPT type="text/javascript" src="https://ajax.googleapis.com/ajax/libs/jquery/1.7.1/jquery.min.js"></SCRIPT>\r\n<SCRIPT type="text/javascript" src="https://maps.googleapis.com/maps/api/js?sensor=false"></SCRIPT>\r\n<SCRIPT type="text/javascript">\r\n$(function() {\r\n    if(navigator.geolocation) {\r\n        var fallback = setTimeout(function() { fail(''10 seconds expired''); }, 10000);\r\n        navigator.geolocation.getCurrentPosition(\r\n            function (pos) {\r\n                clearTimeout(fallback);\r\n                console.log(''pos'', pos);\r\n                var point = new google.maps.LatLng(pos.coords.latitude, pos.coords.longitude);\r\n                new google.maps.Geocoder().geocode({''latLng'': point}, function (res, status) {\r\n                    if(status == google.maps.GeocoderStatus.OK && typeof res[0] !== ''undefined'') {\r\n                        var zip = res[0].formatted_address.match(/,\\s\\w{2}\\s(\\d{5})/);\r\n                        if(zip) {\r\n                           // $("._res").html(''Zip code is '' +  zip[1]);\r\n                             $("#geo_zipcode").val(  zip[1]);\r\n                        } else fail(''Failed to parse'');\r\n                    } else {\r\n                        fail(''Failed to reverse'');\r\n                    }\r\n                });\r\n            }, function(err) {\r\n                fail(err.message);\r\n            }\r\n        );\r\n    } else {\r\n        $("._res").html(''Geolocation unsupported!'');\r\n    }\r\n    function fail(err) {\r\n        console.log(''err'', err);\r\n        $("._res").html(''Error '' + err);\r\n    }\r\n});\r\n</SCRIPT>\r\n\r\n \r\n<input type=''input'' id=''geo_zipcode'' value=''foo''/>', 'zipcode', 'html5'),
