(186, '<?php\r\n// AJAX JSON MODEL PARSE FORMAT EVAL\r\n?>\r\n\r\n<script>\r\n	//MODEL\r\n	function AccountModel(id, type, amount, date) {\r\n		this.id = id;\r\n		this.type = type;\r\n		this.amount = amount;\r\n		this.date = date;\r\n	}\r\n	AccountModel.prototype.id = '''';\r\n	AccountModel.prototype.type = '''';\r\n	AccountModel.prototype.amount = '''';\r\n	AccountModel.prototype.date = ''''; \r\n	\r\n	AccountModel.prototype.someFunction = function() {\r\n		console.log("return "+ this.id + "?");\r\n	};\r\n	\r\n</script>\r\n\r\n<script>\r\n	// CALL AJAX\r\n	Money.getData = (function() {\r\n		AjaxGetMoney("params", "money_server.php")\r\n	}); \r\n</script>\r\n\r\n<script>\r\n	//AJAX\r\n	function AjaxGetMoney(dataString, URL) {\r\n		var xmlhttp;\r\n		if (window.XMLHttpRequest) {// code for IE7+, Firefox, Chrome, Opera, Safari\r\n			xmlhttp = new XMLHttpRequest();\r\n		} else {// code for IE6, IE5\r\n			xmlhttp = new ActiveXObject("Microsoft.XMLHTTP");\r\n		}\r\n		xmlhttp.onreadystatechange = function() {\r\n			if (xmlhttp.readyState == 4 && xmlhttp.status == 200) {\r\n				//console.log(xmlhttp.responseText);\r\n				json = xmlhttp.responseText;\r\n				parseII(json);\r\n			}\r\n		}\r\n		xmlhttp.open("GET", URL, true);\r\n		xmlhttp.setRequestHeader("Content-type", "application/x-www-form-urlencoded");\r\n		//send to server\r\n		xmlhttp.send(dataString);\r\n	}\r\n</script>\r\n<?php\r\n///   MYSQL READ\r\nif (isset($_GET)) {\r\n	mysql_connect("localhost", "root", "root");\r\n	$sql = "SELECT * from money.accounts ORDER BY date;";\r\n	$res = mysql_query($sql);\r\n	while ($row = mysql_fetch_assoc($res)) {\r\n		$data[] = $row;\r\n	}\r\n	echo(mysql_error());\r\n	$json = json_encode($data);\r\n	print_r($json);\r\n}\r\n?>\r\n<?php\r\n///   MYSQL INSERT\r\nif (isset($_POST)) {\r\n	echo(strtotime($_POST[''date'']));\r\n	mysql_connect("localhost", "root", "root");\r\n	$sql = "INSERT IGNORE INTO `money`.`accounts`\r\n(`id`, `type`, `amount`,`date` )\r\nVALUES (NULL, ''" . $_POST[''ty