count += e;\r\n\r\n	// convert json formatted strings to OBJECTS via eval\r\n	checkingData = eval(''{'' + myCheckingAccount + ''}''); \r\n</script>\r\n', ' AJAX JSON MODEL PARSE FORMAT EVAL', 'json'),
(187, 'var MyJSON = {};\r\n\r\nMyJSON.ajax_mysql_to_json = (function(sql, URL,callback) {\r\n	//params: sql, URL, callback(the callback function name)\r\n	var xmlhttp;\r\n	if (window.XMLHttpRequest) {// code for IE7+, Firefox, Chrome, Opera, Safari\r\n		xmlhttp = new XMLHttpRequest();\r\n	} else {// code for IE6, IE5\r\n		xmlhttp = new ActiveXObject("Microsoft.XMLHTTP");\r\n	}\r\n	xmlhttp.onreadystatechange = function() {\r\n		if (xmlhttp.readyState == 4 && xmlhttp.status == 200) {\r\n			console.log(xmlhttp.responseText);\r\n			json = xmlhttp.responseText;\r\n			//MyJSON.ajaxCallback(json);\r\n			callback(json);\r\n		}\r\n	}\r\n	xmlhttp.open("POST", URL, true);\r\n	xmlhttp.setRequestHeader("Content-type", "application/x-www-form-urlencoded");\r\n	xmlhttp.send(sql);\r\n});\r\n\r\n\r\n/*\r\n * REQUIRES SERVER CODE LIKE THIS PHP:\r\nif(isset($_POST)){\r\n	$json = sqlToJson($_POST[''sql'']);\r\n	print_r($json);\r\n}\r\n\r\nfunction sqlToJson($sql){\r\n	mysql_connect("localhost","root","root");\r\n	$res = mysql_query($sql);\r\n	while($row = mysql_fetch_assoc($res)){\r\n		$data[] = $row;\r\n	}\r\n	echo(mysql_error());\r\n	\r\n	$json = json_encode($data);\r\n	return $json;\r\n}\r\n\r\n */', 'MyJSON - a way to ajax SQL , query MySQL in PHP, and return JSON ', 'json'),
(188, '<!-- FLOT Multiple Threads over Time -->\r\n<head>\r\n<link href="js/jquery-ui-1.11.2/jquery-ui.min.css" rel="stylesheet" type="text/css">\r\n<script language="javascript" type="text/javascript" src="js/jquery.js"></script>\r\n<script language="javascript" type="text/javascript" src="js/jquery.flot.js"></script>\r\n<script src="js/jquery-ui-1.11.2/jquery-ui.min.js"></script>	\r\n</head>	\r\n<div id="placeholder" style="width:600px;height:500px;"></div>\r\n<script>\r\n	function doPlot(checkingData, savingsData, creditCard_53Data) {\r\n				//params: data is json of timestamp and amount\r\n				$.plot($("#placeholder"), [{\r\n					data : checkingData,\r\n					label : "checking"\r\n				}, {\r\n					data : savingsData,\r\n					label : "savings"\r\n					\r\n				}, {\r\n					data : creditCard_53Data,\r\n					label : "creditCard_53Data"\r\n					\r\n				}\r\n				\r\n				], {\r\n					xaxes : [{\r\n						mode : ''time''\r\n					}],\r\n					yaxes : [],\r\n					legend : {\r\n						position : ''sw''\r\n					}\r\n				});\r\n			}\r\n</script>', 'FLOT graph Multiple Threads over Time', 'json'),
(189, '<?php /**/ ?>\r\n\r\n<script>\r\n	/* ///////////////////// BASIC OBJECT //////////////////////////// */\r\n	alert("js: Basic Object");\r\n	function Person(gender) {\r\n		this.gender = gender;\r\n	}\r\n\r\n\r\n	Person.prototype.gender = '''';\r\n\r\n	Person.prototype.sayHello = function() {\r\n		alert(''hello'');\r\n		alert(this.gender);\r\n	};\r\n\r\n	var person1 = new Person(''Male'');\r\n	var person2 = new Person(''Female'');\r\n\r\n	// call the Person sayHello method.\r\n	person1.sayHello();\r\n	// hello\r\n</script>\r\n\r\n<script>\r\n	/* ///////////////////// BASIC EXTENDS //////////////////////////// */\r\n	alert("js: Basic Extends");\r\n	var extend = function(OBJ, extendsOBJ) {\r\n		for (var property in extendsOBJ) {\r\n			if (OBJ[property] && ( typeof (OBJ[property]) == ''object'') && (OBJ[property].toString() == ''[object Object]'') && extendsOBJ[property])\r\n				extend(OBJ[property], extendsOBJ[property]);\r\n			else\r\n				OBJ[property] = extendsOBJ[property];\r\n		}\r\n		return OBJ;\r\n	}\r\n	var child = {\r\n		child : ''test''\r\n	};\r\n\r\n	var parent = {\r\n		newFoo : function() {\r\n			alert(''hi how do you like the genes I gave you?'');\r\n		}\r\n	};\r\n\r\n	// the addition\r\n	extend(child, parent);\r\n	// extend it\r\n	child.newFoo(); \r\n</script>\r\n\r\n<script>\r\n	/* ///////////////////// BASIC INHERITENCE //////////////////////////// */\r\n	alert("js: Basic INHERITENCE");\r\n	// define the Person Class\r\n	function Person() {\r\n	}\r\n\r\n\r\n	Person.prototype.walk = function() {\r\n		alert(''I am walking!'');\r\n	};\r\n	Person.prototype.sayHello = function() {\r\n		alert(''hello'');\r\n	};\r\n\r\n	// define the Student class\r\n	function Student() {\r\n		// Call SUPER constuctor\r\n		Person.call(this);\r\n	};\r\n	// inherit Person\r\n	Student.prototype = new Person();\r\n	// !!!!! correct the constructor pointer because it points to Person !!!!!\r\n	Student.prototype.constructor = Student;\r\n	//@override  replace the sayHello method\r\n	Student.prototype.sayHello = function() {\r\n		alert(''hi, I am a student'');\r\n	};\r\n	// add sayGoodBye method\r\n	Student.prototype.sayGoodBye = function() {\r\n		alert(''goodBye'');\r\n	};\r\n\r\n	// USAGE\r\n\r\n	var student1 = new Student();\r\n	student1.sayHello();\r\n	student1.walk();\r\n	student1.sayGoodBye();\r\n\r\n	// check inheritance\r\n	alert( student1 instanceof Person);\r\n	// true\r\n	alert( student1 instanceof Student);\r\n	// true\r\n\r\n	//Using Object.create the inheritance line would instead be:\r\n	//Student.prototype = Object.create(Person.prototype);\r\n</script>\r\n\r\n<script>\r\n	/* ///////////////////// BASIC INTERFACE //////////////////////////// */\r\n	alert("js: Basic INTERFACE");\r\n	Object.prototype.can = function(methodName) {\r\n		return (( typeof this[methodName]) == "function");\r\n	};\r\n\r\n	if (someObject.can("quack")) {\r\n		someObject.quack();\r\n	}\r\n\r\n</script>\r\n<script>\r\n    /* ///////////////////// BASIC INTERFACE //////////////////////////// */\r\n    alert("js: Basic INTERFACE");\r\n    \r\n     function MyObject() {\r\n    }\r\n\r\n    MyObject.prototype.sayHello = function() {\r\n        alert(''hello'');\r\n    };\r\n\r\n    var myObject = new MyObject();\r\n  \r\n// In javascript it appears that the interface and the implementor of that interface, are one and the same. \r\n//There are just a few additional components. 1) The "Object.prototype.can..." and the "condition checks".\r\n\r\n     MyObject.prototype.can = function(methodName) {\r\n        return (( typeof this[methodName]) == "function");\r\n    };\r\n    \r\n\r\n    if (myObject.can("sayHello")) {\r\n        myObject.sayHello();\r\n    }\r\n    if(myObject.can("sayHiYall")) {\r\n        myObject.sayHiYall();\r\n    }else{\r\n        alert(''myObject: you cant do that'');\r\n    }\r\n\r\n</script>\r\n', 'js_oo_cheatsheet', 'javascript'),
(190, ' <script>\r\nalert("Example : Multiple Decorators");\r\n// --- SUPER CLASS\r\nfunction Icecream(){\r\n	this.cost = function (){\r\n		return 2.04;\r\n	}; \r\n}\r\n// --- DECORATORS\r\nfunction NutsDecorator(icecream){\r\n	var currentCost = icecream.cost();\r\n	icecream.cost = function(){\r\n		return currentCost + .33;\r\n	};\r\n}\r\nfunction HoneyDecorator(icecream){\r\n	var v = (icecream.cost());\r\n	icecream.cost = function (){\r\n		return v + .25;\r\n	};\r\n}\r\n\r\n// USAGE\r\n//instantiate the super class. call its method.\r\nvar icecreamOrder = new Icecream();\r\nalert(icecreamOrder.cost());\r\n//call the decorator function with the instatiated superclass as the parameter.\r\nNutsDecorator(icecreamOrder);\r\nalert(icecreamOrder.cost());\r\n\r\nHoneyDecorator(icecreamOrder);\r\nalert(icecreamOrder.cost());\r\n//////--------- Add new functionality\r\nalert("Example : Adding Accessors ");\r\nfunction dessertItem(dessertType){\r\n	this.type = "iceream" || dessertType;\r\n}\r\nvar bannaSplit = new dessertItem();\r\nalert(bannaSplit.type);// default is icecream\r\nbannaSplit.setType = function(dessertType){\r\n	this.type = dessertType;\r\n\r\n};\r\nbannaSplit.setType("banana splitiz");\r\nalert(bannaSplit.type);//now with added accessor method\r\n</script>', 'design pattern : decorator', 'javascript'),
