(191, ' <script>\r\n\r\n	function Item(options) {\r\n		this.idNumber = options.idNumber;\r\n	}\r\n	 \r\n	function Widget(options) {\r\n		this.idNumber = options.idNumber;\r\n		this.itemName = "widget";\r\n		this.use = options.use || "generic helper";\r\n		this.color = options.color || "blue";\r\n	}\r\n	// Define a skeleton  factory\r\n	function Factory() {\r\n\r\n	}\r\n	// Our default object is Car\r\n	Factory.prototype.object = Item;\r\n	// Our Factory method for creating new Vehicle instances\r\n	Factory.prototype.createItem = function(options) {\r\n		if (options.itemName === "car") {\r\n			this.object = Car;\r\n		} else if (options.itemName === "widget") {\r\n			this.object = Widget;\r\n		} else {\r\n		// DEFAULT CLASS\r\n		this.object = Item;\r\n		}\r\n		return new this.object(options);\r\n	}; \r\n</script>\r\n<script>\r\n	// CLIENT \r\n	alert("DesignPattern: Factory");\r\n	//extend Factory\r\n	function WidgetFactory() {\r\n\r\n	}\r\n\r\n	WidgetFactory.prototype = new Factory();\r\n	WidgetFactory.prototype.object = Widget;\r\n\r\n	var widgetFactory = new WidgetFactory();\r\n	var myBigWidget = widgetFactory.createItem({\r\n		idNumber : "t-150",\r\n		itemName : "widget",\r\n		use : "create objects efficiently",\r\n		color : "shiny gold"\r\n		 \r\n	});\r\n	//  verify\r\n	alert(myBigWidget.itemName + ":" + myBigWidget.use + ":" + myBigWidget.color + ":"  + myBigWidget.idNumber); \r\n\r\n	console.log(myBigWidget);\r\n</script>\r\n', 'design pattern: factory', 'javascript'),
