(192, ' <script>\r\n	function Subject() {\r\n		this.observers = new ObserverList();\r\n	}\r\n	Subject.prototype.addObserver = function(observer) {\r\n		this.observers.add(observer);\r\n	};\r\n	Subject.prototype.removeObserver = function(observer) {\r\n		this.observers.removeAt(this.observers.indexOf(observer, 0));\r\n	};\r\n	Subject.prototype.notify = function(context) {\r\n		var observerCount = this.observers.count();\r\n		for (var i = 0; i < observerCount; i++) {\r\n			this.observers.get(i).update(context);\r\n			console.log("subject matter inserted into context: "+context);\r\n		}\r\n	};\r\n	// The Observer\r\n	function Observer(name) {\r\n		var observerName = name;\r\n		var receivedMsg;\r\n		this.update = function(context) {\r\n			receivedMsg = context;\r\n		};\r\n		this.remember = function(){\r\n			//alert("I am "+ observerName +" and I remember the following subject_matter:"+ receivedMsg);\r\n			console.log("I am "+ observerName +" and I remember the following subject matter:"+ receivedMsg);\r\n		};\r\n	}\r\n \r\n	function ObserverList() {\r\n		this.observerList = [];\r\n	}\r\n	ObserverList.prototype.add = function(obj) {\r\n		return this.observerList.push(obj);\r\n	};\r\n	ObserverList.prototype.count = function() {\r\n		return this.observerList.length;\r\n	};\r\n	ObserverList.prototype.get = function(index) {\r\n		if (index > -1 && index < this.observerList.length) {\r\n			return this.observerList[index];\r\n		}\r\n	};\r\n	ObserverList.prototype.indexOf = function(obj, startIndex) {\r\n		var i = startIndex;\r\n		while (i < this.observerList.length) {\r\n			if (this.observerList[i] === obj) {\r\n				return i;\r\n			}\r\n			i++;\r\n		}\r\n		return -1;\r\n	};\r\n	ObserverList.prototype.removeAt = function(index) {\r\n		this.observerList.splice(index, 1);\r\n	}; \r\n</script>\r\n<script>\r\n	//CLIENT\r\n	alert("DesignPattern: Observer(see console)");\r\n	var subject = new Subject();\r\n	\r\n	var observer1 = new Observer("Jill");\r\n	subject.addObserver(observer1);\r\n\r\n	var observer2 = new Observer("Bobby");\r\n	subject.addObserver(observer2);\r\n\r\n	var subject_matter = "Observers 1 and 2 just entered the room.";\r\n	subject.notify(subject_matter);\r\n	subject.removeObserver(observer2);\r\n\r\n	var newMessage = "Observer 2 left the room.";\r\n	subject.notify(newMessage);\r\n	subject.observers.get(0).remember();\r\n	 \r\n\r\n</script>', 'design pattern: observer', 'javascript'),
(193, '<SCRIPT type="text/javascript" src="https://ajax.googleapis.com/ajax/libs/jquery/1.7.1/jquery.min.js"></SCRIPT>\r\n<SCRIPT type="text/javascript" src="https://maps.googleapis.com/maps/api/js?sensor=false"></SCRIPT>\r\n\r\n<SCRIPT type="text/javascript">\r\n$(function() {\r\n    if(navigator.geolocation) {\r\n        var fallback = setTimeout(function() { fail(''10 seconds expired''); }, 10000);\r\n        navigator.geolocation.getCurrentPosition(\r\n            function (pos) {\r\n                clearTimeout(fallback);\r\n                console.log(''pos'', pos);\r\n                var point = new google.maps.LatLng(pos.coords.latitude, pos.coords.longitude);\r\n                localStorage.setItem(''latitude'',pos.coords.latitude);\r\n                localStorage.setItem(''longitude'',pos.coords.longitude);\r\n                new google.maps.Geocoder().geocode({''latLng'': point}, function (res, status) {\r\n                    if(status == google.maps.GeocoderStatus.OK && typeof res[0] !== ''undefined'') {\r\n                        var zip = res[0].formatted_address.match(/,\\s\\w{2}\\s(\\d{5})/);\r\n                        if(zip) {\r\n                            $("._res").html(''Zip code is '' +  zip[1]);\r\n                            localStorage.setItem(''zipcode'', zip[1]);\r\n                        } else fail(''Failed to parse'');\r\n                    } else {\r\n                        fail(''Failed to reverse'');\r\n                    }\r\n                });\r\n            }, function(err) {\r\n                fail(err.message);\r\n            }\r\n        );\r\n    } else {\r\n        $("._res").html(''Geolocation unsupported!'');\r\n    }\r\n    function fail(err) {\r\n        console.log(''err'', err);\r\n        $("._res").html(''Error '' + err);\r\n    }\r\n});\r\n</SCRIPT>\r\n<div class="_res">Nothing yet....</div>', 'zipcode,latitude,longitude by geolocation at googleapis ANDlocalStorage', 'javascript'),
(194, '<!DOCTYPE HTML>\r\n<html>\r\n<head>\r\n<style>\r\n#div1 {width:350px;height:70px;padding:10px;border:1px solid #aaaaaa;}\r\n</style>\r\n<script>\r\nfunction allowDrop(ev) {\r\n    ev.preventDefault();\r\n}\r\n\r\nfunction drag(ev) {\r\n    ev.dataTransfer.setData("text/html", ev.target.id);\r\n}\r\n\r\nfunction drop(ev) {\r\n    ev.preventDefault();\r\n    var data = ev.dataTransfer.getData("text/html");\r\n    ev.target.appendChild(document.getElementById(data));\r\n}\r\n</script>\r\n</head>\r\n<body>\r\n\r\n<p>Drag the W3Schools image into the rectangle:</p>\r\n\r\n<div id="div1" ondrop="drop(event)" ondragover="allowDrop(event)"></div>\r\n<br>\r\n<img id="drag1" src="img_logo.gif" draggable="true" ondragstart="drag(event)" width="336" height="69">\r\n\r\n</body>\r\n</html>', 'drag and drop', 'html5'),
(195, '<!DOCTYPE html>\r\n<html>\r\n	<head>\r\n		<meta charset="utf-8">\r\n<!-- To disable zooming, setup your viewport so that it is not user scalable using the following meta tag: -->\r\n<meta name="viewport"   content="width=device-width, initial-scale=1.0, user-scalable=no">\r\n\r\n		<!--\r\n		<link rel=''stylesheet'' type=''text/css'' href=''style.css''>-->\r\n		<script type=''text/javascript'' src=''jquery-1.11.1.min.js''></script>\r\n		\r\n		<script type=''text/javascript'' src=''jquery.mobile-1.4.5.min.js''></script>\r\n\r\n	</head>\r\n	<body>\r\n	<h3>jquery mobile touch events</h3>\r\n		<div id="touch" style="height:50px;width:200px;margin-top:100px;border:solid 3px black;"> jquery mobile </div>\r\n		<script>\r\n			$(document).ready(function() {\r\n				alert("ready!");\r\n\r\n				$("#touch").on("tap", function() {\r\n					alert("tap!");\r\n				});\r\n\r\n				$("#touch").on("taphold", function() {\r\n					alert("taphold!");\r\n				});\r\n\r\n				$("#touch").on("swipe", function() {\r\n					$("span").text("Swipe detected!");\r\n				});\r\n\r\n				$("#touch").on("swipeleft", function() {\r\n					alert("You swiped left!");\r\n				});\r\n\r\n				$("#touch").on("swiperight", function() {\r\n					alert("You swiped right!");\r\n				});\r\n			});\r\n		</script>\r\n\r\n<h3>HTML5 touch</h3>\r\n<div id=''foo'' style="height:50px;width:200px;margin-top:100px;border:solid 3px black;">html5</div>\r\n \r\n\r\n \r\n\r\n\r\n\r\n\r\n		<script>\r\n//Some mobile devices have default behaviors for touchmove, such as the classic iOS overscroll effect, which causes the view to bounce back when scrolling exceeds the bounds of the content. This is confusing in many multi-touch applications, and can easily be disabled\r\n/*\r\nTHIS FIX FOR HTML5, appeared to KILL JQMOBILE TOUCH EVENTS\r\ndocument.body.addEventListener(''touchmove'', function(event) {\r\n  event.preventDefault();\r\n}, false); \r\n\r\n*/\r\n\r\n\r\n		// HTML5   touch\r\n		var obj = document.getElementById(''foo'');\r\n		obj.addEventListener(''touchmove'', function(event) {\r\n		  // If there''s exactly one finger inside this element\r\n		  if (event.targetTouches.length == 1) {\r\n		    var touch = event.targetTouches[0];\r\n		    // Place element where the finger is\r\n		    obj.style.left = touch.pageX + ''px'';\r\n		    obj.style.top = touch.pageY + ''px'';\r\n		    alert("touch move");\r\n		  }\r\n		}, false);\r\n\r\n\r\n		 \r\n\r\n		 \r\n\r\n				 \r\n		 \r\n \r\n\r\n		</script>\r\n\r\n	</body>\r\n</html>', 'touch events : jQuery and HTML5 too', 'javascript'),
(196, ' <a href="#greeting=hi mom&reply=oh yeah">  A link element  </a>\r\n <script>\r\n        	 \r\n        	console.log("Page location is " + window.location.href);\r\n        	console.log("Page hash is " + window.location.hash);\r\n        	\r\n        </script>\r\n var foo = getParameterByName(''foo'');\r\n			console.log("ok:" + foo);\r\n		  function getParameterByName(name) {\r\n				 name = name.replace(/[\\[]/, "\\\\[").replace(/[\\]]/, "\\\\]");\r\n				 var regex = new RegExp("[\\\\?&]" + name + "=([^&#]*)"),\r\n					  results = regex.exec(location.search);\r\n				 return results === null ? "" : decodeURIComponent(results[1].replace(/\\+/g, " "));\r\n			}', 'Read a query string, a get query, a hash tag , read the URL', 'javascript'),
(197, '<?\r\n\r\nclass RSS {\r\n	public function RSS() {\r\n		require_once (''../mysql_connect.php'');\r\n	}\r\n\r\n	public function GetFeed() {\r\n		return $this -> getDetails() . $this -> getItems();\r\n	}\r\n\r\n	private function dbConnect() {\r\n		DEFINE(''LINK'', mysql_connect(DB_HOST, DB_USER, DB_PASSWORD));\r\n	}\r\n\r\n	private function getDetails() {\r\n		$detailsTable = "webref_rss_details";\r\n		$this -> dbConnect($detailsTable);\r\n		$query = "SELECT * FROM " . $detailsTable;\r\n		$result = mysql_db_query(DB_NAME, $query, LINK);\r\n\r\n		while ($row = mysql_fetch_array($result)) {\r\n			$details = ''<?xml version="1.0" encoding="ISO-8859-1" ?>\r\n    <rss version="2.0">\r\n     <channel>\r\n      <title>'' . $row[''title''] . ''</title>\r\n      <link>'' . $row[''link''] . ''</link>\r\n      <description>'' . $row[''description''] . ''</description>\r\n      <language>'' . $row[''language''] . ''</language>\r\n      <image>\r\n       <title>'' . $row[''image_title''] . ''</title>\r\n       <url>'' . $row[''image_url''] . ''</url>\r\n       <link>'' . $row[''image_link''] . ''</link>\r\n       <width>'' . $row[''image_width''] . ''</width>\r\n       <height>'' . $row[''image_height''] . ''</height>\r\n      </image>'';\r\n		}\r\n		return $details;\r\n	}\r\n\r\n	private function getItems() {\r\n		$itemsTable = "webref_rss_items";\r\n		$this -> dbConnect($itemsTable);\r\n		$query = "SELECT * FROM " . $itemsTable;\r\n		$result = mysql_db_query(DB_NAME, $query, LINK);\r\n		$items = '''';\r\n		while ($row = mysql_fetch_array($result)) {\r\n			$items .= ''<item>\r\n    <title>'' . $row["title"] . ''</title>\r\n    <link>'' . $row["link"] . ''</link>\r\n    <description><![CDATA['' . $row["description"] . '']]></description>\r\n   </item>'';\r\n		}\r\n		$items .= ''</channel>\r\n    </rss>'';\r\n		return $items;\r\n	}\r\n\r\n}\r\n?>\r\n\r\n\r\nCREATE TABLE `webref_rss_details` (\r\n  `id` int(11) NOT NULL auto_increment,\r\n  `title` text NOT NULL,\r\n  `description` mediumtext NOT NULL,\r\n  `link` text,\r\n  `language` text,\r\n  `image_title` text,\r\n  `image_url` text,\r\n  `image_link` text,\r\n  `image_width` text,\r\n  `image_height` text,\r\n  PRIMARY KEY  (`id`)\r\n) ENGINE=MyISAM DEFAULT CHARSET=utf8;\r\n\r\n\r\nCREATE TABLE `webref_rss_items` (\r\n  `id` int(11) NOT NULL auto_increment,\r\n  `title` text NOT NULL,\r\n  `description` mediumtext NOT NULL,\r\n  `link` text,\r\n  PRIMARY KEY  (`id`)\r\n) ENGINE=MyISAM DEFAULT CHARSET=utf8;', 'RSS class : create an RSS feed with PHP and mysql', 'php'),
