(208, 'flash UI:\r\n	button and textfield\r\n	myButton\r\n	myField\r\n\r\nAS code:\r\n	var myVar:Number = 0;\r\n	myField.text = myVar.toString();\r\n	\r\n	ExternalInterface.addCallback("sendToAS", getFromJS);\r\n	\r\n	function getFromJS(dir):void\r\n	{\r\n	myVar += dir;\r\n	myField.text = myVar.toString();\r\n	}\r\n	\r\n	myButton.addEventListener(MouseEvent.CLICK, sendToJS);\r\n	\r\n	function sendToJS(MouseEvent):void\r\n	{\r\n	ExternalInterface.call("receiveAS", myVar);\r\n	}\r\n	\r\nHTML/JAVASCRIPT:\r\n<html>\r\n	<head>\r\n\r\n		<script type="text/javascript">\r\n			function getFlashMovie(movieName) {\r\n				var isIE = navigator.appName.indexOf("Microsoft") != -1;\r\n				return (isIE) ? window[movieName] : document[movieName];\r\n			}\r\n\r\n			function sendNumber(num) {\r\n				getFlashMovie("com_js_as3").sendToAS(num);\r\n			}\r\n\r\n			function receiveAS(val) {\r\n				alert(val);\r\n			}\r\n\r\n		</script>\r\n\r\n		<title> JS-AS3 </title>\r\n	</head>\r\n	<body>\r\n		<object id="com_js_as3" data="com_js_as3.swf"\r\n		style="height: 500px; width: 500px;"\r\n		type="application/x-shockwave-flash">\r\n			<param name="movie" value="com_js_as3.swf">\r\n			<param name="wmode" value="opaque">\r\n		</object>\r\n		<br>\r\n		<a href="#" onMouseDown="sendNumber(1)">Add 1 to the value.</a>\r\n		<br>\r\n		<a href="#" onMouseDown="sendNumber(5)">Add 5 to the value.</a>\r\n		<br>\r\n		<a href="#" onMouseDown="sendNumber(10)">Add 10 to the value.</a>\r\n	</body>\r\n</html>', 'actionscript: communicate with javascript', 'javascript'),
(209, 'SWF SOLUTION:\r\n HTML/JS:\r\n <html>\r\n	<head>\r\n		<script type="text/javascript">\r\n			function getFlashMovie(movieName) {\r\n				var isIE = navigator.appName.indexOf("Microsoft") != -1;\r\n				return (isIE) ? window[movieName] : document[movieName];\r\n			}\r\n			 \r\n			function js_OnLoad(){\r\n				var loopInterval;\r\n					function initLoop() {\r\n						loopInterval = setInterval(loop, 500);\r\n					}\r\n					function loop() {\r\n						 if(typeof getFlashMovie("integration").js_OnLoad() == "undefined"){\r\n						 	clearInterval(loopInterval);\r\n						 }\r\n					}\r\n				initLoop();\r\n			}\r\n			 \r\n			function js_log(val) {\r\n				console.log(''js_log(fromFlash)  : ''+val);\r\n			}\r\n			function myfunction(val)\r\n			{\r\n			   alert("hello..movie finished!" + val); // replace with some jquery \r\n			}\r\n\r\n		</script>\r\n\r\n		<title> Combo: AS3 and JS communication</title>\r\n	</head>\r\n	<body onload="js_OnLoad();">\r\n		<p>Flash .swf object</p>\r\n		<div style="border: solid 1px black;  ">\r\n			\r\n			<object id="integration" data="integration.swf"\r\n			style="height: 500px; width: 500px;"\r\n			type="application/x-shockwave-flash">\r\n				<param name="movie" value="integration.swf">\r\n				<param name="wmode" value="opaque">\r\n			</object>\r\n		</div>\r\n		\r\n		<br>\r\n		<div style="border: solid 1px black; padding:2px;">\r\n		 	<p>Javascript UI</p>\r\n		 	<p>Enter the query string parameter "movie=xxx.swf" in the URL to load a swf file.</p>\r\n		 	<ul>\r\n		 		<li>\r\n		 			redball.swf\r\n		 		</li>\r\n		 		<li>\r\n		 			blueball.swf\r\n		 		</li>\r\n		 		<li>\r\n		 			AP_BusinessUpdate_SingleStory.swf\r\n		 		</li>\r\n		 		 \r\n		 	</ul>\r\n		 \r\n			 \r\n	</div>\r\n	<script type="text/javascript">\r\n		\r\n		</script>\r\n\r\n	</body>\r\n</html>\r\n////////////////////////////////////////////////////////////////////////////////////////////////////\r\n// ACTIONSCRIPT3:\r\nimport flash.events.MouseEvent;\r\nimport flash.display.Loader;\r\nimport flash.events.Event;\r\nimport flash.display.MovieClip;\r\nimport flash.display.LoaderInfo;\r\n \r\n \r\nimport flash.utils.getQualifiedClassName;\r\nvar mc: MovieClip; \r\n \r\nvar urlStr:String = ExternalInterface.call(''window.location.href.toString'');\r\nvar gotMovie:Number;\r\nvar myLoader:Loader = new Loader();\r\nvar moviePath:String ="";\r\n\r\nExternalInterface.addCallback("sendMovieFromJSToAS3", loadMovieByJS);\r\nExternalInterface.addCallback("js_OnLoad", js_loaded);\r\n \r\nfunction js_loaded():void{\r\n	if(gotMovie != 0){\r\n		var myMovieURL:String = getMyMoviePath();\r\n		loadMovieByJS(myMovieURL);\r\n		gotMovie = 1;\r\n	}\r\n}\r\nfunction loadMovieByJS(movie:String):void\r\n{\r\n	var external_2:MovieClip; \r\n	moviePath = movie;\r\n	var url:URLRequest = new URLRequest(movie);\r\n 	myLoader.load(url);                                     \r\n	addChild(myLoader);                                     \r\n	myLoader.x = 0;                                            \r\n	myLoader.y = 0; \r\n	myLoader.contentLoaderInfo.addEventListener(Event.COMPLETE, onCompleteHandler);\r\n	myLoader.load(new URLRequest(movie)); \r\n	//var external_2:MovieClip;\r\n	//external_2=MovieClip(myLoader.content);\r\n	//external_2.x=0;\r\n	//external_2.y=0;\r\n	//trace(external_2.name);\r\n 	//trace(external_2.totalframes);\r\n	//ExternalInterface.call("js_log",external_2.totalframes ); //external_2.totalframes\r\n	ExternalInterface.call("js_log","fail?again?" ); //external_2.totalframes\r\n}\r\n \r\nfunction onCompleteHandler(e:Event){\r\n    //do something when ball.swf has loaded\r\n	 sendToJS(Event);\r\n	 \r\n	  if( !e.target )\r\n        return;\r\n	\r\n    trace( getQualifiedClassName( e.target.content ) );\r\n    /* if you get: flash.display::AVM1Movie\r\n       it means you are trying to load an AS1 or AS2 SWF\r\n       into AS3 SWF. They both need to be AS3 */\r\n\r\n    mc = e.target.content as MovieClip;\r\n    mc.gotoAndPlay( 1 );\r\n	 	trace(mc.name);\r\n 		trac