ameterString = urlMassage2;\r\n	var secondSplitIndex:Number = mainFeedURLParameter.indexOf("=");\r\n	var mainFeedURL:String =mainFeedURLParameter.substr(secondSplitIndex+1);\r\n	// SET MAIN FEED URL ////////////////\r\n	_feedURL = mainFeedURL;\r\n	/////////////////////////////////////\r\n	//trace("Additional Params:"+additionalParameterString);\r\n	 var additionalParams:Array = additionalParameterString.split("&");\r\n	 var num = additionalParams.length;\r\n		var i:int; \r\n		for (i = 0; i < num; i++) \r\n		{ \r\n			//trace(i); \r\n			var keyValueString:String = additionalParams[i];\r\n			// extract keys and values\r\n			var keyValueArray:Array = keyValueString.split("=");\r\n			//switch based on key and assign value as appropriate\r\n			var key:String  = keyValueArray[0];\r\n			var val:String = keyValueArray[1];\r\n			switch(key) \r\n			{ \r\n				 \r\n				case "movie": \r\n					_movieToLoad  = val;\r\n					break; \r\n				 \r\n				case "refreshrate":\r\n					//_refreshrate = Number(val);\r\n					break;\r\n				default: \r\n					//trace("Out of range"); \r\n					_movieToLoad  = "no val";\r\n					break; \r\n			}\r\n		}\r\n	 \r\n	return _movieToLoad; \r\n	*/', 'actionscript: that communicates with javascript and loads movies swf', 'javascript'),
(210, '<!DOCTYPE html>\r\n<html>\r\n\r\n	<head>\r\n		<meta charset="utf-8"/>\r\n		<title>Mini Ajax File Upload Form</title>\r\n\r\n		<!-- Google web fonts -->\r\n	 \r\n		 \r\n		  \r\n	</head>\r\n\r\n	<body  >\r\n\r\n		 \r\n<!-- https://www.apptha.com/blog/how-to-reduce-image-file-size-while-uploading-using-php-code/  -->\r\n\r\n\r\n<?php $name = '''';\r\n	$type = '''';\r\n	$size = '''';\r\n	$error = '''';\r\n	function compress_image($source_url, $destination_url, $quality) {\r\n\r\n		$info = getimagesize($source_url);\r\n\r\n		if ($info[''mime''] == ''image/jpeg'')\r\n			$image = imagecreatefromjpeg($source_url);\r\n		elseif ($info[''mime''] == ''image/gif'')\r\n			$image = imagecreatefromgif($source_url);\r\n		elseif ($info[''mime''] == ''image/png'')\r\n			$image = imagecreatefrompng($source_url);\r\n\r\n		imagejpeg($image, $destination_url, $quality);\r\n		return $destination_url;\r\n	}\r\n\r\n	if ($_POST) {\r\n\r\n		if ($_FILES["file"]["error"] > 0) {\r\n			$error = $_FILES["file"]["error"];\r\n		} else if (($_FILES["file"]["type"] == "image/gif") || ($_FILES["file"]["type"] == "image/jpeg") || ($_FILES["file"]["type"] == "image/png") || ($_FILES["file"]["type"] == "image/pjpeg")) {\r\n\r\n			$url = ''destination .jpg'';\r\n\r\n			$filename = compress_image($_FILES["file"]["tmp_name"], $url, 80);\r\n			$buffer = file_get_contents($url);\r\n move_uploaded_file($filename, ''uploads/''.$filename);\r\n			/* Force download dialog... */\r\n			header("Content-Type: application/force-download");\r\n			header("Content-Type: application/octet-stream");\r\n			header("Content-Type: application/download");\r\n\r\n			/* Don''t allow caching... */\r\n			header("Cache-Control: must-revalidate, post-check=0, pre-check=0");\r\n\r\n			/* Set data type, size and filename */\r\n			header("Content-Type: application/octet-stream");\r\n			header("Content-Transfer-Encoding: binary");\r\n			header("Content-Length: " . strlen($buffer));\r\n			header("Content-Disposition: attachment; filename=$url");\r\n\r\n			/* Send our file... */\r\n			echo $buffer;\r\n		} else {\r\n			$error = "Uploaded image should be jpg or gif or png";\r\n		}\r\n	}\r\n?>\r\n \r\n\r\n		<div class="message">\r\n                    	<?php\r\n                    		if($_POST){\r\n                        		if ($error) {\r\n                            		?>\r\n                            		<label class="error"><?php echo $error; ?></label>\r\n                        			<?php\r\n								}\r\n							}\r\n                    	?>\r\n                	</div>\r\n		<fieldset class="well">\r\n            <legend>Upload Image:</legend>                \r\n			<form action="" name="myform" id="myform" method="post" enctype="multipart/form-data">\r\n				<ul>\r\n			        <li>\r\n						<label>Upload:</label>\r\n			            <input type="file" name="file" id="file"/>\r\n					</li>\r\n					<li>\r\n						<input type="submit" name="submit" id="submit" class="submit btn-success"/>\r\n					</li>\r\n				</ul>\r\n			</form>\r\n		</fieldset>\r\n		\r\n	 \r\n	</body>\r\n</html>', 'load and compress photo image ', 'php'),
(211, 'What you have will work as long as you first escape the regex characters in your pattern. Below is one example from Simon Willisonâ€™s blog:\r\n\r\nRegExp.escape = function(text) {\r\n  if (!arguments.callee.sRE) {\r\n    var specials = [\r\n      ''/'', ''.'', ''*'', ''+'', ''?'', ''|'',\r\n      ''('', '')'', ''['', '']'', ''{'', ''}'', ''\\\\''\r\n    ];\r\n    arguments.callee.sRE = new RegExp(\r\n      ''(\\\\'' + specials.join(''|\\\\'') + '')'', ''g''\r\n    );\r\n  }\r\n  return text.replace(arguments.callee.sRE, ''\\\\$1'');\r\n}\r\nYou could then implement your code as:\r\n\r\nlikeExpr = RegExp.escape(likeExpr);\r\nvar match = new RegEx(likeExpr.replace("%", ".*").replace("_", ".")).exec(str) != null;', 'JAVASCRIPT SQL ''LIKE'' equivalent ?? untested myself', 'javascript'),
