(213, '<!DOCTYPE html>\r\n<html>\r\n	<head>\r\n		<meta charset="utf-8">\r\n		<!--\r\n		<link rel=''stylesheet'' type=''text/css'' href=''style_sheet_name.css''>\r\n		<script type=''text/javascript'' src=''../00_js/jquery-1.9.1.js''></script>\r\n		 -->\r\n	</head>\r\n	<body>\r\n		<form id="passwordForm" action="server.php" method="post" novalidate>\r\n    <fieldset>\r\n        <legend>Change Your Password</legend>\r\n        <ul>\r\n            <li>\r\n                <label for="password1">Password 1:</label>\r\n                <input type="password" required id="password1" name="password1"/>\r\n            </li>\r\n            <li>\r\n                <label for="password2">Password 2:</label>\r\n                <input type="password" required id="password2"  name="password2"/>\r\n            </li>\r\n        </ul>\r\n        <input type="submit" />\r\n    </fieldset>\r\n</form>\r\n<script>\r\n    var password1 = document.getElementById(''password1'');\r\n    var password2 = document.getElementById(''password2'');\r\n\r\n    var checkPasswordValidity = function() {\r\n        if (password1.value != password2.value) {\r\n            password1.setCustomValidity(''Passwords must match.'');\r\n        } else {\r\n            password1.setCustomValidity('''');\r\n        }        \r\n    };\r\n\r\n    password1.addEventListener(''change'', checkPasswordValidity, false);\r\n    password2.addEventListener(''change'', checkPasswordValidity, false);\r\n\r\n    var form = document.getElementById(''passwordForm'');\r\n    form.addEventListener(''submit'', function() {\r\n        checkPasswordValidity();\r\n        if (!this.checkValidity()) {\r\n            event.preventDefault();\r\n            //Implement you own means of displaying error messages to the user here.\r\n            alert(''something else is required...'');\r\n            password1.focus();\r\n        }\r\n    }, false);\r\n</script> \r\n	 \r\n			\r\n	<script>\r\n		var forms = document.getElementsByTagName(''form'');\r\n	for (var i = 0; i < forms.length; i++) {\r\n	    forms[i].noValidate = true;\r\n	\r\n	    forms[i].addEventListener(''submit'', function(event) {\r\n	        //Prevent submission if checkValidity on the form returns false.\r\n	        if (!event.target.checkValidity()) {\r\n	            event.preventDefault();\r\n	            //Implement you own means of displaying error messages to the user here.\r\n	        }else{\r\n	        	alert(''mobile form submit success'');\r\n	        }\r\n	    }, false);\r\n	}\r\n	</script>\r\n\r\n	</body>\r\n</html>\r\n', 'mobile web app with required fields on a form', 'javascript'),
