(219, '<!DOCTYPE html>\r\n<html>\r\n	<head>\r\n		<meta charset="utf-8">\r\n		<!--\r\n		<link rel=''stylesheet'' type=''text/css'' href=''style_sheet_name.css''>\r\n		<script type=''text/javascript'' src=''../00_js/jquery-1.9.1.js''></script>\r\n		-->\r\n		<style>\r\n			#myelement {\r\n				width: 500px;\r\n				height:100%;\r\n			}\r\n\r\n			#myelement:full-screen {\r\n				width: 100%;\r\n				height:100%;\r\n			}\r\n\r\n			#myelement:full-screen img {\r\n				width: 100%;\r\n				height:100%;\r\n			}\r\n			/* this works */\r\n			#myelement:-webkit-full-screen	{ width: 100% }\r\n			#myelement:-moz-full-screen		{ width: 100% }\r\n			#myelement:-ms-full-screen		{ width: 100% }\r\n			#myelement:-o-full-screen		{ width: 100% }\r\n			#myelement:full-screen			{ width: 100% }\r\n		</style>\r\n	</head>\r\n	<body>\r\n		<div id="myelement">myelement fullscreen click?</div>\r\n		<script>\r\n			$(document).ready(function() {\r\n				console.log("ready!");\r\n			});\r\n		</script>\r\n		<script>\r\n			var pfx = ["webkit", "moz", "ms", "o", ""];\r\n			function RunPrefixMethod(obj, method) {\r\n\r\n				var p = 0, m, t;\r\n				while (p < pfx.length && !obj[m]) {\r\n					m = method;\r\n					if (pfx[p] == "") {\r\n						m = m.substr(0, 1).toLowerCase() + m.substr(1);\r\n					}\r\n					m = pfx[p] + m;\r\n					t = typeof obj[m];\r\n					if (t != "undefined") {\r\n						pfx = [pfx[p]];\r\n						return (t == "function" ? obj[m]() : obj[m]);\r\n					}\r\n					p++;\r\n				}\r\n\r\n			}\r\n\r\n			var e = document.getElementById("myelement");\r\n \r\n			e.onclick = function() {\r\n\r\n				if (RunPrefixMethod(document, "FullScreen") || RunPrefixMethod(document, "IsFullScreen")) {\r\n					RunPrefixMethod(document, "CancelFullScreen");\r\n				} else {\r\n					RunPrefixMethod(e, "RequestFullScreen");\r\n				}\r\n\r\n			};\r\n			\r\n			 \r\n\r\n		</script>\r\n	</body>\r\n</html>', 'full screen ', 'javascript'),
