(235, '<?php\r\n$dbhost = "localhost";\r\n$dbname = "pdo";\r\n$dbusername = "root";\r\n$dbpassword = "845625";\r\n\r\n$link = new PDO("mysql:host=$dbhost;dbname=$dbname",$dbusername,$dbpassword);\r\n\r\n$statement = $link->prepare("INSERT INTO testtable(name, lastname, age)\r\n    VALUES(:fname, :sname, :age)");\r\n$statement->execute(array(\r\n    "fname" => "Bob",\r\n    "sname" => "Desaunois",\r\n    "age" => "18"\r\n));\r\nPrepared statements are used to sanitize your input, and to do that you can use :foo without any single quotes within the SQL to bind variables, and then in the execute() function you pass in an array of the variables you defined in the SQL statement.\r\n\r\nYou may also use ? instead of :foo and then pass in an array of just the values to input like so;\r\n\r\n$statement = $link->prepare("INSERT INTO testtable(name, lastname, age)\r\n    VALUES(?, ?, ?)");\r\n\r\n$statement->execute(array("Bob", "Desaunois", "18"));', 'PDO insert example ', 'php'),
(236, '<?php\r\n$dbhost = "localhost";\r\n$dbname = "pdo";\r\n$dbusername = "root";\r\n$dbpassword = "845625";\r\n\r\n$link = new PDO("mysql:host=$dbhost;dbname=$dbname",$dbusername,$dbpassword);\r\n\r\n$statement = $link->prepare("INSERT INTO testtable(name, lastname, age)\r\n    VALUES(:fname, :sname, :age)");\r\n$statement->execute(array(\r\n    "fname" => "Bob",\r\n    "sname" => "Desaunois",\r\n    "age" => "18"\r\n));\r\nPrepared statements are used to sanitize your input, and to do that you can use :foo without any single quotes within the SQL to bind variables, and then in the execute() function you pass in an array of the variables you defined in the SQL statement.\r\n\r\nYou may also use ? instead of :foo and then pass in an array of just the values to input like so;\r\n\r\n$statement = $link->prepare("INSERT INTO testtable(name, lastname, age)\r\n    VALUES(?, ?, ?)");\r\n\r\n$statement->execute(array("Bob", "Desaunois", "18"));', 'PDO insert example ', 'php'),
(237, '<?php\r\n$servername = "localhost";\r\n$username = "username";\r\n$password = "password";\r\n$dbname = "myDBPDO";\r\n\r\ntry {\r\n    $conn = new PDO("mysql:host=$servername;dbname=$dbname", $username, $password);\r\n    // set the PDO error mode to exception\r\n    $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\r\n    $sql = "INSERT INTO MyGuests (firstname, lastname, email)\r\n    VALUES (''John'', ''Doe'', ''john@example.com'')";\r\n    // use exec() because no results are returned\r\n    $conn->exec($sql);\r\n    echo "New record created successfully";\r\n    }\r\ncatch(PDOException $e)\r\n    {\r\n    echo $sql . "<br>" . $e->getMessage();\r\n    }\r\n\r\n$conn = null;\r\n?>', 'PDO INSERT', 'php'),
(238, '<?php\r\n$servername = "localhost";\r\n$username = "username";\r\n$password = "password";\r\n$dbname = "myDBPDO";\r\n\r\ntry {\r\n    $conn = new PDO("mysql:host=$servername;dbname=$dbname", $username, $password);\r\n    // set the PDO error mode to exception\r\n    $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\r\n\r\n    // prepare sql and bind parameters\r\n    $stmt = $conn->prepare("INSERT INTO MyGuests (firstname, lastname, email) \r\n    VALUES (:firstname, :lastname, :email)");\r\n    $stmt->bindParam('':firstname'', $firstname);\r\n    $stmt->bindParam('':lastname'', $lastname);\r\n    $stmt->bindParam('':email'', $email);\r\n\r\n    // insert a row\r\n    $firstname = "John";\r\n    $lastname = "Doe";\r\n    $email = "john@example.com";\r\n    $stmt->execute();\r\n\r\n    // insert another row\r\n    $firstname = "Mary";\r\n    $lastname = "Moe";\r\n    $email = "mary@example.com";\r\n    $stmt->execute();\r\n\r\n    // insert another row\r\n    $firstname = "Julie";\r\n    $lastname = "Dooley";\r\n    $email = "julie@example.com";\r\n    $stmt->execute();\r\n\r\n    echo "New records created successfully";\r\n    }\r\ncatch(PDOException $e)\r\n    {\r\n    echo "Error: " . $e->getMessage();\r\n    }\r\n$conn = null;\r\n?>', 'PHP PDO prepared statements', 'php'),
(239, '<?php\r\necho "<table style=''border: solid 1px black;''>";\r\necho "<tr><th>Id</th><th>Firstname</th><th>Lastname</th></tr>";\r\n\r\nclass TableRows extends RecursiveIteratorIterator { \r\n    function __construct($it) { \r\n        parent::__construct($it, self::LEAVES_ONLY); \r\n    }\r\n\r\n    function current() {\r\n        return "<td style=''width:150px;border:1px solid black;''>" . parent::current(). "</td>";\r\n    }\r\n\r\n    function beginChildren() { \r\n        echo "<tr>"; \r\n    } \r\n\r\n    function endChildren() { \r\n        echo "</tr>" . "\\n";\r\n    } \r\n} \r\n\r\n$servername = "localhost";\r\n$username = "username";\r\n$password = "password";\r\n$dbname = "myDBPDO";\r\n\r\ntry {\r\n    $conn = new PDO("mysql:host=$servername;dbname=$dbname", $username, $password);\r\n    $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\r\n    $stmt = $conn->prepare("SELECT id, firstname, lastname FROM MyGuests"); \r\n    $stmt->execute();\r\n\r\n    // set the resulting array to associative\r\n    $result = $stmt->setFetchMode(PDO::FETCH_ASSOC); \r\n    foreach(new TableRows(new RecursiveArrayIterator($stmt->fetchAll())) as $k=>$v) { \r\n        echo $v;\r\n    }\r\n}\r\ncatch(PDOException $e) {\r\n    echo "Error: " . $e->getMessage();\r\n}\r\n$conn = null;\r\necho "</table>";\r\n?>', 'PHP PDO SELECT ', 'php'),
(240, '<?php\r\n$servername = "localhost";\r\n$username = "username";\r\n$password = "password";\r\n$dbname = "myDBPDO";\r\n\r\ntry {\r\n    $conn = new PDO("mysql:host=$servername;dbname=$dbname", $username, $password);\r\n    // set the PDO error mode to exception\r\n    $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\r\n\r\n    $sql = "UPDATE MyGuests SET lastname=''Doe'' WHERE id=2";\r\n\r\n    // Prepare statement\r\n    $stmt = $conn->prepare($sql);\r\n\r\n    // execute the query\r\n    $stmt->execute();\r\n\r\n    // echo a message to say the UPDATE succeeded\r\n    echo $stmt->rowCount() . " records UPDATED successfully";\r\n    }\r\ncatch(PDOException $e)\r\n    {\r\n    echo $sql . "<br>" . $e->getMessage();\r\n    }\r\n\r\n$conn = null;\r\n?>', 'PDO UPDATE', 'php'),
(241, '<?php\r\n$servername = "localhost";\r\n$username = "username";\r\n$password = "password";\r\n$dbname = "myDBPDO";\r\n\r\ntry {\r\n    $conn = new PDO("mysql:host=$servername;dbname=$dbname", $username, $password);\r\n    // set the PDO error mode to exception\r\n    $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\r\n\r\n    // sql to delete a record\r\n    $sql = "DELETE FROM MyGuests WHERE id=3";\r\n\r\n    // use exec() because no results are returned\r\n    $conn->exec($sql);\r\n    echo "Record deleted successfully";\r\n    }\r\ncatch(PDOException $e)\r\n    {\r\n    echo $sql . "<br>" . $e->getMessage();\r\n    }\r\n\r\n$conn = null;\r\n?>', 'PDO DELETE', 'php'),
(242, '<?php\r\n$servername = "localhost";\r\n$username = "username";\r\n$password = "password";\r\n$dbname = "myDBPDO";\r\n\r\ntry {\r\n    $conn = new PDO("mysql:host=$servername;dbname=$dbname", $username, $password);\r\n    // set the PDO error mode to exception\r\n    $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\r\n    $sql = "INSERT INTO MyGuests (firstname, lastname, email)\r\n    VALUES (''John'', ''Doe'', ''john@example.com'')";\r\n    // use exec() because no results are returned\r\n    $conn->exec($sql);\r\n    $last_id = $conn->lastInsertId();\r\n    echo "New record created successfully. Last inserted ID is: " . $last_id;\r\n    }\r\ncatch(PDOException $e)\r\n    {\r\n    echo $sql . "<br>" . $e->getMessage();\r\n    }\r\n\r\n$conn = null;\r\n?>', 'PDO GET LAST INSERT ID', 'php'),
(243, '<!DOCTYPE html>\r\n<html>\r\n	<head>\r\n		<meta charset="utf-8">\r\n		<script type=''text/javascript'' src=''jquery-1.9.1.js''></script>\r\n		<!--\r\n		4 Parts:\r\n		1) the call\r\n		2) the ajax function\r\n		3) the callback function\r\n		4) the ajax server\r\n		-->\r\n		<!--\r\n		//the ajax server\r\n		<?php\r\n		//"ajax_server.php" : the essential gist of it.\r\n		$json = json_encode($_POST);\r\n		//or get mysql data and json_encode it.\r\n		echo($json);\r\n		?>\r\n		-->\r\n	</head>\r\n	<body>\r\n		<script>\r\n			//callback function\r\n			function ajaxResponseHandler(json) {\r\n				alert(json);\r\n				var obj = JSON.parse(json);\r\n				var num = obj.length;\r\n				console.log(obj);\r\n\r\n			}\r\n\r\n			// the call\r\n			ajax_datastring_URL_callback("foo=bar&jack=daniels", "ajax_server.php", ajaxResponseHandler);\r\n			//the ajax function\r\n			function ajax_datastring_URL_callback(dataString, URL, callback) {\r\n				// POST\r\n				var xmlhttp;\r\n				if (window.XMLHttpRequest) {// code for IE7+, Firefox, Chrome, Opera, Safari\r\n					xmlhttp = new XMLHttpRequest();\r\n				} else {// code for IE6, IE5\r\n					xmlhttp = new ActiveXObject("Microsoft.XMLHTTP");\r\n				}\r\n				xmlhttp.onreadystatechange = function() {\r\n					if (xmlhttp.readyState == 4 && xmlhttp.status == 200) {\r\n						callback(xmlhttp.responseText);\r\n					};\r\n				};\r\n				xmlhttp.open("POST", URL, true);\r\n				xmlhttp.setRequestHeader("Content-type", "application/x-www-form-urlencoded");\r\n				xmlhttp.send(dataString);\r\n			}\r\n		</script>\r\n\r\n	</body>\r\n</html>\r\n', 'ajax complete cycle: 4 Parts: 1) the call, 2) the ajax function, 3) the callback function, and  4) the ajax server.', 'javascript'),
(244, 'function ajax_datastring_URL_callback(dataString, URL, callback) {\r\n				// POST\r\n				var xmlhttp;\r\n				if (window.XMLHttpRequest) {// code for IE7+, Firefox, Chrome, Opera, Safari\r\n					xmlhttp = new XMLHttpRequest();\r\n				} else {// code for IE6, IE5\r\n					xmlhttp = new ActiveXObject("Microsoft.XMLHTTP");\r\n				}\r\n				xmlhttp.onreadystatechange = function() {\r\n					if (xmlhttp.readyState == 4 && xmlhttp.status == 200) {\r\n						callback(xmlhttp.responseText);\r\n					};\r\n				};\r\n				xmlhttp.open("POST", URL, true);\r\n				xmlhttp.setRequestHeader("Content-type", "application/x-www-form-urlencoded");\r\n				xmlhttp.send(dataString);\r\n			}', 'ajax_datastring_URL_callback(dataString, URL, callback)', 'javascript'),
