(249, '<?php\r\n \r\nclass UserEntity {\r\n	//this is a private variable, and can only be accessed by this class\r\n	private $id;\r\n	private $username_email;\r\n	private $phone;\r\n	private $address;\r\n	private $city;\r\n	private $state;\r\n	private $zipcode;\r\n	private $authorization;\r\n	private $password;\r\n	\r\n	\r\n\r\n	function __construct($username_email,$phone,$address,$city,$state,$zipcode,$authorization,$password) {\r\n		$this->id = 0;\r\n		$this -> username_email = $username_email;\r\n		$this -> phone = $phone;\r\n		$this -> address = $address;\r\n		$this -> city = $city;\r\n		$this -> state = $state;\r\n		$this -> zipcode = $zipcode;\r\n		$this -> authorization = $authorization;\r\n		$this -> password = $password;\r\n	}\r\n	/*\r\n	 * public function setName($nameIn) {\r\n		$this -> username_email = $nameIn;\r\n	}\r\n	 */\r\n	 \r\n	public function set_id($id) {\r\n		$this -> id = $id;\r\n	}\r\n	\r\n	public function get_id() {\r\n		return $this -> id;\r\n	}\r\n	public function get_username_email() {\r\n		return $this -> username_email;\r\n	}\r\n	public function get_phone() {\r\n		return $this -> phone;\r\n	}\r\n	public function get_address() {\r\n		return $this -> address;\r\n	}\r\n	public function get_city() {\r\n		return $this -> city;\r\n	}\r\n	public function get_state() {\r\n		return $this -> state;\r\n	}\r\n	public function get_zipcode() {\r\n		return $this -> zipcode;\r\n	}\r\n	public function get_authorization() {\r\n		return $this -> authorization;\r\n	}\r\n	public function get_password() {\r\n		return $this -> password;\r\n	}\r\n	\r\n\r\n}\r\n?>', 'Entity for a table object, class', 'php'),
(250, '<?php\r\ninclude_once (''Model.model.php'');\r\n\r\nclass User extends Model {\r\n	public function __construct() {\r\n		parent::__construct();\r\n	}\r\n\r\n	public function model_connect() {\r\n		return parent::connect();\r\n	}\r\n\r\n	public function readUsers($return = "") {\r\n		$this -> model_connect();\r\n		$sql = " SELECT * FROM " . $this -> getDatabase() . ".user ;";\r\n		//WHERE username_email = ''bayon@gocodigo.com'' AND password = ''pass''\r\n		$data = $this -> exe_sql($sql, $return);\r\n		return $data;\r\n	}\r\n\r\n	function login($username, $password, $return = "") {\r\n		 $this -> model_connect();\r\n		$sql = "SELECT * FROM " . $this -> getDatabase() . ".user WHERE username_email = ''" . $username . "'' AND password = ''" . $password . "'';";\r\n		$data = $this -> exe_sql($sql, $return);\r\n		return $data;\r\n	}\r\n//function createNewUser($auth_val,$password,$email,$return = "json") {\r\n	function createNewUser($user_entity,$return = "json") {\r\n		$this -> model_connect();\r\n		$sql = "INSERT INTO " . $this -> getDatabase() . ".user \r\n		(id, authorization, username_email, password, company_id, location_id, phone, address, city, state, zipcode) \r\n 		VALUES \r\n 		(NULL, ''" . $user_entity->get_authorization() . "'', ''" . $user_entity->get_username_email() . "'', ''" . $user_entity->get_password() . "'', ''1'', ''1'', ''" . $user_entity->get_phone() . "'', ''" . $user_entity->get_address() . "'', ''" . $user_entity->get_city() . "'', ''" . $user_entity->get_state() . "'', ''" . $user_entity->get_zipcode() . "'');";\r\n		\r\n		$data = $this -> exe_sql($sql, $return);\r\n		 // in the case of an insert , the return data will be the "last id inserted".\r\n		echo($data);\r\n		\r\n	}\r\n	\r\n	function updateUser($user_entity,$return = "json") {\r\n		$this -> model_connect();\r\n		$sql = "UPDATE " . $this -> getDatabase() . ".user set authorization = ''".$user_entity->get_authorization()."'', username_email = ''".$user_entity->get_username_email()."'' , phone = ''".$user_entity->get_phone()."''  , address = ''".$user_entity->get_address()."'' , city = ''".$user_entity->get_city()."'' , state = ''".$user_entity->get_state()."'' , zipcode = ''".$user_entity->get_zipcode()."'' WHERE id =  ".$user_entity->get_id()." ;";\r\n		\r\n		$data = $this -> exe_sql($sql, $return);\r\n		 // in the case of an insert , the return data will be the "last id inserted".\r\n		echo($data);\r\n		\r\n	}\r\n\r\n}\r\n?>', 'Model for a table, entity , class , object', 'php'),
(251, '<?php\r\nclass Model {\r\n	private $database;\r\n	private $host;\r\n	private $username;\r\n	private $password;\r\n	function __construct() {\r\n		$this -> database = ''ameriana_dev'';\r\n		$this -> host = ''localhost'';\r\n		$this -> username = ''root'';\r\n		$this -> password = '''';\r\n	}\r\n	public function getDatabase() {\r\n		return $this -> database;\r\n	}\r\n	public function setDatabase($database) {\r\n		$this -> database = $database;\r\n	}\r\n	public function connect() {\r\n		 mysql_connect($this->host,$this->username, $this->password);\r\n	}\r\n	public function exe_sql($sql,$return=""){\r\n		$res = mysql_query($sql);\r\n		//echo("</br>---- GET TYPE ---".gettype($res)."</br>");\r\n		 \r\n		if(gettype($res) == "boolean"){\r\n			//INSERT creates a boolean for $res\r\n			//return last id\r\n			 return mysql_insert_id();\r\n			//return $res;\r\n		}\r\n		 \r\n		$data = "";\r\n		while ($row = mysql_fetch_assoc($res)) {\r\n			$data[] = $row;\r\n		}\r\n		if($return =="json"){\r\n			return json_encode($data);\r\n		}else{\r\n			return $data;\r\n		}\r\n	}\r\n}\r\n?>', 'BASE MODEL for models', 'php'),
