(255, '<?php\r\n \r\n// Input: $filePath : path to image file\r\n// Output: If there is any orientation required then function will change that image accordingly\r\nfunction setDeviceRotation($filePath) {\r\n$exif = exif_read_data($filePath);\r\n \r\n//if there is orientation change\r\n$exif_orient = isset($exif[''Orientation'']) ? $exif[''Orientation''] : 0;\r\n$rotateImage = 0;\r\n \r\n//convert exif rotation to angles\r\nif (6 == $exif_orient) {\r\n$rotateImage = 90;\r\n$imageOrientation = 1;\r\n} elseif (3 == $exif_orient) {\r\n$rotateImage = 180;\r\n$imageOrientation = 1;\r\n} elseif (8 == $exif_orient) {\r\n$rotateImage = 270;\r\n$imageOrientation = 1;\r\n}\r\n//if the image is rotated\r\nif ($rotateImage) {\r\n$rotateImage = -$rotateImage;\r\n$info = getimagesize($filePath);\r\n$fileType = $info[2];\r\nswitch ($fileType) {\r\ncase ''2'' :\r\n//image/jpeg\r\n$source = imagecreatefromjpeg($filePath);\r\n$rotate = imagerotate($source, $rotateImage, 0);\r\nimagejpeg($rotate, $filePath);\r\nbreak;\r\ncase ''3'' :\r\n//image/png\r\n$source = imagecreatefrompng($filePath);\r\n$rotate = imagerotate($source, $rotateImage, 0);\r\nimagepng($rotate, $filePath);\r\nbreak;\r\ncase ''1'' :\r\n$source = imagecreatefromgif($filePath);\r\n$rotate = imagerotate($source, $rotateImage, 0);\r\nimagegif($rotate, $filePath);\r\nbreak;\r\ndefault :\r\nbreak;\r\n}\r\n}\r\n// The image orientation is fixed, pass it back for further processing\r\nreturn $filePath; //$file\r\n}\r\n \r\n//setDeviceRotation("yourimagename.jpg");\r\n?>', 'setDeviceRotation  rotate images from mobile device upload photos', 'php'),
(256, '<?php\r\n \r\n/*\r\n* File: SimpleImage.php\r\n* Author: Simon Jarvis\r\n* Copyright: 2006 Simon Jarvis\r\n* Date: 08/11/06\r\n* Link: http://www.white-hat-web-design.co.uk/articles/php-image-resizing.php\r\n*\r\n* This program is free software; you can redistribute it and/or\r\n* modify it under the terms of the GNU General Public License\r\n* as published by the Free Software Foundation; either version 2\r\n* of the License, or (at your option) any later version.\r\n*\r\n* This program is distributed in the hope that it will be useful,\r\n* but WITHOUT ANY WARRANTY; without even the implied warranty of\r\n* MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE. See the\r\n* GNU General Public License for more details:\r\n* http://www.gnu.org/licenses/gpl.html\r\n*\r\n*/\r\n \r\nclass SimpleImage {\r\n \r\n   var $image;\r\n   var $image_type;\r\n \r\n   function load($filename) {\r\n \r\n      $image_info = getimagesize($filename);\r\n      $this->image_type = $image_info[2];\r\n      if( $this->image_type == IMAGETYPE_JPEG ) {\r\n \r\n         $this->image = imagecreatefromjpeg($filename);\r\n      } elseif( $this->image_type == IMAGETYPE_GIF ) {\r\n \r\n         $this->image = imagecreatefromgif($filename);\r\n      } elseif( $this->image_type == IMAGETYPE_PNG ) {\r\n \r\n         $this->image = imagecreatefrompng($filename);\r\n      }\r\n   }\r\n   function save($filename, $image_type=IMAGETYPE_JPEG, $compression=75, $permissions=null) {\r\n \r\n      if( $image_type == IMAGETYPE_JPEG ) {\r\n         imagejpeg($this->image,$filename,$compression);\r\n      } elseif( $image_type == IMAGETYPE_GIF ) {\r\n \r\n         imagegif($this->image,$filename);\r\n      } elseif( $image_type == IMAGETYPE_PNG ) {\r\n \r\n         imagepng($this->image,$filename);\r\n      }\r\n      if( $permissions != null) {\r\n \r\n         chmod($filename,$permissions);\r\n      }\r\n   }\r\n   function output($image_type=IMAGETYPE_JPEG) {\r\n \r\n      if( $image_type == IMAGETYPE_JPEG ) {\r\n         imagejpeg($this->image);\r\n      } elseif( $image_type == IMAGETYPE_GIF ) {\r\n \r\n         imagegif($this->image);\r\n      } elseif( $image_type == IMAGETYPE_PNG ) {\r\n \r\n         imagepng($this->image);\r\n      }\r\n   }\r\n   function getWidth() {\r\n \r\n      return imagesx($this->image);\r\n   }\r\n   function getHeight() {\r\n \r\n      return imagesy($this->image);\r\n   }\r\n   function resizeToHeight($height) {\r\n \r\n      $ratio = $height / $this->getHeight();\r\n      $width = $this->getWidth() * $ratio;\r\n      $this->resize($width,$height);\r\n   }\r\n \r\n   function resizeToWidth($width) {\r\n      $ratio = $width / $this->getWidth();\r\n      $height = $this->getheight() * $ratio;\r\n      $this->resize($width,$height);\r\n   }\r\n \r\n   function scale($scale) {\r\n      $width = $this->getWidth() * $scale/100;\r\n      $height = $this->getheight() * $scale/100;\r\n      $this->resize($width,$height);\r\n   }\r\n  \r\n   function resize($width,$height) {\r\n      $new_image = imagecreatetruecolor($width, $height);\r\n      imagecopyresampled($new_image, $this->image, 0, 0, 0, 0, $width, $height, $this->getWidth(), $this->getHeight());\r\n      $this->image = $new_image;\r\n   }  \r\n   \r\n  \r\n   \r\n  /* FOR GIF OR PNG\r\n  function resize($width,$height) {\r\n	$new_image = imagecreatetruecolor($width, $height);\r\n	if( $this->image_type == IMAGETYPE_GIF || $this->image_type == IMAGETYPE_PNG ) {\r\n		$current_transparent = imagecolortransparent($this->image);\r\n		if($current_transparent != -1) {\r\n			$transparent_color = imagecolorsforindex($this->image, $current_transparent);\r\n			$current_transparent = imagecolorallocate($new_image, $transparent_color[''red''], $transparent_color[''green''], $transparent_color[''blue'']);\r\n			imagefill($new_image, 0, 0, $current_transparent);\r\n			imagecolortransparent($new_image, $current_transparent);\r\n		} elseif( $this->image_type == IMAGETYPE_PNG) {\r\n			imagealphablending($new_image, false);\r\n			$color = imagecolorallocatealpha($new_image, 0, 0, 0, 127);\r\n			imagefill($new_image, 0, 0, $color);\r\n			imagesavealpha($new_image, true);\r\n		}\r\n	}\r\n	imagecopyresampled($new_image, $this->image, 0, 0, 0, 0, $width, $height, $this->getWidth(), $this->getHeight());\r\n	$this->image = $new_image;	\r\n}  \r\n   * \r\n   */\r\n \r\n}\r\n/*\r\n * EXAMPLE USAGE:\r\n * http://www.white-hat-web-design.co.uk/blog/resizing-images-with-php/\r\n * \r\n * \r\n * more there...\r\n * \r\n * If you want to resize to a specifed width but keep the dimensions ratio the same then the script can work out the required height for you, just use the resizeToWidth function.\r\n \r\n<?php\r\n   include(''SimpleImage.php'');\r\n   $image = new SimpleImage();\r\n   $image->load(''picture.jpg'');\r\n   $image->resizeToWidth(250);\r\n   $image->save(''picture2.jpg'');\r\n?>\r\nYou may wish to scale an image to a specified percentage like the following which will resize the image to 50% of its original width and height\r\n \r\n<?php\r\n   include(''SimpleImage.php'');\r\n   $image = new SimpleImage();\r\n   $image->load(''picture.jpg'');\r\n   $image->scale(50);\r\n   $image->save(''picture2.jpg'');\r\n?>\r\nYou c\r\n * \r\n * \r\n */\r\n?>', 'resize and compress images photos', 'php'),
(257, '\r\n		\r\n		$(document).on("pagebeforecreate", function(event) {\r\n			alert("PS: pagebeforecreate event fired!");\r\n		});\r\n		$(document).on("pagecreate", function(event) {\r\n			alert("PS: pagecreate event fired!");\r\n		});\r\n		$(document).on(''pageinit'', function() {\r\n			alert("PS: page init");\r\n		});\r\n\r\n		$(document).on("pagecontainerload", function(event, data) {\r\n			alert("PS: pageload event fired!\\nURL: " + data.url);\r\n		});\r\n		$(document).on("pagecontainerloadfailed", function(event, data) {\r\n			alert("PS: Sorry, requested page does not exist.");\r\n		});\r\n		$(document).on("pagebeforeshow", "#pagetwo", function() {// When entering pagetwo\r\n			alert("PS: pagetwo is about to be shown");\r\n		});\r\n		$(document).on("pageshow", "#pagetwo", function() {// When entering pagetwo\r\n			alert("PS: pagetwo is now shown");\r\n		});\r\n		$(document).on("pagebeforehide", "#pagetwo", function() {// When leaving pagetwo\r\n			alert("PS: pagetwo is about to be hidden");\r\n		});\r\n		$(document).on("pagehide", "#pagetwo", function() {// When leaving pagetwo\r\n			alert("PS: pagetwo is now hidden");\r\n		});', 'jquery mobile events', 'javascript'),
(258, 'function updateclock() {\r\n\r\n    function pad(n) {\r\n         return (n < 10) ? ''0'' + n : n;\r\n    }\r\n\r\n    var time = new Date();\r\n    var hours = time.getHours();\r\n    var minutes = time.getMinutes();\r\n\r\n    if (hours > 12) {\r\n        hours -= 12;\r\n    } else if (hours === 0) {\r\n        hours = 12;\r\n    }\r\n\r\n    var todisplay = pad(hours) + '':'' + pad(minutes);\r\n    document.getElementById("clock").innerHTML = todisplay;\r\n}', 'time 12 hour versus 24 hour and military time', 'javascript');
