(259, '\r\nfunction abbrState(input, to){\r\n    //credit: Caleb Grove: https://gist.github.com/CalebGrove/c285a9510948b633aa47\r\n    // USAGE:\r\n\r\n    // abbrState(''ny'', ''name''); // --> ''New York''\r\n localStorage.setItem("abbrState(input,to)", "inside abbrState");\r\n    // abbrState(''New York'', ''abbr'');  // --> ''NY''\r\n  localStorage.setItem("input", input);  \r\n  localStorage.setItem("to", to); \r\n    var states = [\r\n        [''Arizona'', ''AZ''],\r\n        [''Alabama'', ''AL''],\r\n        [''Alaska'', ''AK''],\r\n        [''Arizona'', ''AZ''],\r\n        [''Arkansas'', ''AR''],\r\n        [''California'', ''CA''],\r\n        [''Colorado'', ''CO''],\r\n        [''Connecticut'', ''CT''],\r\n        [''Delaware'', ''DE''],\r\n        [''Florida'', ''FL''],\r\n        [''Georgia'', ''GA''],\r\n        [''Hawaii'', ''HI''],\r\n        [''Idaho'', ''ID''],\r\n        [''Illinois'', ''IL''],\r\n        [''Indiana'', ''IN''],\r\n        [''Iowa'', ''IA''],\r\n        [''Kansas'', ''KS''],\r\n        [''Kentucky'', ''KY''],\r\n        [''Kentucky'', ''KY''],\r\n        [''Louisiana'', ''LA''],\r\n        [''Maine'', ''ME''],\r\n        [''Maryland'', ''MD''],\r\n        [''Massachusetts'', ''MA''],\r\n        [''Michigan'', ''MI''],\r\n        [''Minnesota'', ''MN''],\r\n        [''Mississippi'', ''MS''],\r\n        [''Missouri'', ''MO''],\r\n        [''Montana'', ''MT''],\r\n        [''Nebraska'', ''NE''],\r\n        [''Nevada'', ''NV''],\r\n        [''New Hampshire'', ''NH''],\r\n        [''New Jersey'', ''NJ''],\r\n        [''New Mexico'', ''NM''],\r\n        [''New York'', ''NY''],\r\n        [''North Carolina'', ''NC''],\r\n        [''North Dakota'', ''ND''],\r\n        [''Ohio'', ''OH''],\r\n        [''Oklahoma'', ''OK''],\r\n        [''Oregon'', ''OR''],\r\n        [''Pennsylvania'', ''PA''],\r\n        [''Rhode Island'', ''RI''],\r\n        [''South Carolina'', ''SC''],\r\n        [''South Dakota'', ''SD''],\r\n        [''Tennessee'', ''TN'