(293, '<?php\r\n\r\nclass Model {\r\n	private $database;\r\n	private $host;\r\n	private $username;\r\n	private $password;\r\n	function __construct() {\r\n		$this -> database = ''ameriana_dev'';\r\n		$this -> host = ''localhost'';\r\n		$this -> username = ''root'';\r\n		$this -> password = '''';\r\n	}\r\n\r\n	public function getDatabase() {\r\n		return $this -> database;\r\n	}\r\n\r\n	public function setDatabase($database) {\r\n		$this -> database = $database;\r\n	}\r\n\r\n	public function connect() {\r\n		//mysql_connect($this -> host, $this -> username, $this -> password);\r\n		$con = mysqli_connect($this -> host, $this -> username, $this -> password);\r\n		return $con;\r\n	}\r\n\r\n	public function exe_sql($con, $sql, $return = "") {\r\n		$res = mysqli_query($con, $sql);\r\n		\r\n		if (mysqli_connect_errno())\r\n		  {\r\n		  echo "Failed to connect to MySQL: " . mysqli_connect_error();\r\n		  }\r\n		  \r\n		if (gettype($res) == "boolean") {\r\n			//INSERT creates a boolean for $res\r\n			//return last id\r\n			return mysqli_insert_id($con);\r\n		}\r\n		// L E F T   O F F   H E R E   switching over to mysqli\r\n		$data = "";\r\n		while ($row = mysqli_fetch_assoc($res)) {\r\n			$data[] = $row;\r\n		}\r\n		// Free result set\r\n		mysqli_free_result($res);\r\n		mysqli_close($con);\r\n		\r\n		if ($return == "json") {\r\n			return json_encode($data);\r\n		} else {\r\n			return $data;\r\n		}\r\n	}\r\n\r\n	 \r\n\r\n}\r\n?>', 'mysqli base model', 'php'),
(294, '<?php\r\ninclude_once (''Model.model.SQLi.php'');\r\n\r\nclass User extends Model {\r\n	public function __construct() {\r\n		parent::__construct();\r\n	}\r\n	public function model_connect() {\r\n		return parent::connect();\r\n	}\r\n	public function readUsers($return = "") {\r\n		$con = $this -> model_connect();\r\n		$sql = " SELECT * FROM " . $this -> getDatabase() . ".user ;";\r\n		//WHERE username_email = ''bayon@gocodigo.com'' AND password = ''pass''\r\n		$data = $this -> exe_sql($con, $sql, $return);\r\n		return $data;\r\n	}\r\n	function login($username, $password, $return = "") {\r\n		$con = $this -> model_connect();\r\n		$sql = "SELECT * FROM " . $this -> getDatabase() . ".user WHERE username_email = ''" . $username . "'' AND password = ''" . $password . "'';";\r\n		$data = $this -> exe_sql($con, $sql, $return);\r\n		return $data;\r\n	}\r\n	function createNewUser($user_entity,$return = "json") {\r\n		$con = $this -> model_connect();\r\n		$sql = "INSERT INTO " . $this -> getDatabase() . ".user \r\n		(id, authorization, username_email, password, company_id, location_id, phone, address, city, state, zipcode) \r\n 		VALUES \r\n 		(NULL, ''" . $user_entity->get_authorization() . "'', ''" . $user_entity->get_username_email() . "'', ''" . $user_entity->get_password() . "'', ''1'', ''1'', ''" . $user_entity->get_phone() . "'', ''" . $user_entity->get_address() . "'', ''" . $user_entity->get_city() . "'', ''" . $user_entity->get_state() . "'', ''" . $user_entity->get_zipcode() . "'');";\r\n		$data = $this -> exe_sql($con,$sql, $return);\r\n		 // in the case of an insert , the return data will be the "last id inserted".\r\n		echo($data);\r\n	}\r\n	\r\n	function updateUser($user_entity,$return = "json") {\r\n		$con = $this -> model_connect();\r\n		$sql = "UPDATE " . $this -> getDatabase() . ".user set authorization = ''".$user_entity->get_authorization()."'', username_email = ''".$user_entity->get_username_email()."'' , phone = ''".$user_entity->get_phone()."''  ,