(342, '<!DOCTYPE html>\r\n<html>\r\n	<head>\r\n		<meta charset="utf-8">\r\n		<script type=''text/javascript'' src=''../js/jquery-1.11.2.min.js''></script>\r\n	</head>\r\n	<body onload="getData();">\r\n		<p>\r\n			getData(); AND populateSelectIdWithArrayOfObjects();\r\n		</p>\r\n		<!-- select element -->\r\n		<select id="select1"></select>\r\n		<script>\r\n			function getData() {\r\n				//object\r\n				var monkeys = {};\r\n				//row 1\r\n				monkey1 = {};\r\n				monkey1.name = "Gorilla";\r\n				monkey1.age = "101";\r\n				monkeyJSON_1 = JSON.stringify(monkey1);\r\n				//row2\r\n				monkey2 = {};\r\n				monkey2.name = "Chimp";\r\n				monkey2.age = "55";\r\n				monkeyJSON_2 = JSON.stringify(monkey2);\r\n				//add rows to object\r\n				monkeys.monkey1 = monkey1;\r\n				monkeys.monkey2 = monkey2;\r\n\r\n				populateSelectIdWithArrayOfObjects(monkeys, ''select1'', myCallback);\r\n			}\r\n\r\n			function myCallback(data) {\r\n				console.log(data);\r\n				alert(data.name + "--" + data.age);\r\n			}\r\n\r\n			//function to populate js object with data and build select options.\r\n			function populateSelectIdWithArrayOfObjects(obj, selectId, callback) {\r\n				//ASSUMPTION: \r\n					//1) we assume your object has a "name" property.\r\n					//2) you want to select the entire object.\r\n				\r\n				//build select element\r\n				html = "";\r\n				for (x in obj) {\r\n					json_data = JSON.stringify(obj[x]);\r\n					html += "<option data-value=''" + json_data + "''>" + obj[x].name + "</option>";\r\n				}\r\n				$("#" + selectId).html(html);\r\n				$("#" + selectId).change(function() {\r\n					//callback to return selected option data\r\n					callback($(this).find(":selected").data("value"));\r\n				});\r\n			}\r\n		</script>\r\n	</body>\r\n</html>\r\n', 'populate select element with array of js javascript objects', 'javascript'),
