(348, '<?php\r\n//CONFIG\r\n/*=========== Database Configuraiton ==========*/\r\n \r\n/*=========== Website Configuration ==========*/\r\n define(''SITE_ROOT'',''StX_Accolades'');\r\n/*============================================================*/\r\n/*-----	AUTOMATED CONFIGS	 ---------------------------------*/\r\n/*============================================================*/\r\n\r\n$domain = $_SERVER[''HTTP_HOST''];\r\n$hostname = $domain . "/" . SITE_ROOT . "/";\r\ndefine(''BASE_URL'', ''http://'' . $hostname . '''');\r\ndefine(''HOSTNAME'', $hostname);\r\ndefine(''DOMAIN'', $domain);\r\n\r\ndefine(''DEBUG'', 1); // 1 or 0 then see admin page.\r\ndefine(''DIR_SEPARATOR'',''/''); // iis "//", apache "/"\r\ndefine(''BASE_PATH'',str_replace(''\\\\'', DIR_SEPARATOR, dirname(__FILE__)));\r\n//NOTE: THE DIRECTORY SEPARATOR HAS TO  BE DOUBLED // for windows.\r\nif(DEBUG == 1){\r\n	error_reporting(E_ALL ^ E_NOTICE);\r\n	//echo(''<br><br><br>config:<hr></br>'');\r\n	//echo("<br>BASE_PATH:".BASE_PATH);\r\n	//echo("<br>BASE_URL:".BASE_URL);	 \r\n}\r\nif(DEBUG == 0){\r\n	error_reporting(0);\r\n	@ini_set(''display_errors'', 0);\r\n	error_reporting(E_ERROR);\r\n}\r\n?>\r\n<?php\r\n//CONSTANTS\r\ndefine(''APP_TITLE'', ''Saint X'');\r\n\r\ndefine("DATABASE","saintx");\r\ndefine("HOST","localhost");\r\ndefine("USERNAME","root");\r\ndefine("PASSWORD","");//MyNewPass\r\n//o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=\r\ndefine("IMAGE_URL",BASE_URL.DIR_SEPARATOR."img");\r\ndefine("IMAGE_PATH",BASE_PATH.DIR_SEPARATOR."img");\r\n//STRINGS\r\n \r\n//o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=o=\r\n\r\n?>\r\n<?php\r\n//DATA MODEL\r\nclass Model {\r\n	private $database;\r\n	private $host;\r\n	private $username;\r\n	private $password;\r\n	function __construct() {\r\n		$this -> database = DATABASE;\r\n		$this -> host = HOST;\r\n		$this -> username = USERNAME;\r\n		$this -> password = PASSWORD;\r\n	}\r\n	public function getDatabase() {\r\n		return $this -> database;\r\n	}\r\n	public function setDatabase($database) {\r\n		$this -> database = $database;\r\n	}\r\n	public function connect() {\r\n		$con = mysqli_connect($this -> host, $this -> username, $this -> password);\r\n		return $con;\r\n	}\r\n	public function exe_sql($con, $sql, $return = "") {\r\n		$res = mysqli_query($con, $sql);\r\n		if (mysqli_connect_errno())\r\n		  {\r\n		  echo "Failed to connect to MySQL: " . mysqli_connect_error();\r\n		  }\r\n		if (gettype($res) == "boolean") {\r\n			//INSERT creates a boolean for $res\r\n			//return last id\r\n			return mysqli_insert_id($con);\r\n		}\r\n		$data = "";\r\n		while ($row = mysqli_fetch_assoc($res)) {\r\n			$data[] = $row;\r\n		}\r\n		mysqli_free_result($res);\r\n		mysqli_close($con);\r\n		if ($return == "json") {\r\n			return json_encode($data);\r\n		} else {\r\n			return $data;\r\n		}\r\n	}\r\n}\r\n?>\r\n<?php\r\nfunction multipleInsert($arrayOfRows){\r\n	die(''DONT RUN THIS SCRIPT!'');\r\n	 \r\n	//INSERT INTO tbl_name (a,b,c) VALUES(1,2,3),(4,5,6),(7,8,9);\r\n	$m = new Model();\r\n	$db = "saintx";\r\n	$m->setDatabase($db);\r\n	$con = $m->connect();\r\n	$tbl_name = "bestlift";\r\n	$fields = "id, category,group_id,year, img ";\r\n	$sql = "INSERT INTO  ".$db.".".$tbl_name." (".$fields.") VALUES";\r\n	// $arrayOfRows: GOT THIS VIA read.dir.util\r\n	/*\r\n	 * //example of expected "$arrayOfRows"\r\n	$arrayOfRows = array(\r\n	"(NULL,''volleyball'',''2'',''1993'',''none.jpg'')",\r\n	"(NULL,''volleyball'',''2'',''2014'',''none.jpg'')",\r\n	"(NULL,''volleyball'',''2'',''2015'',''none.jpg'')"\r\n	);\r\n	 */\r\n	\r\n	 for($i = 0; $i < count($arrayOfRows) ; $i++){\r\n	 	if($i == 0){\r\n	 		$sql .= $arrayOfRows[$i]; \r\n	 	}else{\r\n	 		$sql .= ",".$arrayOfRows[$i]; \r\n	 	}\r\n	 }\r\n	$sql .= ";";  \r\n	\r\n	echo($sql);\r\n	die(''DONT RUN THIS SCRIPT!'');\r\n	$data = $m ->exe_sql($con, $sql );\r\n	echo("<pre>");print_r($data);echo("</pre>");\r\n}\r\n?>', 'data utility script: insert multiple rows', 'php'),
