(377, '<?php\r\n\r\nclass Paginator {\r\n\r\n	private $_conn;\r\n	private $_limit;\r\n	private $_page;\r\n	private $_query;\r\n	private $_total;\r\n\r\n	public function __construct($conn, $query) {\r\n\r\n		$this -> _conn = $conn;\r\n		$this -> _query = $query;\r\n\r\n		$rs = $this -> _conn -> query($this -> _query);\r\n		$this -> _total = $rs -> num_rows;\r\n\r\n	}\r\n\r\n	public function getData($limit = 10, $page = 1) {\r\n\r\n		$this -> _limit = $limit;\r\n		$this -> _page = $page;\r\n\r\n		if ($this -> _limit == ''all'') {\r\n			$query = $this -> _query;\r\n		} else {\r\n			$query = $this -> _query . " LIMIT " . (($this -> _page - 1) * $this -> _limit) . ", $this->_limit";\r\n		}\r\n		$rs = $this -> _conn -> query($query);\r\n\r\n		while ($row = $rs -> fetch_assoc()) {\r\n			$results[] = $row;\r\n		}\r\n\r\n		$result = new stdClass();\r\n		$result -> page = $this -> _page;\r\n		$result -> limit = $this -> _limit;\r\n		$result -> total = $this -> _total;\r\n		$result -> data = $results;\r\n\r\n		return $result;\r\n	}\r\n\r\n	public function createLinks($links, $list_class) {\r\n		if ($this -> _limit == ''all'') {\r\n			return '''';\r\n		}\r\n\r\n		$last = ceil($this -> _total / $this -> _limit);\r\n\r\n		$start = (($this -> _page - $links) > 0) ? $this -> _page - $links : 1;\r\n		$end = (($this -> _page + $links) < $last) ? $this -> _page + $links : $last;\r\n\r\n		$html = ''<ul class="'' . $list_class . ''">'';\r\n\r\n		$class = ($this -> _page == 1) ? "disabled" : "";\r\n		$html .= ''<li class="'' . $class . ''"><a href="?limit='' . $this -> _limit . ''&page='' . ($this -> _page - 1) . ''">Â«</a></li>'';\r\n\r\n		if ($start > 1) {\r\n			$html .= ''<li><a href="?limit='' . $this -> _limit . ''&page=1">1</a></li>'';\r\n			$html .= ''<li class="disabled"><span>...</span></li>'';\r\n		}\r\n\r\n		for ($i = $start; $i <= $end; $i++) {\r\n			$class = ($this -> _page == $i) ? "active" : "";\r\n			$html .= ''<li class="'' . $class . ''"><a href="?limit='' . $this -> _limit . ''&page='' . $i . ''">'' . $i . ''</a></li>'';\r\n		}\r\n\r\n		if ($end < $last) {\r\n			$html .= ''<li class="disabled"><span>...</span></li>'';\r\n			$html .= ''<li><a href="?limit='' . $this -> _limit . ''&page='' . $last . ''">'' . $last . ''</a></li>'';\r\n		}\r\n\r\n		$class = ($this -> _page == $last) ? "disabled" : "";\r\n		$html .= ''<li class="'' . $class . ''"><a href="?limit='' . $this -> _limit . ''&page='' . ($this -> _page + 1) . ''">Â»</a></li>'';\r\n\r\n		$html .= ''</ul>'';\r\n\r\n		return $html;\r\n	}\r\n\r\n}\r\n////////////////////////////////////////////////////\r\n//EXAMPLE CLIENT//////////////////////////////////\r\n<?php\r\n  require_once ''Paginator.class.php'';\r\n	 \r\n	$conn       = new mysqli( ''127.0.0.1'', ''root'', '''', ''ameriana_dev'' );\r\n    $limit      = ( isset( $_GET[''limit''] ) ) ? $_GET[''limit''] : 25;\r\n    $page       = ( isset( $_GET[''page''] ) ) ? $_GET[''page''] : 1;\r\n    $links      = ( isset( $_GET[''links''] ) ) ? $_GET[''links''] : 7;\r\n	 \r\n	$query      = "SELECT entry.id,entry.author,entry.message,entry.date FROM entry ";\r\n    $Paginator  = new Paginator( $conn, $query );\r\n    $results    = $Paginator->getData( $page, $limit );\r\n\r\n?>\r\n\r\n<!DOCTYPE html>\r\n    <head>\r\n        <title>PHP Pagination</title>\r\n        <link rel="stylesheet" href="bootstrap.min.css">\r\n        <script src="jquery-1.9.1.js"></script>\r\n        <script>\r\n        	var data = [];\r\n        </script>\r\n    </head>\r\n    <style>\r\n    	.amer_table tr:hover{\r\n    		 color:#bbb !important;\r\n    	}\r\n    </style>\r\n    <body>\r\n        <div class="container">\r\n                <div class="col-md-10 col-md-offset-1">\r\n                <h1>Ameriana</h1>\r\n                <table class="table table-striped table-condensed table-bordered table-rounded">\r\n                	  \r\n                	<thead>\r\n                        <tr>\r\n	                        <th>Author</th>\r\n	                        <th width="20%">Message</th>\r\n	                        <th width="20%">Date</th>\r\n                        </tr>\r\n                    </thead>\r\n                    <tbody class=''amer_table''>\r\n                    	<?php for( $i = 0; $i < count( $results->data ); $i++ ) : ?>\r\n                    		\r\n						        <tr   id=''<?php echo $i; ?>'' class=''data_row''    >\r\n						        	\r\n						                <td><?php echo $results->data[$i][''author'']; ?></td>\r\n						                <td><?php echo $results->data[$i][''message'']; ?></td>\r\n						                <td><?php echo $results->data[$i][''date'']; ?></td>\r\n						                 \r\n						        </tr>\r\n						 <?php \r\n						 $json = json_encode($results->data[$i]);\r\n						 echo("\r\n						 <script>\r\n						 var row_".$i." = ".$json.";\r\n						 //console.log(row_".$results->data[$i][''id''].");\r\n						 //console.log(row_".$i.");\r\n						 data.push(row_".$i.");\r\n						 </script>\r\n						 ");\r\n						 ?>      \r\n						<?php endfor; ?>\r\n                    	\r\n                    </tbody>\r\n                        \r\n                </table>\r\n                <?php echo $Paginator->createLinks( $links, ''pagination pagination-sm'' ); ?> \r\n                </div>\r\n           \r\n        </div>\r\n        <div class="container">\r\n         <div class="details_container col-md-10 col-md-offset-1">\r\n            	<div id="details_header">Entry:</div>\r\n        		<div id="details"></div>\r\n        	</div>\r\n        </div>\r\n        <script>\r\n        $(document).ready(function(){\r\n        	$(''.data_row'').click(function (){\r\n        		//alert(this.id);\r\n        		var i = this.id;\r\n        		//console.log(data);\r\n        		console.log(data[i]);\r\n        		var html =  "<label>ID:</label><span>"+data[i][''id'']+"</span></br>";\r\n        			html += "<label>Author:</label><span>"+data[i][''author'']+"</span></br>";\r\n        			html += "<label>Mess