--------\r\n			function loadScript(url, callback) {\r\n				var head = document.getElementsByTagName(''head'')[0];\r\n				var script = document.createElement(''script'');\r\n				script.type = ''text/javascript'';\r\n				script.src = url;\r\n				script.onreadystatechange = callback(url);\r\n				// cross browser compatibility.\r\n				script.onload = callback(url);\r\n				head.appendChild(script);\r\n			}\r\n\r\n			function loadScript_callback(data) {\r\n				console.log(''loadScript_callback'');\r\n			}\r\n			\r\n			function callback_animationObject(){\r\n				console.log(''callback_animationObject'');\r\n			}\r\n			function animateObject(obj) {\r\n			//id, de., x, y, w, h, a, dur, e, dx, dy, dw, dh, da\r\n			$(''#'' + obj.id).delay(obj.del).animate({\r\n				left : obj.x,\r\n				top : obj.y,\r\n				width : obj.w,\r\n				height : obj.h,\r\n				opacity : obj.a\r\n			});\r\n			var options = {\r\n				duration : obj.dur,\r\n				easing : obj.e\r\n			};\r\n			$(''#'' + obj.id).animate({\r\n				left : obj.dx,\r\n				top : obj.dy,\r\n				width : obj.dw,\r\n				height : obj.dh,\r\n				opacity : obj.da\r\n\r\n			}, $.extend(true, {}, options, {\r\n				complete : function() {\r\n					console.log(''callback complete'');\r\n					callback_animationObject(obj);\r\n				}\r\n			}));\r\n		}\r\n		\r\n		function load_image_callback(){\r\n			console.log(''load_image_callback'');\r\n			if(fader.innerHTML != ""){\r\n				console.log(''ready'');\r\n			}\r\n		}\r\n		function load_image(callback) {\r\n			imageUrl = "./img/zeroBackground_50.png";\r\n			$(''body'').css(''background-image'', ''url('' + imageUrl + '')'');\r\n			fader.innerHTML = "<img src=''./img/GlobalDevDiagram.png'' />"; \r\n			mover.innerHTML = "<img src=''./img/10.jpg'' />"; \r\n			callback;\r\n		}\r\n		</script>\r\n	</body>\r\n</html>', 'load_x-1.1.php with iFrame included.', 'javascript'),
(397, '/*As "there are tens of thousands of cells in the page" binding the click-event to every single cell will cause a terrible performance problem. There''s a better way to do this, that is binding a click event to the body & then finding out if the cell element was the target of the click. Like this:*/\r\n\r\n$(''body'').click(function(e){\r\n       var Elem = e.target;\r\n       if (Elem.nodeName==''td''){\r\n           //.... your business goes here....\r\n           // remember to replace $(this) with $(Elem)\r\n       }\r\n})', 'avoid binding click events to too many objects', 'javascript'),
(398, ' <?php\r\n \r\n/**\r\n *  Example API call\r\n *  GET profile information\r\n */\r\n\r\n// the ID of the profile\r\n\r\n$profileID = 2;\r\n\r\n// the token\r\n\r\n$token = ''your token here'';\r\n\r\n// set up the curl resource\r\n\r\n$ch = curl_init();\r\n//curl_setopt($ch, CURLOPT_URL, "https://api.copernica.com/profile/$profileID?access_token=$token");\r\ncurl_setopt($ch, CURLOPT_URL, "http://10.110.210.93:8888/00_HTML/post_response.php?access_token=$token");\r\n\r\n\r\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\r\ncurl_setopt($ch, CURLOPT_HEADER, 1);\r\n\r\n// execute the request\r\n\r\n$output = curl_exec($ch);\r\n\r\n// output the profile information - includes the header\r\n\r\necho($output) . PHP_EOL;\r\n\r\n// close curl resource to free up system resources\r\n\r\ncurl_close($ch);\r\n?>', 'CURL GET REQUEST for API', 'php'),
(399, '<?php\r\n/**\r\n *  Example API call\r\n *  Create a new profile in a database\r\n * http://www.copernica.com/en/support/rest/example-get-post-put-and-delete-requests\r\n */\r\n\r\n// the databaseID\r\n\r\n$databaseID = 1;\r\n\r\n$data = array (\r\n	"Company" => "EasyREST4u",\r\n	"City" => "Hendrik Ido Ambacht"\r\n	);\r\n\r\n// json encode data\r\n\r\n$data_string = json_encode($data); \r\n\r\n// the token\r\n\r\n$token = ''monkey345345345'';\r\n\r\n// set up the curl resource\r\n$ch = curl_init();\r\n//curl_setopt($ch, CURLOPT_URL, "https://api.copernica.com/database/$databaseID/profiles?access_token=$token");\r\ncurl_setopt($ch, CURLOPT_URL, "http://10.110.210.93:8888/00_HTML/post_response.php?access_token=$token&data=$data_string");\r\n\r\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\r\ncurl_setopt($ch, CURLOPT_CUSTOMREQUEST, "POST");\r\ncurl_setopt($ch, CURLOPT_POST, true);\r\ncurl_setopt($ch, CURLOPT_POSTFIELDS, $data_string);\r\ncurl_setopt($ch, CURLOPT_HEADER, true);\r\ncurl_setopt($ch, CURLOPT_HTTPHEADER, array(                                                                          \r\n    ''Content-Type: application/json'',                                                                                \r\n    ''Content-Length: '' . strlen($data_string)                                                                       \r\n));       \r\n\r\n// execute the request\r\n\r\n$output = curl_exec($ch);\r\n\r\n// output the profile information - includes the header\r\n\r\necho($output) . PHP_EOL;\r\n\r\n// close curl resource to free up system resources\r\ncurl_close($ch);', 'CURL POST REQUEST for API ', 'php'),
(400, '<textarea id=''Editor''></textarea>\r\n		<button onclick=''ShowSelection()''>selection</button>\r\n		<script>\r\n			function ShowSelection() {\r\n				var textComponent = document.getElementById(''Editor'');\r\n				var selectedText;\r\n				// IE version\r\n				if (document.selection != undefined) {\r\n					textComponent.focus();\r\n					var sel = document.selection.createRange();\r\n					selectedText = sel.text;\r\n				}\r\n				// Mozilla version\r\n				else if (textComponent.selectionStart != undefined) {\r\n					var startPos = textComponent.selectionStart;\r\n					var endPos = textComponent.selectionEnd;\r\n					selectedText = textComponent.value.substring(startPos, endPos)\r\n				}\r\n				alert("You selected: " + selectedText);\r\n			}\r\n		</script>', 'show selected text in textarea', 'javascript'),
