(404, ' function AnimObj(id,json) {\r\n	this.id = id;\r\n	this.json = json;\r\n	this.type = ''layer'';\r\n}\r\nAnimObj.prototype.id = '''';\r\nAnimObj.prototype.owners_id = '''';\r\nAnimObj.prototype.type = ''layer'';\r\nAnimObj.prototype.name = ''object default name'';\r\nAnimObj.prototype.index = '''';\r\nAnimObj.prototype.w = 400;\r\nAnimObj.prototype.h = 300;\r\nAnimObj.prototype.x = 0;\r\nAnimObj.prototype.y = 0;\r\nAnimObj.prototype.a = 1;\r\nAnimObj.prototype.dw = 400;\r\nAnimObj.prototype.dh = 300;\r\nAnimObj.prototype.dx = 0;\r\nAnimObj.prototype.dy = 0;\r\nAnimObj.prototype.dx2 = 0;\r\nAnimObj.prototype.dy2 = 0;\r\nAnimObj.prototype.da = 0;\r\nAnimObj.prototype.dur = 1000;\r\nAnimObj.prototype.e = ''linear'';\r\nAnimObj.prototype.z = 1;\r\nAnimObj.prototype.p = ''absolute'';\r\nAnimObj.prototype.disp = ''block'';\r\nAnimObj.prototype.del = 0;\r\nAnimObj.prototype.img = ''img/imageX.jpg'';\r\nAnimObj.prototype.txt = ''default text'';\r\nAnimObj.prototype.text_class = ''animText_1'';\r\nAnimObj.prototype.font_family = ''courier'';\r\nAnimObj.prototype.font_size = 20;\r\nAnimObj.prototype.font_weight = ''normal'';\r\nAnimObj.prototype.line_height = 1;\r\nAnimObj.prototype.color = ''#FFFFFF'';\r\nAnimObj.prototype.inner = 0;\r\nAnimObj.prototype.innerTxt = 0;\r\nAnimObj.prototype.stockAnimation = '''';\r\n//Methods\r\nAnimObj.prototype.initWithJSON = function(obj) {\r\n	console.log(''BEFORE fn initWithJSON'');\r\n	obj.owners_id = obj.json.owners_id;\r\n	obj.name = obj.json.name;\r\n	obj.z =obj.json.z;\r\n	obj.dur = obj.json.dur;\r\n	obj.x = obj.json.x+"%";\r\n	obj.y =obj.json.y+"%";\r\n	obj.w = obj.json.w+"%";\r\n	obj.h = obj.json.h+"%";\r\n	obj.a = obj.json.a;\r\n	obj.e = obj.json.e;\r\n	obj.del = obj.json.del;\r\n	obj.dx = obj.json.dx+"%";\r\n	obj.dy = obj.json.dy+"%";\r\n	obj.dx2 = obj.json.dx2+"%";\r\n	obj.dy2 = obj.json.dy2+"%";\r\n	obj.dw = obj.json.dw+"%";\r\n	obj.dh = obj.json.dh+"%";\r\n	obj.da = obj.json.da;\r\n	obj.img = obj.json.img;\r\n	obj.txt = 