	}\r\n	}));\r\n};\r\nAnimObj.prototype.hide = function(obj) {\r\n	console.log(''fn hide'');\r\n 	$(''#'' + obj.id).delay(obj.del).animate({opacity: obj.a});\r\n	var options = {\r\n		duration : 0,\r\n		easing : obj.e\r\n	};\r\n	$(''#'' + obj.id).animate({\r\n		opacity : 0\r\n	}, $.extend(true, {}, options, {\r\n		complete : function() {\r\n			animationCallback(obj);\r\n		}\r\n	}));\r\n};\r\n///   FADE COMBINATIONS\r\nAnimObj.prototype.fade_slide = function(obj) {\r\n 	$(''#'' + obj.id).delay(obj.del).animate({opacity: obj.a});\r\n	var options = {\r\n		duration : obj.dur,\r\n		easing : obj.e\r\n	};\r\n	$(''#'' + this.id).animate({\r\n		opacity : obj.da\r\n	}, options).animate({\r\n		left : obj.dx,\r\n		top : obj.dy\r\n	}, $.extend(true, {}, options, {\r\n		complete : function() {\r\n			animationCallback(obj);\r\n		}\r\n	}));\r\n};\r\n////////////   EXPAND/CONTRACT\r\nAnimObj.prototype.expand = function(obj) {\r\n	console.log(''fn expand'');\r\n	//CRUCIAL OVERRIDE FOR EXPANDS CORRECT PERSPECTIVE\r\n	dx = "-=" + ((obj.dw - obj.w) / 2) + "";\r\n	dy = "-=" + ((obj.dh - obj.h) / 2) + "";\r\n	//expand from the center\r\n 	$(''#'' + obj.id).delay(obj.del).animate({opacity: obj.a});\r\n	var options = {\r\n		duration : obj.dur,\r\n		easing : obj.e\r\n	};\r\n	$(''#'' + obj.id)\r\n	.animate({\r\n		width : obj.dw,\r\n		height : obj.dh,\r\n		left : dx,\r\n		top : dy\r\n	}, $.extend(true, {}, options, {\r\n		complete : function() {\r\n			animationCallback(obj);\r\n		}\r\n	}\r\n	));\r\n};\r\n////////////////  EXPAND COMBINATIONS\r\nAnimObj.prototype.expand_fade = function(obj) {\r\n	console.log(''fn expand_fade'');\r\n	//CRUCIAL OVERRIDE FOR EXPANDS CORRECT PERSPECTIVE\r\n	//PERCENTAGE\r\n	//dx = "-=" + ((parseInt(obj.dw) - parseInt(obj.w) / 2) + "";\r\n	//dy = "-=" + ((parseInt(obj.dh) - parseInt(obj.h) / 2) + "";\r\n	//expand from the center\r\n 	$(''#'' + obj.id).delay(obj.del).animate({opacity: obj.a});\r\n	var options = {\r\n		duration : obj.dur,\r\n		easing : obj.e\r\n	};\r\n	