(424, '<html>\r\n	<head>\r\n		<script src="jquery-1.9.1.js" ></script>\r\n		<script src="jquery-ui.js" ></script>\r\n		<style>\r\n		</style>\r\n	</head>\r\n	<body>\r\n		<table width="100%">\r\n			<tr>\r\n				<th width="25%">top</th><th width="25%">left</th><th width="25%">right</th><th width="25%">bottom</th>\r\n			</tr>\r\n			<tr>\r\n				<td><div id=''top''></div></td>\r\n				<td><div id=''left''></div></td>\r\n				<td><div id=''right''></div></td>\r\n				<td><div id=''bottom''></div></td>\r\n			</tr>\r\n		</table>\r\n		<div id="object">\r\n			object\r\n		</div>\r\n\r\n		<script>\r\n			var t = 0;\r\n			var l = 0;\r\n			var r = 0;\r\n			var b = 0;\r\n\r\n			var object = document.getElementById(''object'');\r\n			$(''#object'').draggable();\r\n			object.onmousemove = function() {\r\n				//console.log(this);\r\n				var rect = object.getBoundingClientRect();\r\n				//console.log(rect.top, rect.right, rect.bottom, rect.left);\r\n				$(''#top'').html(parseInt(rect.top));\r\n				$(''#left'').html(parseInt(rect.left));\r\n				$(''#right'').html(parseInt(rect.right));\r\n				$(''#bottom'').html(parseInt(rect.bottom));\r\n\r\n				var newTop = parseInt(rect.top);\r\n				var newLeft = parseInt(rect.left);\r\n				var newRight = parseInt(rect.right);\r\n				var newBottom = parseInt(rect.bottom);\r\n\r\n				//console.log( t+"----"+newTop);\r\n				if (t < newTop) {\r\n					animDown();\r\n				} else if (t > newTop) {\r\n					animUp();\r\n				}\r\n				if (l < newLeft) {\r\n					animRight();\r\n				} else if (l > newLeft) {\r\n					animLeft();\r\n				}\r\n				t = newTop;\r\n				l = newLeft;\r\n				r = newRight;\r\n				b = newBottom;\r\n\r\n			};\r\n\r\n			function animUp() {\r\n				console.log(''moving up'');\r\n			}\r\n\r\n			function animLeft() {\r\n				console.log(''moving left'');\r\n			}\r\n\r\n			function animRight() {\r\n				console.log(''moving right'');\r\n			}\r\n\r\n			function animDown() {\r\n				console.log(''moving down'');\r\n			}\r\n\r\n		</script>\r\n	</body>\r\n</ht