(442, '//  SEARCH FUNCTIONALITY   /////////////////////////////////////////////////////////////////////////////////\r\n$("#searchButton").click(function(){\r\n	theSearchFunction();\r\n});\r\nfunction checkKeyPress(e) {\r\n	e = e || window.event;\r\n	if (e.keyCode == ''13'') {\r\n		// enter\r\n		theSearchFunction();\r\n	}  \r\n	return e.keyCode;\r\n}\r\ndocument.onkeydown = checkKeyPress;\r\n\r\nfunction theSearchFunction(){\r\n	var search_text;\r\n	search_text = $("#search_context").val();\r\n	if(search_text !="" && search_text !=" " ){\r\n		// re-ex for ALL alpha numeric ALSO -,_, and space.\r\n		var clean_search_text =	search_text.match(/^[-_ a-zA-Z0-9]+$/)\r\n		 var where="";\r\n			  for(var i=0;i <search_fields.length ;i++){\r\n			  	if(i==0){\r\n			  		where +=" WHERE "+search_fields[i]+" LIKE ''%25"+clean_search_text+"%25'' ";\r\n			  	}else{\r\n			  		where +=" OR   "+search_fields[i]+" LIKE ''%25"+clean_search_text+"%25'' ";\r\n			  	}\r\n			  }\r\n			 var view='''';\r\n			$(location).attr(''href'',"<?php BASE_URL?>?c="+controller+"&m=search&v="+view+"&where="+where+"&keyword="+clean_search_text);\r\n	}\r\n}\r\n///////////////////////////////////////////////////////////////', 'search package ', 'javascript'),
