(483, '<!DOCTYPE html>\r\n<html>\r\n<body>\r\n\r\n<p>Count numbers: <output id="result"></output></p>\r\n<button onclick="startWorker()">Start Worker</button> \r\n<button onclick="stopWorker()">Stop Worker</button>\r\n<br><br>\r\n\r\n<script>\r\nvar w;\r\n\r\nfunction startWorker() {\r\n    if(typeof(Worker) !== "undefined") {\r\n        if(typeof(w) == "undefined") {\r\n            w = new Worker("demo_workers.js");\r\n        }\r\n        w.onmessage = function(event) {\r\n           \r\n            if(event <= 12){\r\n            	 document.getElementById("result").innerHTML = "lesser";//event.data\r\n            }else{\r\n            	 document.getElementById("result").innerHTML = "greater";\r\n            }\r\n        };\r\n    } else {\r\n        document.getElementById("result").innerHTML = "Sorry! No Web Worker support.";\r\n    }\r\n}\r\n\r\nfunction stopWorker() { \r\n    w.terminate();\r\n    w = undefined;\r\n}\r\n</script>\r\n\r\n</body>\r\n</html>\r\n\r\n\r\n<!-- THE EXTERNAL js file named in this example demo_workers.js -->\r\n<!--\r\n	var i = 0;\r\n\r\nfunction timedCount() {\r\n   // i = i + 1;\r\n   // postMessage(i);\r\n     try {\r\n	    var xhr = new XMLHttpRequest();\r\n	    xhr.open(''GET'', "DB_checker.php", false);\r\n	    xhr.send();\r\n	    postMessage(xhr.responseText);\r\n	 } catch (e) {\r\n	    postMessage(e);\r\n	  }\r\n    setTimeout("timedCount()",500);\r\n}\r\n\r\ntimedCount();\r\n	\r\n-->\r\n', 'web worker example : checks a db ', 'javascript'),
