(487, 'function makeModule(id,name){\r\n	var MODULE = (function () {\r\n		var obj = {};\r\n		//-private\r\n		var privateProperty = "Module Maker 3000";\r\n		function privateMethod(arg) {\r\n\r\n			return "Private Method and arg: " + arg;\r\n		}\r\n		//-public\r\n		obj.id = id;\r\n		obj.name = name;\r\n\r\n		obj.publicMethod = function (arg) {\r\n			//-code\r\n			var res = privateMethod(arg);\r\n			return ''Public Method with: ''+res;\r\n		};\r\n\r\n		return obj;\r\n	}());\r\n	return MODULE;\r\n}\r\n/*\r\n<!DOCTYPE html>\r\n<html>\r\n<head>\r\n<script src="module_factory_example.js"></script>\r\n</head>\r\n\r\n<body>\r\n<script>\r\nvar modj = makeModule(99,"SAM");\r\nconsole.log(modj.id);\r\nconsole.log(modj.name);\r\nvar str = modj.publicMethod("allo");\r\nconsole.log(str);\r\n</script>\r\n\r\n</body>\r\n</html>\r\n*/', 'module factory of sorts modular object', 'javascript'),
