inding end\r\n      Symbol.bindTriggerAction(compId, symbolName, "Default Timeline", 500, function(sym, e) {\r\n          symbol_1.play();\r\n          if(condition == "sun"){\r\n          	sun.show();\r\n          	 sun.play();\r\n          }\r\n         \r\n      });\r\n      //Edge binding end\r\n\r\n      Symbol.bindTriggerAction(compId, symbolName, "Default Timeline", 1500, function(sym, e) {\r\n          symbol_2.play();\r\n          if(condition == "sun"){\r\n          	sun.show();\r\n          	 sun.play();\r\n          }\r\n      });\r\n      //Edge binding end\r\n\r\n     \r\n\r\n      \r\n\r\n   })("stage");\r\n   //Edge symbol end:''stage''\r\n\r\n   //=========================================================\r\n   \r\n   //Edge symbol: ''sqr_out''\r\n   (function(symbolName) {   \r\n   \r\n   })("sqr_out");\r\n   //Edge symbol end:''sqr_out''\r\n\r\n   //=========================================================\r\n   \r\n   //Edge symbol: ''sun''\r\n   (function(symbolName) {   \r\n   \r\n      Symbol.bindTriggerAction(compId, symbolName, "Default Timeline", 0, function(sym, e) {\r\n         // insert code here\r\n         \r\n\r\n      });\r\n      //Edge binding end\r\n\r\n      Symbol.bindTriggerAction(compId, symbolName, "Default Timeline", 2000, function(sym, e) {\r\n         // insert code here\r\n         sym.play("restart_point");\r\n\r\n      });\r\n      //Edge binding end\r\n\r\n   })("sun");\r\n   //Edge symbol end:''sun''\r\n\r\n})(jQuery, AdobeEdge, "gomedia40-20");', 'edge: symbols ', 'javascript'),
