(514, 'DROP TABLE IF EXISTS time_dimension;\r\nCREATE TABLE time_dimension (\r\n        id                      INTEGER PRIMARY KEY,  -- year*10000+month*100+day\r\n        db_date                 DATE NOT NULL,\r\n        year                    INTEGER NOT NULL,\r\n        month                   INTEGER NOT NULL, -- 1 to 12\r\n        day                     INTEGER NOT NULL, -- 1 to 31\r\n        quarter                 INTEGER NOT NULL, -- 1 to 4\r\n        week                    INTEGER NOT NULL, -- 1 to 52/53\r\n        day_name                VARCHAR(9) NOT NULL, -- ''Monday'', ''Tuesday''...\r\n        month_name              VARCHAR(9) NOT NULL, -- ''January'', ''February''...\r\n        holiday_flag            CHAR(1) DEFAULT ''f'' CHECK (holiday_flag in (''t'', ''f'')),\r\n        weekend_flag            CHAR(1) DEFAULT ''f'' CHECK (weekday_flag in (''t'', ''f'')),\r\n        event                   VARCHAR(50),\r\n        UNIQUE td_ymd_idx (year,month,day),\r\n        UNIQUE td_dbdate_idx (db_date)\r\n\r\n) Engine=MyISAM;\r\n\r\nDROP PROCEDURE IF EXISTS fill_date_dimension;\r\nDELIMITER //\r\nCREATE PROCEDURE fill_date_dimension(IN startdate DATE,IN stopdate DATE)\r\nBEGIN\r\n    DECLARE currentdate DATE;\r\n    SET currentdate = startdate;\r\n    WHILE currentdate < stopdate DO\r\n        INSERT INTO time_dimension VALUES (\r\n                        YEAR(currentdate)*10000+MONTH(currentdate)*100 + DAY(currentdate),\r\n                        currentdate,\r\n                        YEAR(currentdate),\r\n                        MONTH(currentdate),\r\n                        DAY(currentdate),\r\n                        QUARTER(currentdate),\r\n                        WEEKOFYEAR(currentdate),\r\n                        DATE_FORMAT(currentdate,''%W''),\r\n                        DATE_FORMAT(currentdate,''%M''),\r\n                        ''f'',\r\n                        CASE DAYOFWEEK(currentdate) WHEN 1 THEN ''t'' WHEN 7 then ''t'' ELSE ''f'' END,\r\n                        NULL);\r\n        SET currentdate = ADDDATE(currentdate,INTERVAL 1 DAY);\r\n    END WHILE;\r\nEND\r\n//\r\nDELIMITER ;\r\n\r\nTRUNCATE TABLE time_dimension;\r\n\r\nCALL fill_date_dimension(''1-01-01'',''2015-01-01'');\r\nOPTIMIZE TABLE time_dimension;', 'mysql CREATE a calendar table', 'php'),
