(518, '/*\r\n  SortTable\r\n  version 2\r\n  7th April 2007\r\n  Stuart Langridge, http://www.kryogenix.org/code/browser/sorttable/\r\n\r\n  Instructions:\r\n  Download this file\r\n  Add <script src="sorttable.js"></script> to your HTML\r\n  Add class="sortable" to any table you''d like to make sortable\r\n  Click on the headers to sort\r\n\r\n  Thanks to many, many people for contributions and suggestions.\r\n  Licenced as X11: http://www.kryogenix.org/code/browser/licence.html\r\n  This basically means: do what you want with it.\r\n*/\r\n\r\n\r\nvar stIsIE = /*@cc_on!@*/false;\r\n\r\nsorttable = {\r\n  init: function() {\r\n    // quit if this function has already been called\r\n    if (arguments.callee.done) return;\r\n    // flag this function so we don''t do the same thing twice\r\n    arguments.callee.done = true;\r\n    // kill the timer\r\n    if (_timer) clearInterval(_timer);\r\n\r\n    if (!document.createElement || !document.getElementsByTagName) return;\r\n\r\n    sorttable.DATE_RE = /^(\\d\\d?)[\\/\\.-](\\d\\d?)[\\/\\.-]((\\d\\d)?\\d\\d)$/;\r\n\r\n    forEach(document.getElementsByTagName(''table''), function(table) {\r\n      if (table.className.search(/\\bsortable\\b/) != -1) {\r\n        sorttable.makeSortable(table);\r\n      }\r\n    });\r\n\r\n  },\r\n\r\n  makeSortable: function(table) {\r\n    if (table.getElementsByTagName(''thead'').length == 0) {\r\n      // table doesn''t have a tHead. Since it should have, create one and\r\n      // put the first table row in it.\r\n      the = document.createElement(''thead'');\r\n      the.appendChild(table.rows[0]);\r\n      table.insertBefore(the,table.firstChild);\r\n    }\r\n    // Safari doesn''t support table.tHead, sigh\r\n    if (table.tHead == null) table.tHead = table.getElementsByTagName(''thead'')[0];\r\n\r\n    if (table.tHead.rows.length != 1) return; // can''t cope with two header rows\r\n\r\n    // Sorttable v1 put rows with a class of "sortbottom" at the bottom (as\r\n    // "total" rows, for example). This is B&R, since what you''re supposed\r\n    // to do is put them in a tfoot. So, if there are sortbottom rows,\r\n    // for backwards compatibility, move them to tfoot (creating it if needed).\r\n    sortbottomrows = [];\r\n    for (var i=0; i<table.rows.length; i++) {\r\n      if (table.rows[i].className.search(/\\bsortbottom\\b/) != -1) {\r\n        sortbottomrows[sortbottomrows.length] = table.rows[i];\r\n      }\r\n    }\r\n    if (sortbottomrows) {\r\n      if (table.tFoot == null) {\r\n        // table doesn''t have a tfoot. Create one.\r\n        tfo = document.createElement(''tfoot'');\r\n        table.appendChild(tfo);\r\n      }\r\n      for (var i=0; i<sortbottomrows.length; i++) {\r\n        tfo.appendChild(sortbottomrows[i]);\r\n      }\r\n      delete sortbottomrows;\r\n    }\r\n\r\n    // work through each column and calculate its type\r\n    headrow = table.tHead.rows[0].cells;\r\n    for (var i=0; i<headrow.length; i++) {\r\n      // manually override the type with a sorttable_type attribute\r\n      if (!headrow[i].className.match(/\\bsorttable_nosort\\b/)) { // skip this col\r\n        mtch = headrow[i].className.match(/\\bsorttable_([a-z0-9]+)\\b/);\r\n        if (mtch) { override = mtch[1]; }\r\n	      if (mtch && typeof sorttable["sort_"+override] == ''function'') {\r\n	        headrow[i].sorttable_sortfunction = sorttable["sort_"+override];\r\n	      } else {\r\n	        headrow[i].sorttable_sortfunction = sorttable.guessType(table,i);\r\n	      }\r\n	      // make it clickable to sort\r\n	      headrow[i].sorttable_columnindex = i;\r\n	      headrow[i].sorttable_tbody = table.tBodies[0];\r\n	      dean_addEvent(headrow[i],"click", sorttable.innerSortFunction = function(e) {\r\n\r\n          if (this.className.search(/\\bsorttable_sorted\\b/) != -1) {\r\n            // if we''re already sorted by this column, just\r\n            // reverse the table, which is quicker\r\n            sorttable.reverse(this.sorttable_tbody);\r\n            this.className = this.className.replace(''sorttable_sorted'',\r\n                                                    ''sorttable_sorted_reverse'');\r\n            this.removeChild(document.getElementById(''sorttable_sortfwdind''));\r\n            sortrevind = document.createElement(''span'');\r\n            sortrevind.id = "sorttable_sortrevind";\r\n            sortrevind.innerHTML = stIsIE ? ''&nbsp<font face="webdings">5</font>'' : ''&nbsp;&#x25B4;'';\r\n            this.appendChild(sortrevind);\r\n            return;\r\n          }\r\n          if (this.className.search(/\\bsorttable_sorted_reverse\\b/) != -1) {\r\n            // if we''re already sorted by this column in reverse, just\r\n            // re-reverse the table, which is quicker\r\n            sorttable.reverse(this.sorttable_tbody);\r\n            this.className = this.className.replace(''sorttable_sorted_reverse'',\r\n                                                    ''sorttable_sorted'');\r\n            this.removeChild(document.getElementById(''sorttable_sortrevind''));\r\n            sortfwdind = document.createElement(''span'');\r\n            sortfwdind.id = "sorttable_sortfwdind";\r\n            sortfwdind.innerHTML = stIsIE ? ''&nbsp<font face="webdings">6</font>'' : ''&nbsp;&#x25BE;'';\r\n            this.appendChild(sortfwdind);\r\n            return;\r\n          }\r\n\r\n          // remove sorttable_sorted classes\r\n          theadrow = this.parentNode;\r\n          forEach(theadrow.childNodes, function(cell) {\r\n            if (cell.nodeType == 1) { // an element\r\n              cell.className = cell.className.replace(''sorttable_sorted_reverse'','''');\r\n              cell.className = cell.className.replace(''sorttable_sorted'','''');\r\n            }\r\n          });\r\n          sortfwdind = document.getElementById(''sorttable_sortfwdind'');\r\n          if (sortfwdind) { sortfwdind.parentNode.removeChild(sortfwdind); }\r\n          sortrevind = document.getElementById(''sorttable_sortrevind'');\r\n          if (sortrevind) { sortrevind.parentNode.removeChild(sortrevind); }\r\n\r\n          this.className += '' sorttable_sorted'';\r\n          sortfwdind = document.createElement(''span'');\r\n          sortfwdind.id = "sorttable_sortfwdind";\r\n          sortfwdind.innerHTML = stIsIE ? ''&nbsp<font face="webdings">6</font>'' : ''&nbsp;&#x25BE;'';\r\n          this.appendChild(sortfwdind);\r\n\r\n	        // build an array to sort. This is a Schwartzian transform thing,\r\n	        // i.e., we "decorate" each row with the actual sort key,\r\n	        // sort based on the sort keys, and then put the rows back in order\r\n	        // which is a lot faster because you only do getInnerText once per row\r\n	        row_array = [];\r\n	        col = this.sorttable_columnindex;\r\n	        rows = this.sorttable_tbody.rows;\r\n	        for (var j=0; j<rows.length; j++) {\r\n	          row_array[row_array.length] = [sorttable.getInnerText(rows[j].cells[col]), rows[j]];\r\n	        }\r\n	        /* If you want a stable sort, uncomment the following line */\r\n	        //sorttable.shaker_sort(row_array, this.sorttable_sortfunction);\r\n	        /* and comment out this one */\r\n	        row_array.sort(this.sorttable_sortfunction);\r\n\r\n	        tb = this.sorttable_tbody;\r\n	        for (var j=0; j<row_array.length; j++) {\r\n	          tb.appendChild(row_array[j][1]);\r\n	        }\r\n\r\n	        delete row_array;\r\n	      });\r\n	    }\r\n    }\r\n  },\r\n\r\n  guessType: function(table, column) {\r\n    // guess the type of a column based on its first non-blank row\r\n    sortfn = sorttable.sort_alpha;\r\n    for (var i=0; i<table.tBodies[0].rows.length; i++) {\r\n      text = sorttable.getInnerText(table.tBodies[0].rows[i].cells[column]);\r\n      if (text != '''') {\r\n        if (text.match(/^-?[Â£$Â¤]?[\\d,.]+%?$/)) {\r\n          return sorttable.sort_numeric;\r\n        }\r\n        // check for a date: dd/mm/yyyy or dd/mm/yy\r\n        // can have / or . or - as separator\r\n        // can be mm/dd as well\r\n        possdate = text.match(sorttable.DATE_RE);\r\n        if (possdate) {\r\n          // looks like a date\r\n          first = parseInt(possdate[1]);\r\n          second = parseInt(possdate[2]);\r\n          if (first > 12) {\r\n            // definitely dd/mm\r\n            return sorttable.sort_ddmm;\r\n          } else if (second > 12) {\r\n            return sorttable.sort_mmdd;\r\n          } else {\r\n            // looks like a date, but we can''t tell which, so assume\r\n            // that it''s dd/mm (English imperialism!) and keep looking\r\n            sortfn = sorttable.sort_ddmm;\r\n          }\r\n        }\r\n      }\r\n    }\r\n    return sortfn;\r\n  },\r\n\r\n  getInnerText: function(node) {\r\n    // gets the text we want to use for sorting for a cell.\r\n    // strips leading and trailing whitespace.\r\n    // this is *not* a generic getInnerText function; it''s special to sorttable.\r\n    // for example, you can override the cell text with a customkey attribute.\r\n    // it also gets .value for <input> fields.\r\n\r\n    if (!node) return "";\r\n\r\n    hasInputs = (typeof node.getElementsByTagName == ''function'') &&\r\n                 node.getElementsByTagName(''input'').length;\r\n\r\n    if (node.getAttribute("sorttable_customkey") != null) {\r\n      return node.getAttribute("sorttable_customkey");\r\n    }\r\n    else if (typeof node.textContent != ''undefined'' && !hasInputs) {\r\n      return node.textContent.replace(/^\\s+|\\s+$/g, '''');\r\n    }\r\n    else if (typeof node.innerText != ''undefined'' && !hasInputs) {\r\n      return node.innerText.replace(/^\\s+|\\s+$/g, '''');\r\n    }\r\n    else if (typeof node.text != ''undefined'' && !hasInputs) {\r\n      return node.text.replace(/^\\s+|\\s+$/g, '''');\r\n    }\r\n    else {\r\n      switch (node.nodeType) {\r\n        case 3:\r\n          if (node.nodeName.toLowerCase() == ''input'') {\r\n            return node.value.replace(/^\\s+|\\s+$/g, '''');\r\n          }\r\n        case 4:\r\n          return node.nodeValue.replace(/^\\s+|\\s+$/g, '''');\r\n          break;\r\n        case 1:\r\n        case 11:\r\n          var innerText = '''';\r\n          for (var i = 0; i < node.childNodes.length; i++) {\r\n            innerText += sorttable.getInnerText(node.childNodes[i]);\r\n          }\r\n          return innerText.replace(/^\\s+|\\s+$/g, '''');\r\n          break;\r\n        default:\r\n          return '''';\r\n      }\r\n    }\r\n  },\r\n\r\n  reverse: function(tbody) {\r\n    // reverse the rows in a tbody\r\n    newrows = [];\r\n    for (var i=0; i<tbody.rows.length; i++) {\r\n      newrows[newrows.length] = tbody.rows[i];\r\n    }\r\n    for (var i=newrows.length-1; i>=0; i--) {\r\n       tbody.appendChild(newrows[i]);\r\n    }\r\n    delete newrows;\r\n  },\r\n\r\n  /* sort functions\r\n     each sort function takes two parameters, a and b\r\n     you are comparing a[0] and b[0] */\r\n  sort_numeric: function(a,b) {\r\n    aa = parseFloat(a[0].replace(/[^0-9.-]/g,''''));\r\n    if (isNaN(aa)) aa = 0;\r\n    bb = parseFloat(b[0].replace(/[^0-9.-]/g,''''));\r\n    if (isNaN(bb)) bb = 0;\r\n    return aa-bb;\r\n  },\r\n  sort_alpha: function(a,b) {\r\n    if (a[0]==b[0]) return 0;\r\n    if (a[0]<b[0]) return -1;\r\n    return 1;\r\n  },\r\n  sort_ddmm: function(a,b) {\r\n    mtch = a[0].match(sorttable.DATE_RE);\r\n    y = mtch[3]; m = mtch[2]; d = mtch[1];\r\n    if (m.length == 1) m = ''0''+m;\r\n    if (d.length == 1) d = ''0''+d;\r\n    dt1 = y+m+d;\r\n    mtch = b[0].match(sorttable.DATE_RE);\r\n    y = mtch[3]; m = mtch[2]; d = mtch[1];\r\n    if (m.length == 1) m = ''0''+m;\r\n    if (d.length == 1) d = ''0''+d;\r\n    dt2 = y+m+d;\r\n    if (dt1==dt2) return 0;\r\n    if (dt1<dt2) return -1;\r\n    return 1;\r\n  },\r\n  sort_mmdd: function(a,b) {\r\n    mtch = a[0].match(sorttable.DATE_RE);\r\n    y = mtch[3]; d = mtch[2]; m = mtch[1];\r\n    if (m.length == 1) m = ''0''+m;\r\n    if (d.length == 1) d = ''0''+d;\r\n    dt1 = y+m+d;\r\n    mtch = b[0].match(sorttable.DATE_RE);\r\n    y = mtch[3]; d = mtch[2]; m = mtch[1];\r\n    if (m.length == 1) m = ''0''+m;\r\n    if (d.length == 1) d = ''0''+d;\r\n    dt2 = y+m+d;\r\n    if (dt1==dt2) return 0;\r\n    if (dt1<dt2) return -1;\r\n    return 1;\r\n  },\r\n\r\n  shaker_sort: function(list, comp_func) {\r\n    // A stable sort function to allow multi-level sorting of data\r\n    // see: http://en.wikipedia.org/wiki/Cocktail_sort\r\n    // thanks to Joseph Nahmias\r\n    var b = 0;\r\n    var t = list.length - 1;\r\n    var swap = true;\r\n\r\n    while(swap) {\r\n        swap = false;\r\n        for(var i = b; i < t; ++i) {\r\n            if ( comp_func(list[i], list[i+1]) > 0 ) {\r\n                var q = list[i]; list[i] = list[i+1]; list[i+1] = q;\r\n                swap = true;\r\n            }\r\n        } // for\r\n        t--;\r\n\r\n        if (!swap) break;\r\n\r\n        for(var i = t; i > b; --i) {\r\n            if ( comp_func(list[i], list[i-1]) < 0 ) {\r\n                var q = list[i]; list[i] = list[i-1]; list[i-1] = q;\r\n                swap = true;\r\n            }\r\n        } // for\r\n        b++;\r\n\r\n    } // while(swap)\r\n  }\r\n};\r\n\r\n/* ******************************************************************\r\n   Supporting functions: bundled here to avoid depending on a library\r\n   ****************************************************************** */\r\n\r\n// Dean Edwards/Matthias Miller/John Resig\r\n\r\n/* for Mozilla/Opera9 */\r\nif (document.addEventListener) {\r\n    document.addEventListener("DOMContentLoaded", sorttable.init, false);\r\n}\r\n\r\n/* for Internet Explorer */\r\n/*@cc_on @*/\r\n/*@if (@_win32)\r\n    document.write("<script id=__ie_onload defer src=javascript:void(0)><\\/script>");\r\n    var script = document.getElementById("__ie_onload");\r\n    script.onreadystatechange = function() {\r\n        if (this.readyState == "complete") {\r\n            sorttable.init(); // call the onload handler\r\n        }\r\n    };\r\n/*@end @*/\r\n\r\n/* for Safari */\r\nif (/WebKit/i.test(navigator.userAgent)) { // sniff\r\n    var _timer = setInterval(function() {\r\n        if (/loaded|complete/.test(document.readyState)) {\r\n            sorttable.init(); // call the onload handler\r\n        }\r\n    }, 10);\r\n}\r\n\r\n/* for other browsers */\r\nwindow.onload = sorttable.init;\r\n\r\n// written by Dean Edwards, 2005\r\n// with input from Tino Zijdel, Matthias Miller, Diego Perini\r\n\r\n// http://dean.edwards.name/weblog/2005/10/add-event/\r\n\r\nfunction dean_addEvent(element, type, handler) {\r\n	if (element.addEventListener) {\r\n		element.addEventListener(type, handler, false);\r\n	} else {\r\n		// assign each event handler a unique ID\r\n		if (!handler.$$guid) handler.$$guid = dean_addEvent.guid++;\r\n		// create a hash table of event types for the element\r\n		if (!element.events) element.events = {};\r\n		// create a hash table of event handlers for each element/event pair\r\n		var handlers = element.events[type];\r\n		if (!handlers) {\r\n			handlers = element.events[type] = {};\r\n			// store the existing event handler (if there is one)\r\n			if (element["on" + type]) {\r\n				handlers[0] = element["on" + type];\r\n			}\r\n		}\r\n		// store the event handler in the hash table\r\n		handlers[handler.$$guid] = handler;\r\n		// assign a global event handler to do all the work\r\n		element["on" + type] = handleEvent;\r\n	}\r\n};\r\n// a counter used to create unique IDs\r\ndean_addEvent.guid = 1;\r\n\r\nfunction removeEvent(element, type, handler) {\r\n	if (element.removeEventListener) {\r\n		element.removeEventListener(type, handler, false);\r\n	} else {\r\n		// delete the event handler from the hash table\r\n		if (element.events && element.events[type]) {\r\n			delete element.events[type][handler.$$guid];\r\n		}\r\n	}\r\n};\r\n\r\nfunction handleEvent(event) {\r\n	var returnValue = true;\r\n	// grab the event object (IE uses a global event object)\r\n	event = event || fixEvent(((this.ownerDocument || this.document || this).parentWindow || window).event);\r\n	// get a reference to the hash table of event handlers\r\n	var handlers = this.events[event.type];\r\n	// execute each event handler\r\n	for (var i in handlers) {\r\n		this.$$handleEvent = handlers[i];\r\n		if (this.$$handleEvent(event) === false) {\r\n			returnValue = false;\r\n		}\r\n	}\r\n	return returnValue;\r\n};\r\n\r\nfunction fixEvent(event) {\r\n	// add W3C standard event methods\r\n	event.preventDefault = fixEvent.preventDefault;\r\n	event.stopPropagation = fixEvent.stopPropagation;\r\n	return event;\r\n};\r\nfixEvent.preventDefault = function() {\r\n	this.returnValue = false;\r\n};\r\nfixEvent.stopPropagation = function() {\r\n  this.cancelBubble = true;\r\n};\r\n\r\n// Dean''s forEach: http://dean.edwards.name/base/forEach.js\r\n/*\r\n	forEach, version 1.0\r\n	Copyright 2006, Dean Edwards\r\n	License: http://www.opensource.org/licenses/mit-license.php\r\n*/\r\n\r\n// array-like enumeration\r\nif (!Array.forEach) { // mozilla already supports this\r\n	Array.forEach = function(array, block, context) {\r\n		for (var i = 0; i < array.length; i++) {\r\n			block.call(context, array[i], i, array);\r\n		}\r\n	};\r\n}\r\n\r\n// generic enumeration\r\nFunction.prototype.forEach = function(object, block, context) {\r\n	for (var key in object) {\r\n		if (typeof this.prototype[key] == "undefined") {\r\n			block.call(context, object[key], key, object);\r\n		}\r\n	}\r\n};\r\n\r\n// character enumeration\r\nString.forEach = function(string, block, context) {\r\n	Array.forEach(string.split(""), function(chr, index) {\r\n		block.call(context, chr, index, string);\r\n	});\r\n};\r\n\r\n// globally resolve forEach enumeration\r\nvar forEach = function(object, block, context) {\r\n	if (object) {\r\n		var resolve = Object; // default\r\n		if (object instanceof Function) {\r\n			// functions have a "length" property\r\n			resolve = Function;\r\n		} else if (object.forEach instanceof Function) {\r\n			// the object implements a custom forEach method so use that\r\n			object.forEach(block, context);\r\n			return;\r\n		} else if (typeof object == "string") {\r\n			// the object is a string\r\n			resolve = String;\r\n		} else if (typeof object.length == "number") {\r\n			// the object is array-like\r\n			resolve = Array;\r\n		}\r\n		resolve.forEach(object, block, context);\r\n	}\r\n};\r\n', 'sorttable.js    The code for sorting tables. ( include library and <table class="sortable">', 'javascript'),
