(521, '<script type="text/javascript">\r\nfunction getViewportWidth()\r\n{\r\n       if (window.innerWidth)\r\n       {\r\n               return window.innerWidth;\r\n       }\r\n       else if (document.body && document.body.offsetWidth)\r\n       {\r\n               return document.body.offsetWidth;\r\n       }\r\n       else\r\n       {\r\n               return 0;\r\n       }\r\n}\r\n\r\nfunction getViewportHeight()\r\n{\r\n       if (window.innerHeight)\r\n       {\r\n               return window.innerHeight;\r\n       }\r\n       else if (document.body && document.body.offsetHeight)\r\n       {\r\n               return document.body.offsetHeight;\r\n       }\r\n       else\r\n       {\r\n               return 0;\r\n       }\r\n}\r\n\r\nvar tellMeTheSizes=function()\r\n{\r\n       document.getElementById("viewportwidth").innerHTML = getViewportWidth() + "px";\r\n       document.getElementById("viewportheight").innerHTML = getViewportHeight() + "px";\r\n       document.getElementById("resolutionheight").innerHTML = screen.height + "px";\r\n       document.getElementById("resolutionwidth").innerHTML = screen.width + "px";\r\n}\r\n\r\nwindow.onload=function()\r\n{\r\n       tellMeTheSizes();\r\n}\r\n\r\nwindow.onresize=function()\r\n{\r\n       tellMeTheSizes();\r\n}\r\n\r\nwindow.onmousemove=function(event)\r\n{\r\n       ev = event || window.event;\r\n       document.getElementById("mousetop").innerHTML = ev.pageY + "px";\r\n       document.getElementById("mouseleft").innerHTML = ev.pageX + "px";\r\n}\r\n</script>', 'Viewport Size, Screen Resolution and Mouse Position mousex mouse-x', 'javascript'),
(522, '//1) Use a blank.gif as the src of images, and include the width and height of the final image.\r\n//2) HTML:\r\n<img src="blank.gif" class="lazy" data-src="/images/full-size.jpg" width="240" height="152">\r\n\r\n//3)\r\n/* lazyload.js (c) Lorenzo Giuliani\r\n * MIT License (http://www.opensource.org/licenses/mit-license.html)\r\n *\r\n * expects a list of:  \r\n * `<img src="blank.gif" data-src="my_image.png" width="600" height="400" class="lazy">`\r\n */\r\n\r\n!function(window){\r\n  var $q = function(q, res){\r\n        if (document.querySelectorAll) {\r\n          res = document.querySelectorAll(q);\r\n        } else {\r\n          var d=document\r\n            , a=d.styleSheets[0] || d.createStyleSheet();\r\n          a.addRule(q,''f:b'');\r\n          for(var l=d.all,b=0,c=[],f=l.length;b<f;b++)\r\n            l[b].currentStyle.f && c.push(l[b]);\r\n\r\n          a.removeRule(0);\r\n          res = c;\r\n        }\r\n        return res;\r\n      }\r\n    , addEventListener = function(evt, fn){\r\n        window.addEventListener\r\n          ? this.addEventListener(evt, fn, false)\r\n          : (window.attachEvent)\r\n            ? this.attachEvent(''on'' + evt, fn)\r\n            : this[''on'' + evt] = fn;\r\n      }\r\n    , _has = function(obj, key) {\r\n        return Object.prototype.hasOwnProperty.call(obj, key);\r\n      }\r\n    ;\r\n\r\n  function loadImage (el, fn) {\r\n    var img = new Image()\r\n      , src = el.getAttribute(''data-src'');\r\n    img.onload = function() {\r\n      if (!! el.parent)\r\n        el.parent.replaceChild(img, el)\r\n      else\r\n        el.src = src;\r\n\r\n      fn? fn() : null;\r\n    }\r\n    img.src = src;\r\n  }\r\n\r\n  function elementInViewport(el) {\r\n    var rect = el.getBoundingClientRect()\r\n\r\n    return (\r\n       rect.top    >= 0\r\n    && rect.left   >= 0\r\n    && rect.top <= (window.innerHeight || document.documentElement.clientHeight)\r\n    )\r\n  }\r\n\r\n    var images = new Array()\r\