(533, '<?php\r\n\r\n$databasehost = "localhost";\r\n$databasename = "test";\r\n$databasetable = "sample";\r\n$databaseusername ="test";\r\n$databasepassword = "";\r\n$fieldseparator = ",";\r\n$lineseparator = "\\n";\r\n$csvfile = "filename.csv";\r\n\r\n/********************************/\r\n/* Would you like to add an ampty field at the beginning of these records?\r\n/* This is useful if you have a table with the first field being an auto_increment integer\r\n/* and the csv file does not have such as empty field before the records.\r\n/* Set 1 for yes and 0 for no. ATTENTION: don''t set to 1 if you are not sure.\r\n/* This can dump data in the wrong fields if this extra field does not exist in the table\r\n/********************************/\r\n$addauto = 0;\r\n/********************************/\r\n\r\n/* Would you like to save the mysql queries in a file? If yes set $save to 1.\r\n/* Permission on the file should be set to 777. Either upload a sample file through ftp and\r\n/* change the permissions, or execute at the prompt: touch output.sql && chmod 777 output.sql\r\n/********************************/\r\n$save = 1;\r\n$outputfile = "output.sql";\r\n/********************************/\r\n\r\nif (!file_exists($csvfile)) {\r\n        echo "File not found. Make sure you specified the correct path.\\n";\r\n        exit;\r\n}\r\n\r\n$file = fopen($csvfile,"r");\r\n\r\nif (!$file) {\r\n        echo "Error opening data file.\\n";\r\n        exit;\r\n}\r\n\r\n$size = filesize($csvfile);\r\n\r\nif (!$size) {\r\n        echo "File is empty.\\n";\r\n        exit;\r\n}\r\n\r\n$csvcontent = fread($file,$size);\r\n\r\nfclose($file);\r\n\r\n$con = @mysql_connect($databasehost,$databaseusername,$databasepassword) or die(mysql_error());\r\n@mysql_select_db($databasename) or die(mysql_error());\r\n\r\n$lines = 0;\r\n$queries = "";\r\n$linearray = array();\r\n\r\nforeach(split($lineseparator,$csvcontent) as $line) {\r\n\r\n        $lines++;\r\n\r\n        $line = trim($line," \\t");\r\n\r\n        $line = str_replace("\\r","",$line);\r\n\r\n        /************************************\r\n        This line escapes the special character. remove it if entries are already escaped in the csv file\r\n        ************************************/\r\n        $line = str_replace("''","\\''",$line);\r\n        /*************************************/\r\n\r\n        $linearray = explode($fieldseparator,$line);\r\n\r\n        $linemysql = implode("'',''",$linearray);\r\n\r\n        if($addauto)\r\n                $query = "insert into $databasetable values('''',''$linemysql'');";\r\n        else\r\n                $query = "insert into $databasetable values(''$linemysql'');";\r\n\r\n        $queries .= $query . "\\n";\r\n\r\n        @mysql_query($query);\r\n}\r\n\r\n@mysql_close($con);\r\n\r\nif ($save) {\r\n\r\n        if (!is_writable($outputfile)) {\r\n                echo "File is not writable, check permissions.\\n";\r\n        }\r\n\r\n        else {\r\n                $file2 = fopen($outputfile,"w");\r\n\r\n                if(!$file2) {\r\n                        echo "Error writing to the output file.\\n";\r\n                }\r\n                else {\r\n                        fwrite($file2,$queries);\r\n                        fclose($file2);\r\n                }\r\n        }\r\n\r\n}\r\n\r\necho "Found a total of $lines records in this csv file.\\n";\r\n\r\n?>', 'import CSV into mySQL ', 'php'),
(534, '<?php\r\n\r\n$requiredFields = array(''field1'', ''field2'', ''field3'', ''field4'');\r\n$formData = getFormData($requiredFields);\r\n\r\nfunction getFormData($requiredFields){\r\n       $formData = array();\r\n       $formData[''valid''] = true;\r\n       $formData[''fields''] = array();\r\n       $formData[''notValidFields''] = array();\r\n\r\n       for($a = 0; $a < count($requiredFields); $a++){\r\n               $field = $requiredFields[$a];\r\n               if(isset($_POST[$field])){\r\n                       $value = $_POST[$field];\r\n                       if(empty($value)){\r\n                               $formData[''valid''] = false;\r\n                               $formData[''notValidFields''][] = $field;\r\n                       }else{\r\n                               $formData[''fields''][$field] = $value;\r\n                       }\r\n               }else{\r\n                       $formData[''valid''] = false;\r\n                       $formData[''notValidFields''][] = $field;\r\n               }\r\n       }\r\n       return $formData;\r\n}', 'POST validation loop   form get form data', 'php'),
(535, '<?php\r\n   /*\r\n\r\nYou''ll need a MySQL database with a a table called `rss_feed`. In that table there are 3 colums: item title (which is the name a person will see for an item), the item link (which is the location of the page with the item on it), and description (which tells what the feed is about). Put the file in a folder called feed and you''re feed will be available at yoursite.com/feed\r\n*/\r\n   // Connect to database... (you''ll need to create this yourself)\r\n   require_once $_SERVER[''DOCUMENT_ROOT''] . ''/includes/connection.php'';\r\n\r\n   // Run query...\r\n   $getFeed = mysql_query("SELECT *\r\n                           FROM `rss_feed`\r\n                           ORDER BY `time` DESC\r\n                           ")or die(mysql_error());\r\n\r\n   // Output XML (RSS)\r\n    echo ''<?xml version="1.0" encoding="ISO-8859-1" ?>\r\n          <rss version="2.0">\r\n                <channel>\r\n                        <title>Your RSS Title</title>\r\n                        <link>http://the_location_of_your_feed/feed</link>\r\n                        <description>Description of your Feed</description>\r\n                        <language>English</language>\r\n                        <image>\r\n                                <title>website Logo</title>\r\n                                <url></url>\r\n                                <link>Link to image</link>\r\n                                <width>width</width>\r\n                                <height>height</height>\r\n                        </image>'';\r\n						while($rssFeed = mysql_fetch_array($getFeed)) {\r\n        					 echo ''<item>'',\r\n					              ''<title>'', $rssFeed[''item_title'']</title>'',\r\n					              ''<link>'', $rssFeed[''link''], ''</link>'',\r\n					              ''<description><![CDATA[ ,$rssFeed[''description''],'']]></description>\r\n								   </item>'';\r\n\r\n 						}\r\n				echo ''</channel>\r\n        </rss>'';\r\n\r\n?>', 'RSS GENERATOR',