(543, '<!DOCTYPE html>\r\n<html>\r\n	<head>\r\n		 \r\n	</head>\r\n	<body>\r\n\r\n		 \r\n			<table id="table">\r\n				<tr>\r\n					<td>\r\n					<input type=''checkbox''  >\r\n					Click me!</td>\r\n					<td>Demodata 1</td>\r\n				</tr>\r\n				<tr>\r\n					<td>\r\n					<input type=''checkbox''  >\r\n					Click me!</td>\r\n					<td>Demodata 2</td>\r\n				</tr>\r\n			</table>\r\n		 <button onclick="removeAllRowsContainingCheckedCheckbox(''table'');">delete</button>\r\n		<script>\r\n			function removeAllRowsContainingCheckedCheckbox(id) {\r\n				\r\n				var table = document.getElementById(id);\r\n				\r\n				for (var rowi = table.rows.length; rowi-- > 0; ) {\r\n					var row = table.rows[rowi];\r\n					var inputs = row.getElementsByTagName(''input'');\r\n					for (var inputi = inputs.length; inputi-- > 0; ) {\r\n						var input = inputs[inputi];\r\n\r\n						if (input.type === ''checkbox'' && input.checked) {\r\n							row.parentNode.removeChild(row);\r\n							break;\r\n						}\r\n					}\r\n				}\r\n			}\r\n		</script>\r\n	</body>\r\n</html>\r\n', 'DELETE checked rows in a table checkbox ', 'javascript'),
