(596, '/*\r\n * \r\n * the frame code:\r\n * \r\n\r\ntrace(''make object with Array as arguments'');\r\nvar args:Array = new Array("John","janitor");\r\nvar f:foo = new foo(args);\r\nf.who();\r\n\r\n\r\ntrace(''make object with Object as arguments'');\r\nvar o:Object =  {name:''john'',job:''autoworker''};\r\nvar b:bar = new bar(o);\r\nb.who();\r\n\r\n\r\n * \r\n * \r\n * \r\n * \r\n */\r\n\r\n/*\r\n package  {\r\n	\r\n	public class foo {\r\n		var name:String;\r\n		var job:String;\r\n		public function foo(args:Array) {\r\n			// constructor code\r\n			 this.name = args[0];\r\n			 this.job = args[1];\r\n		}\r\n		\r\n		public function who():void{\r\n			 trace(this.name);\r\n			 trace(this.job);\r\n		}\r\n\r\n	}\r\n	\r\n}\r\n */\r\n\r\n/*\r\n package  {\r\n	\r\n	public class bar {\r\n		var name:String;\r\n		var job:String;\r\n		\r\n		public function bar(o:Object) {\r\n			// constructor code\r\n			this.name = o.name;\r\n			this.job = o.job;\r\n		}\r\n		public function who():void{\r\n			trace(this.name);\r\n			trace(this.job);\r\n		}\r\n\r\n	}\r\n	\r\n}\r\n */', 'actionscript: object instantiation with arguments', 'javascript'),
(597, 'function tallyCostPerTime(obj,rate_per_hr){\r\n    //  start loop obj{\r\n     days = days + parseFloat(obj[i][''days'']);\r\n     hrs =  hrs + parseFloat(obj[i][''hrs'']);\r\n     mins = mins + parseFloat(obj[i][''mins'']);\r\n     // end loop }\r\n     var sbt_days       = Math.round(rate_per_hr*days*8);//8hrs per #of days * rate\r\n     var sbt_hours      = Math.round(rate_per_hr*hrs);\r\n     var sbt_minutes    = Math.round(rate_per_hr*( mins/60));\r\n     var total_cost     = sbt_days+sbt_hours+sbt_minutes;\r\n}\r\n', 'tallyCostPerTime ', 'javascript');
