(598, 'import flash.text.TextField;\r\nimport flash.text.TextFormat;\r\nimport flash.external.ExternalInterface;\r\nvar queryString:String = String(ExternalInterface.call("function() { return window.location.search; }"));\r\ntrace(queryString);\r\n\r\nvar exampleString:String;\r\nexampleString = "?foo=bar&monkey=see&gorilla=do&src=http";\r\ntrace(exampleString);\r\nvar foo:String;\r\nvar monkey:String;\r\nvar gorilla:String;\r\nvar src:String;\r\n\r\nvar res = exampleString.split(''?'');\r\ntrace(res[1]);\r\nvar res2 = res[1].split(''&'');\r\ntrace(res2.length);\r\ntrace(res2[0]);\r\ntrace(res2[1]);\r\ntrace(res2[2]);\r\nvar map:Object = new Object();\r\nfor (var i=0; i< res2.length; i++)\r\n{\r\n	trace(i);\r\n	trace(res2[i]);\r\n	var resX = res2[i].split(''='');\r\n	trace(resX[0] + "----" + resX[1]);\r\n	//map.resX[0] = resX[1];\r\n	//switch \r\n	switch ( resX[0])\r\n	{\r\n		case "foo" :\r\n			foo = resX[1];\r\n			break;\r\n\r\n		case "monkey" :\r\n			monkey = resX[1];\r\n			break;\r\n		case "gorilla" :\r\n			gorilla = resX[1];\r\n			break;\r\n		case "src" :\r\n			src = resX[1];\r\n			break;\r\n		default :\r\n			break;\r\n	}\r\n\r\n\r\n\r\n}\r\n//trace(map);\r\n\r\ntrace(foo);\r\ntrace(monkey);\r\ntrace(gorilla);\r\ntrace(src);\r\n\r\n\r\n\r\n//TUTORIAL: http://www.republicofcode.com/tutorials/flash/as3text/\r\n\r\n//basic\r\n/*\r\nvar  myText:TextField = new TextField();\r\nmyText.text = "The quick red fox jumps over the lazy dog.";\r\naddChild(myText);\r\nmyText.textColor = 0xFF0000;\r\nmyText.border = true;\r\nmyText.wordWrap = true;\r\nmyText.width = 150;\r\nmyText.height = 50;\r\nmyText.x = 100;\r\nmyText.y = 100;\r\n*/\r\n//------------------------------------------\r\n// Using the TextFormat CLass\r\n// USING EMBEDDED FONTS\r\n\r\n//This step involved selecting a font from the library, exporting it as actionscript...\r\nvar myFont = new Font1();\r\n\r\n//1) the format class\r\nvar myFormat:TextFormat = new TextFormat();\r\nmyFormat.size = 25;\r\nmyFormat.font = myFont.fontName;\r\n//alignment options:\r\n//myFormat.align = TextFormatAlign.CENTER;\r\n//CENTER, LEFT, RIGHT , JUSTIFY\r\n//2 then the textField\r\nvar myText:TextField = new TextField();\r\n// important step to declare textfields default format class.\r\nmyText.defaultTextFormat = myFormat;\r\n//---add these 2 lines for fonts to work\r\nmyText.embedFonts = true;\r\nmyText.antiAliasType = AntiAliasType.ADVANCED;\r\n//\r\nmyText.text = queryString;// "The quick red fox jumps over the lazy dog.";\r\naddChild(myText);\r\nmyText.textColor = 0xFF0000;\r\nmyText.border = true;\r\nmyText.wordWrap = true;\r\nmyText.width = 500;\r\nmyText.height = 50;\r\nmyText.x = 100;\r\nmyText.y = 100;', 'actionscript: listen for query string vars and display them. (ROUGHLY)', 'javascript'),
