(608, '<!DOCTYPE html>\r\n<html>\r\n	<head>\r\n		<script src=''sandbox.js''></script>\r\n	</head>\r\n	<body>\r\n		<script>\r\n			// JAVASCRIPT NINJA: http://ejohn.org/apps/learn/#12\r\n			////function call itself\r\n			function yell(n) {\r\n				return n > 0 ? yell(n - 1) + "a" : "hiy";\r\n			}\r\n\r\n			assert(yell(4) == "hiyaaaa", "Calling the function by itself comes naturally.");\r\n			console.log(yell(1000));\r\n			//#12\r\n			var ninja = {\r\n				yell : function(n) {\r\n					return n > 0 ? ninja.yell(n - 1) + "a" : "hiy";\r\n				}\r\n			};\r\n			assert(ninja.yell(4) == "hiyaaaa", "A single object isn''t too bad, either.");\r\n			//#14\r\n			var ninja = {\r\n				yell : function yell(n) {\r\n					return n > 0 ? yell(n - 1) + "a" : "hiy";\r\n				}\r\n			};\r\n			assert(ninja.yell(4) == "hiyaaaa", "Works as we would expect it to!");\r\n\r\n			var samurai = {\r\n				yell : ninja.yell\r\n			};\r\n			var ninja = {};\r\n			assert(samurai.yell(4) == "hiyaaaa", "The method correctly calls itself.");\r\n			//#15\r\n			var ninja = {\r\n				yell : function(n) {\r\n					return n > 0 ? arguments.callee(n - 1) + "a" : "hiy";\r\n				}\r\n			};\r\n			assert(ninja.yell(4) == "hiyaaaa", "arguments.callee is the function itself.");\r\n			//#18 how similar are functions and objects\r\n			var obj = {};\r\n			var fn = function() {\r\n			};\r\n			obj.prop = "some value";\r\n			fn.prop = "some value";\r\n			assert(obj.prop == fn.prop, "Both are objects, both have the property.");\r\n			//#19 cache results of function\r\n			function getElements(name) {\r\n				var results;\r\n\r\n				if (getElements.cache[name]) {\r\n					results = getElements.cache[name];\r\n				} else {\r\n					results = document.getElementsByTagName(name);\r\n					getElements.cache[name] = results;\r\n				}\r\n\r\n				return results;\r\n			}\r\n\r\n\r\n			getElements.cache = {};\r\n\r\n			log("Elements found: ", getElements("pre").length);\r\n			log("Cache found: ", getElements.cache.pre.length);\r\n			//#28 Function Looping\r\n			function loop(array, fn) {\r\n				for (var i = 0; i < array.length; i++)\r\n					fn.call(array, array[i], i);\r\n			}\r\n\r\n			var num = 0;\r\n			loop([0, 1, 2], function(value, i) {\r\n				assert(value == num++, "Make sure the contents are as we expect it.");\r\n				assert(this instanceof Array, "The context should be the full array.");\r\n			});\r\n			//#31  ''this'' context\r\n			function Ninja() {\r\n				this.swung = false;\r\n\r\n				// Should return true\r\n				this.swingSword = function() {\r\n					this.swung = !this.swung;\r\n					return this.swung;\r\n				};\r\n			}\r\n\r\n			var ninja = new Ninja();\r\n			assert(ninja.swingSword(), "Calling the instance method.");\r\n			assert(ninja.swung, "The ninja has swung the sword.");\r\n\r\n			var ninjaB = new Ninja();\r\n			assert(!ninjaB.swung, "Make sure that the ninja has not swung his sword.");\r\n			//#40 Using a variable number of arguments\r\n			function merge(root) {\r\n				for (var i = 1; i < arguments.length; i++)\r\n					for (var key in arguments[i] )\r\n					root[key] = arguments[i][key];\r\n				return root;\r\n			}\r\n\r\n			var merged = merge({\r\n				name : "John"\r\n			}, {\r\n				city : "Boston"\r\n			});\r\n			assert(merged.name == "John", "The original name is intact.");\r\n			assert(merged.city == "Boston", "And the city has been copied over.");\r\n			//#41 find min max in an array\r\n			function smallest(array) {\r\n				return Math.min.apply(Math, array);\r\n			}\r\n\r\n			function largest(array) {\r\n				return Math.max.apply(Math, array);\r\n			}\r\n\r\n			assert(smallest([0, 1, 2, 3]) == 0, "Locate the smallest value.");\r\n			assert(largest([0, 1, 2, 3]) == 3, "Locate the largest value.");\r\n\r\n			//#49 basic closure:\r\n			var num = 10;\r\n\r\n			function addNum(myNum) {\r\n				return num + myNum;\r\n			}\r\n\r\n			assert(addNum(5) == 15, "Add two numbers together, one from a closure.");\r\n			//#51: Closures used for callback:\r\n			var results = jQuery("#results").html("<li>Loading...</li>");\r\n\r\n			jQuery.get("test.html", function(html) {\r\n				results.html(html);\r\n				assert(results, "The element to append to, via a closure.");\r\n			});\r\n			//#52 closures for timers\r\n			var count = 0;\r\n\r\n			var timer = setInterval(function() {\r\n				if (count < 5) {\r\n					log("Timer call: ", count);\r\n					count++;\r\n				} else {\r\n					assert(count == 5, "Count came via a closure, accessed each step.");\r\n					assert(timer, "The timer reference is also via a closure.");\r\n					clearInterval(timer);\r\n				}\r\n			}, 100);\r\n			//#53 closures to attach event listeners\r\n			var count = 1;\r\n			var elem = document.createElement("li");\r\n			elem.innerHTML = "Click me!";\r\n			elem.onclick = function() {\r\n				log("Click #", count++);\r\n			};\r\n			document.getElementById("results").appendChild(elem);\r\n			assert(elem.parentNode, "Clickable element appended.");\r\n			//#54: Private properties using closures\r\n			function Ninja() {\r\n				var slices = 0;\r\n\r\n				this.getSlices = function() {\r\n					return slices;\r\n				};\r\n				this.slice = function() {\r\n					slices++;\r\n				};\r\n			}\r\n\r\n			var ninja = new Ninja();\r\n			ninja.slice();\r\n			assert(ninja.getSlices() == 1, "We''re able to access the internal slice data.");\r\n			assert(ninja.slices === undefined, "And the private data is inaccessible to us.");\r\n			///  TEMPORARY SCOPE:\r\n			//#58 Self-executing temporary function\r\n			(\r\n				function() {\r\n					var count = 0;\r\n\r\n					var timer = setInterval(function() {\r\n						if (count < 5) {\r\n							log("Timer call: ", count);\r\n							count++;\r\n						} else {\r\n							assert(count == 5, "Count came via a closure, accessed each step.");\r\n							assert(timer, "The timer reference is also via a closure.");\r\n							clearInterval(timer);\r\n						}\r\n					}, 100);\r\n				})();\r\n\r\n			assert( typeof count == "undefined", "count doesn''t exist outside the wrapper");\r\n			assert( typeof timer == "undefined", "neither does timer");\r\n			// #59: closures and looping\r\n			for (var d = 0; d < 3; d++)\r\n				(function(d) {\r\n					setTimeout(function() {\r\n						log("Value of d: ", d);\r\n						assert(d == d, "Check the value of d.");\r\n					}, d * 200);\r\n				})(d);\r\n			//#60 : Anonymous Wrapper\r\n			(function() {\r\n				var myLib = window.myLib = function() {\r\n					// Initialize\r\n				};\r\n\r\n				// ...\r\n			})();\r\n			//#61: another way to wrap a library:\r\n			var myLib = (function() {\r\n				function myLib() {\r\n					// Initialize\r\n				}\r\n\r\n				// ...\r\n\r\n				return myLib;\r\n			})();\r\n			//THESE GO ALL THE WAY TO #90\r\n			\r\n		</script>\r\n	</body>\r\n</html>', 'ninja script: thanks to http://ejohn.org/apps/learn/#70', 'javascript'),
