(623, '	function rotateCClockwise(id) {\r\n		$(''#'' + id).animate({\r\n			borderSpacing : -360\r\n		}, {\r\n			step : function(now, fx) {\r\n				$(this).css(''-webkit-transform'', ''rotate('' + now + ''deg)'');\r\n				$(this).css(''-moz-transform'', ''rotate('' + now + ''deg)'');\r\n				$(this).css(''transform'', ''rotate('' + now + ''deg)'');\r\n			},\r\n			duration : 24000\r\n		}, ''linear'');\r\n	}\r\n\r\n	function rotateClockwise(id) {\r\n		$(''#'' + id).animate({\r\n			borderSpacing : 360\r\n		}, {\r\n			step : function(now, fx) {\r\n				$(this).css(''-webkit-transform'', ''rotate('' + now + ''deg)'');\r\n				$(this).css(''-moz-transform'', ''rotate('' + now + ''deg)'');\r\n				$(this).css(''transform'', ''rotate('' + now + ''deg)'');\r\n			},\r\n			duration : 24000\r\n		}, ''linear'');\r\n	}\r\n\r\n	function leftRight(targetElement, speed) {\r\n		$(targetElement).animate({\r\n			marginLeft : "+=25px"\r\n		}, {\r\n			duration : speed,\r\n			complete : function() {\r\n				targetElement.animate({\r\n					marginLeft : "-=25px"\r\n				}, {\r\n					duration : speed,\r\n					complete : function() {\r\n						leftRight(targetElement, speed);\r\n					}\r\n				});\r\n			}\r\n		});\r\n	}\r\n\r\n	function rightLeft(targetElement, speed) {\r\n		$(targetElement).animate({\r\n			marginLeft : "-=25px"\r\n		}, {\r\n			duration : speed,\r\n			complete : function() {\r\n				targetElement.animate({\r\n					marginLeft : "+=25px"\r\n				}, {\r\n					duration : speed,\r\n					complete : function() {\r\n						rightLeft(targetElement, speed);\r\n					}\r\n				});\r\n			}\r\n		});\r\n	}\r\n\r\n	function upDown(targetElement, speed) {\r\n		$(targetElement).animate({\r\n			marginTop : "+=25px"\r\n		}, {\r\n			duration : speed,\r\n			complete : function() {\r\n				targetElement.animate({\r\n					marginTop : "-=25px"\r\n				}, {\r\n					duration : speed,\r\n					complete : function() {\r\n						upDown(targetElement, speed);\r\n					}\r\n				});\r\n			}\r\n		});\r\n	}\r\n\r\n	function downU