(654, '<!-- ======================================================================================   -->\r\n<!-- ====== HTML GENERATOR ================================================================   -->\r\n<!-- \r\n	1) place this at the bottom of your page.\r\n	2) hit the button\r\n	3) copy and paste the html it creates.\r\n	\r\n-->\r\n		<script>\r\n			function saveChangesHTML() {\r\n				document.getElementById(''generatedHTML'').style.display = "block";\r\n			var generated_html = document.documentElement.innerHTML;\r\n			var safeHTML = escapeHtml(generated_html);\r\n			document.getElementById(''generatedHTML'').innerHTML = "<pre>"+safeHTML+"</pre>";\r\n		}\r\n		function escapeHtml(unsafe) {\r\n			    return unsafe\r\n			         .replace(/&/g, "&amp;")\r\n			         .replace(/</g, "&lt;")\r\n			         .replace(/>/g, "&gt;")\r\n			         .replace(/"/g, "&quot;")\r\n			         .replace(/''/g, "&#039;");\r\n			 }\r\n			 function hideHTML(){\r\n			 	document.getElementById(''generatedHTML'').style.display = "none";\r\n			 }\r\n		</script>\r\n		<style>\r\n		.html_view_row{text-align:left;}\r\n			.html_view{\r\n				display:none;\r\n				 margin-top:50px;\r\n				width:100%;\r\n				min-height:300px;\r\n				max-height:300px;\r\n				overflow:hidden  ;\r\n				overflow-y:scroll;\r\n				background-color:#222;\r\n				color:orange;\r\n			}\r\n		</style>\r\n		<div class=''row''>\r\n			<div class=''col-12 col-m-12''>\r\n						<button type=''button'' onclick=''saveChangesHTML()''>view generated html</button>&nbsp;&nbsp;<button type=''button'' onclick=''hideHTML()''>hide html</button>\r\n\r\n			</div>\r\n		</div>\r\n		<div class='' html_view_row'' >\r\n			\r\n			<div id=''generatedHTML'' class=''html_view''></div>\r\n		</div>\r\n<!-- ===========================================================================================  -->', 'view generated HTML - plug and play JS', 'javascript'),
