(656, ' /* CSS Document */\r\n\r\n/* Genral Grid Layout  | iPad Kiosk */\r\n/*\r\n * rwd-grid.css is a compilation of code created at w3schools. w3css\r\n * Thanks to: http://www.w3schools.com/css/css_rwd_grid.asp\r\n */\r\n/* Website Produced by Codigo | gocodigo.com */\r\n\r\n/* --------------------------------------------------\r\nCSS Table of Contents // Primary CSS Doc\r\n-----------------------------------------------------\r\n1. General Grid CSS\r\n\r\n3. Basic Colors\r\n4. RWD\r\n\r\n********************************************* */\r\n\r\n\r\n/**********************/\r\n/*  1. General */\r\n\r\nhtml, body {\r\n	width: 100%;\r\n	height: 100%;\r\n	text-align:center;\r\n	font-family: "Lucida Sans", sans-serif;\r\n}\r\n \r\n\r\n* {\r\n	margin: 0px 0px 0px 0px;\r\n	padding: 0px 0px 0px 0px\r\n}\r\n/* set all items box sizing property*/\r\n* {\r\n	box-sizing: border-box;\r\n}\r\n/*when placed on the body tag, keeps page centered and at maximum width limitation.*/\r\n.grid-contain{\r\n	max-width: 1024px;\r\n	margin: 0 auto;\r\n}\r\n.main {\r\n	width: 100%;\r\n	float: left;\r\n	background-color:none;\r\n	/*height: 100%;\r\n	width:90%;\r\n	margin-left:5%;*/\r\n}\r\n \r\n.col-1 {\r\n	width: 8.33%;\r\n}\r\n.col-2 {\r\n	width: 16.66%;\r\n}\r\n.col-3 {\r\n	width: 25%;\r\n}\r\n.col-4 {\r\n	width: 33.33%;\r\n}\r\n.col-5 {\r\n	width: 41.66%;\r\n}\r\n.col-6 {\r\n	width: 50%;\r\n}\r\n.col-7 {\r\n	width: 58.33%;\r\n}\r\n.col-8 {\r\n	width: 66.66%;\r\n}\r\n.col-9 {\r\n	width: 75%;\r\n}\r\n.col-10 {\r\n	width: 83.33%;\r\n}\r\n.col-11 {\r\n	width: 91.66%;\r\n}\r\n.col-12 {\r\n	width: 100%;\r\n}\r\n[class*="col-"] {\r\n	float: left;\r\n	/*padding: 15px;border: 1px solid #ddd;*/\r\n	\r\n}\r\n/* .h- class is for filling space horizontally inside a .col class by percentage */\r\n.h-100prct{height:100%;border:dotted 1px #222;}\r\n.h-75prct{height:75%;border:dotted 1px #222;}\r\n.h-66prct{height:66%;border:dotted 1px #222;}\r\n.h-50prct{height:50%;border:dotted 1px #222;}\r\n.h-33prct{height:33%;border:dotted 1px #222;}\r\n.h-25prct{height:25%;border:dotted 1px #222;}\r\n/* pad */\r\n.pad-5{ padding:5px; }\r\n.pad-10{ padding:10px;}\r\n.pad-15{ padding:15px;}\r\n.pad-B-150{ padding-bottom:150px;}\r\n.txt-align-c{text-align:center;}\r\n.txt-align-r{text-align:right;}\r\n.txt-align-l{text-align:left;}\r\n/* .row:after\r\n * The columns inside a row are all floating to the left,\r\n * and are therefore taken out of the flow of the page,\r\n * and other elements will be placed as if the columns does not exist.\r\n * To prevent this, we will add a style that clears the flow:\r\n * */\r\n.row:after {\r\n	content: "";\r\n	clear: both;\r\n	display: block;\r\n}\r\n\r\n\r\n/***************************/\r\n/* 2. Basic Colors 	 */\r\n \r\n.white { background-color: #fff; }\r\n.black { background-color: #000; }\r\n.gray { background-color: #ccc; }\r\n \r\n\r\n/*********************************/\r\n/* 3. MODAL thanks to w3css  */\r\n.w3-modal{z-index:3;display:none;padding-top:100px;position:fixed;left:0;top:0;width:100%;height:100%;overflow:auto;background-color:rgb(0,0,0);background-color:rgba(0,0,0,0.4)}\r\n.w3-modal-content{margin:auto;background-color:#fff;position:relative;padding:0;outline:0;width:600px}.w3-closebtn{text-decoration:none;float:right;font-size:24px;font-weight:bold;color:inherit}\r\n.w3-container{padding:0.01em 16px}\r\n.w3-container:after,.w3-row:after,.w3-row-padding:after,.w3-topnav:after,.w3-clear:after,.w3-btn-group:before,.w3-btn-group:after{content:"";display:table;clear:both}\r\n.w3-closebtn:hover,.w3-closebtn:focus{color:#000;text-decoration:none;cursor:pointer}\r\n\r\n\r\n/*********************************/\r\n/* 4. RWD  */\r\n@media (max-width:600px){.w3-modal-content{margin:50px 10px 10px 10px;width:auto !important}}\r\n@media (max-width:768px){.w3-modal-content{width:500px}}\r\n@media (min-width:993px){.w3-modal-content{width:900px}}\r\n \r\n@media only screen and (max-width: 500px) {\r\n	body {\r\n		background-color: lightblue;\r\n	}\r\n}\r\n@media only screen and (max-width: 768px) {\r\n	/* For mobile phones: */\r\n	[class*="col-"] {\r\n		width: 100%;\r\n	}\r\n	 \r\n	[class*="horiz-"] {\r\n		width: 100%;\r\n	}\r\n}\r\n/* Break Points */\r\n@media only screen and (min-width: 600px) {\r\n	/* For tablets: */\r\n	.col-m-1 {\r\n		width: 8.33%;\r\n	}\r\n	.col-m-2 {\r\n		width: 16.66%;\r\n	}\r\n	.col-m-3 {\r\n		width: 25%;\r\n	}\r\n	.col-m-4 {\r\n		width: 33.33%;\r\n	}\r\n	.col-m-5 {\r\n		width: 41.66%;\r\n	}\r\n	.col-m-6 {\r\n		width: 50%;\r\n	}\r\n	.col-m-7 {\r\n		width: 58.33%;\r\n	}\r\n	.col-m-8 {\r\n		width: 66.66%;\r\n	}\r\n	.col-m-9 {\r\n		width: 75%;\r\n	}\r\n	.col-m-10 {\r\n		width: 83.33%;\r\n	}\r\n	.col-m-11 {\r\n		width: 91.66%;\r\n	}\r\n	.col-m-12 {\r\n		width: 100%;\r\n	}\r\n}\r\n@media only screen and (min-width: 768px) {\r\n	/* For desktop: */\r\n	.col-1 {\r\n		width: 8.33%;\r\n	}\r\n	.col-2 {\r\n		width: 16.66%;\r\n	}\r\n	.col-3 {\r\n		width: 25%;\r\n	}\r\n	.col-4 {\r\n		width: 33.33%;\r\n	}\r\n	.col-5 {\r\n		width: 41.66%;\r\n	}\r\n	.col-6 {\r\n		width: 50%;\r\n	}\r\n	.col-7 {\r\n		width: 58.33%;\r\n	}\r\n	.col-8 {\r\n		width: 66.66%;\r\n	}\r\n	.col-9 {\r\n		width: 75%;\r\n	}\r\n	.col-10 {\r\n		width: 83.33%;\r\n	}\r\n	.col-11 {\r\n		width: 91.66%;\r\n	}\r\n	.col-12 {\r\n		width: 100%;\r\n	}\r\n}\r\n/* Orientation */\r\n@media only screen and (orientation: landscape) {\r\n	body {\r\n		background-color: lightblue;\r\n	}\r\n}\r\n', 'rwd-grid.css w3', 'css'),
(657, '<!DOCTYPE html>\r\n<html>\r\n	<head>\r\n		<meta charset=''utf-8''>\r\n		<!-- <link rel=''stylesheet'' type=''text/css'' href=''style.css''> -->\r\n		<script src=''ajax.js''></script>\r\n		<script src=''jquery.js''></script>\r\n		<script src=''ls_data.js''></script>\r\n		<title>HTML DOC</title>\r\n	</head>\r\n	<body>\r\n		<script>\r\n			var people = {\r\n				"COLUMNS" : ["id", "NAME", "AGE"],\r\n				"DATA" : [["7", "Jon", "16"], ["9", "Jerry", "23"]]\r\n			};\r\n			console.log(people);\r\n			people.tablename = ''people'';\r\n			ls_setTable(people);\r\n			var retrieved_data = ls_getTable(''people'');\r\n			//FIND\r\n			var id = "7";\r\n			ls_queryDataById(id, retrieved_data);\r\n		</script>\r\n		<script>\r\n			// code for ls_data.js\r\n			/*\r\n			 * ls_data.js\r\n			 * Local Storage Data\r\n			 * Each Table is an JSON Object AND has a ''id'' property attached as well.\r\n			 * ie.  var table = {<json>};\r\n			 * 			table.tablename = ''myTable'';\r\n			 *\r\n			 *\r\n			 */\r\n			function ls_setTable(obj) {\r\n				var json = JSON.stringify(obj);\r\n				localStorage.setItem(obj.tablename, json);\r\n			}\r\n\r\n			function ls_getTable(id) {\r\n				var json = localStorage.getItem(id);\r\n				var obj = JSON.parse(json);\r\n				return obj;\r\n			}\r\n\r\n			function ls_logQueryResult(obj) {\r\n				var json = JSON.stringify(obj);\r\n				localStorage.setItem(obj.tablename, json);\r\n			}\r\n\r\n			function ls_queryDataById(id, data) {\r\n				for (var i = 0; i < 10; i++) {\r\n					var q = data[''DATA''][i];\r\n					if ( typeof q != ''undefined'') {\r\n						if (q[0] == id) {\r\n							console.log(q);\r\n							console.log(''awesomeness'');\r\n							q.tablename = ''query_'' + data.tablename;\r\n							ls_logQueryResult(q);\r\n						}\r\n					}\r\n				}\r\n			}\r\n		</script>\r\n\r\n	</body>\r\n</html>', 'json localStorage database (in progress)', 'json'),
