(661, '<!DOCTYPE html>\r\n<html>\r\n	<head>\r\n		<meta charset=''utf-8''>\r\n		<!-- <link rel=''stylesheet'' type=''text/css'' href=''style.css''> -->\r\n		<script src=''ajax.js''></script>\r\n		<script src=''jquery-1.11.2.min.js''></script>\r\n		<script src="ajax.js"></script>\r\n		<script src=''ls_data.js''></script>\r\n		<title>HTML DOC</title>\r\n	</head>\r\n	<body>\r\n		<script>\r\n		  \r\n			//get mysql data\r\n			var dataString = "method=getLsData&table=people";\r\n			var URL = "serverside.php";\r\n			ajax_datastring_URL_callback(dataString, URL, callbackPeople);\r\n			\r\n			function callbackPeople(response){\r\n				console.log(''callbackPeople response'');\r\n				console.log(response);\r\n				var obj = JSON.parse(response);\r\n				 \r\n				 //Prepare Object for Local Storage\r\n				var people = {};\r\n				people.tablename = "people"; \r\n				people.data = obj;\r\n				console.log(people);\r\n				//store it in local storage\r\n				ls_setTable(people);\r\n				/// retrieve it from local storage\r\n				var newdata = ls_getTable(''people'');\r\n				console.log(''retrieved data'');\r\n				console.log(newdata);\r\n				\r\n				ls_queryDataById("2", newdata);\r\n				\r\n				\r\n				var record = ls_getItem(''ls_lastQuery'');\r\n				console.log(''record'');\r\n				console.log(record);\r\n				//change field value\r\n				record.NAME = "SandraGG";\r\n				console.log(record);\r\n				// save it in the local staorage.\r\n				//get ''people'' table \r\n				var table = JSON.parse(localStorage.getItem(''people''));\r\n				console.log(table);\r\n			\r\n				//loop/search for record.id\r\n				ls_updateDataByIdKeyandValue(record.id, table, ''NAME'', record.NAME);\r\n				//change data.data[i].field = newValue;\r\n				\r\n				\r\n				\r\n				\r\n			}\r\n		</script>\r\n		 <script>\r\n		 	//below is the js for ls_data.js\r\n		 	// code for ls_data.js\r\n/*\r\n * ls_data.js\r\n * Local Storage Data\r\n * Each Table is an JSON Object AND has a ''id'' property attached as well.\r\n * ie.  var table = {<json>};\r\n * 			table.tablename = ''myTable'';\r\n *\r\n *\r\n */\r\nfunction ls_setTable(obj) {\r\n	var json = JSON.stringify(obj);\r\n	localStorage.setItem(obj.tablename, json);\r\n}\r\n\r\nfunction ls_getTable(id) {\r\n	var json = localStorage.getItem(id);\r\n	var obj = JSON.parse(json);\r\n	return obj;\r\n}\r\n\r\nfunction ls_logQueryResult(obj) {\r\n\r\n	console.log(''ls_logQueryResult'');\r\n\r\n	console.log(obj);\r\n	var json = JSON.stringify(obj);\r\n	localStorage.setItem("ls_lastQuery", json);\r\n}\r\n\r\nfunction ls_queryDataById(id, data) {\r\n	console.log("FIX IT");\r\n	console.log(data);\r\n	console.log(data.data);\r\n	console.log(data.data.length);\r\n	var max = data.data.length;\r\n	console.log(data.data[0]);\r\n	for (var i = 0; i < max; i++) {\r\n		var q = data.data[i].id;\r\n		if ( typeof q != ''undefined'') {\r\n			if (q == id) {\r\n				console.log(q);\r\n				ls_logQueryResult(data.data[i]);\r\n			}\r\n		}\r\n	}\r\n}\r\n\r\nfunction ls_getItem(id) {\r\n	var json = localStorage.getItem(id);\r\n	var obj = JSON.parse(json);\r\n	return obj;\r\n}\r\n\r\nfunction ls_updateDataByIdKeyandValue(id, table, key, value) {\r\n	console.log("UPDATE IT");\r\n	console.log(id);\r\n	console.log(table);\r\n	console.log(key);\r\n	console.log(value);\r\n	var data = table.data;\r\n	var max = data.length;\r\n	for (var i = 0; i < max; i++) {\r\n		 var q =  data[i].id;\r\n		 if ( typeof q != ''undefined'') {\r\n			 if (q == id) {\r\n				 console.log(q);\r\n				 data[i][key] = value;\r\n			 }\r\n		 }\r\n	 }\r\n	 console.log(''new data '');\r\n	 data.tablename = table.tablename;\r\n	 console.log(data);\r\n	 ls_setTable(data);\r\n	 \r\n}\r\n\r\n\r\n		 	\r\n		 </script>\r\n\r\n	</body>\r\n</html>\r\n\r\n<?php\r\n// server_side.php is this...\r\n<?php\r\n\r\nif(isset($_POST[''method''])){\r\n    \r\n    // switch be table names\r\n    switch ($_POST[''method'']) {\r\n        case ''getLsData'' :\r\n            \r\n            if($_POST[''table''] == ''people''){\r\n                $data = getPeople();\r\n                echo($data);\r\n            }\r\n            \r\n            break;\r\n\r\n        default :\r\n            break;\r\n    }\r\n    \r\n}\r\n\r\n\r\nfunction getPeople(){\r\n   \r\n    $HOST = "localhost";\r\n    $USER = "root";\r\n    $PASS = "";\r\n    $DB="ls_data";\r\n    $TABLE="people";\r\n\r\n    $con=mysqli_connect($HOST,$USER,$PASS,$DB);\r\n    // Check connection\r\n    if (mysqli_connect_errno())\r\n      {\r\n      echo "Failed to connect to MySQL: " . mysqli_connect_error();\r\n      }\r\n    $sql="SELECT * FROM ".$TABLE."  ";\r\n    //echo("<br>".$sql);\r\n    $result=mysqli_query($con,$sql);\r\n    while($row=mysqli_fetch_assoc($result)){\r\n       $data[] = $row; \r\n    }\r\n    //echo("</br>");\r\n    //echo("<pre>");print_r($data);echo("</pre>");\r\n    \r\n    //echo(json_encode($data));\r\n    // Free result set\r\n    mysqli_free_result($result);\r\n    mysqli_close($con);\r\n\r\n    return json_encode($data);\r\n}\r\n\r\n\r\n\r\n \r\n\r\n?>\r\n\r\n?>', 'json ls_data  ls_D', 'json'),
(662, '\r\n/* THIS is example of querying 1 row and making an editable form of inputs.\r\n * Here I inserted elemental.js inputs into a form with id=''nut_form''.with a button.\r\n * \r\n * function element_callback(data) {\r\n				console.log(''element callback'');\r\n				var obj = data[0];\r\n				var e = data[1];\r\n				console.log(obj.id + "- " + e.type);\r\n				switch(obj.id) {\r\n					case ''input4'':\r\n						//input4 or whatever the form button id is.						 \r\n						var serial_data = $(''#nut_form'').serializeArray();\r\n						var dataString = ''[{'';\r\n						var i=0;\r\n						$.each(serial_data, function(i, field) {\r\n							var record = "";\r\n							if(i==0){\r\n								record += ''"''+field.name + ''":"''+ field.value +''"'';\r\n							}else{\r\n								record += '',"''+field.name + ''":"''+ field.value +''"'';\r\n							}\r\n							i++;\r\n							dataString += record ;\r\n						});\r\n						dataString += ''}]'';\r\n						console.log(dataString);\r\n						var obj_dataString = JSON.parse(dataString);\r\n						console.log(obj_dataString);\r\n						//FROM HERE you can ''UPDATE'' the record ie.\r\n						//ls_updateDataByIdKeyandValue(id, table, key, value) \r\n						break;\r\n					case "foo":\r\n						//code block\r\n						break;\r\n					default:\r\n					//default code block\r\n				}\r\n			}\r\n */\r\n', 'serializeArray get data from FORM ', 'javascript'),
