(664, '<!doctype html>\r\n<html>\r\n<head>\r\n    <script>\r\n    	var STATE = "";\r\n        function CheckOnlineStatus(msg) {\r\n            var condition = navigator.onLine ? "ONLINE" : "OFFLINE";           \r\n            STATE = condition;  \r\n            console.log(''STATE is'');\r\n            console.log(STATE);       \r\n        }\r\n        function Pageloaded() {\r\n           CheckOnlineStatus("load");\r\n            document.body.addEventListener("offline", function () {\r\n                CheckOnlineStatus("offline")\r\n            }, false);\r\n            document.body.addEventListener("online", function () {\r\n                CheckOnlineStatus("online")\r\n            }, false);\r\n        }\r\n    </script>\r\n    \r\n</head>\r\n<body onload="Pageloaded()">\r\n   \r\n</body>\r\n</html>', 'check if online or offline internet connection', 'javascript'),
(665, '<!doctype html>\r\n<html>\r\n	<head>\r\n\r\n		<script>\r\n			var loopInterval;\r\n			function initLoop() {\r\n				loopInterval = setInterval(Pageloaded, 2000);\r\n			}\r\n\r\n			var STATE = "";\r\n			function CheckOnlineStatus(msg) {\r\n				var condition = navigator.onLine ? "ONLINE" : "OFFLINE";\r\n				STATE = condition;\r\n				console.log(''STATE is'');\r\n				console.log(STATE);\r\n			}\r\n\r\n			function Pageloaded() {\r\n				CheckOnlineStatus("load");\r\n				document.body.addEventListener("offline", function() {\r\n					CheckOnlineStatus("offline")\r\n				}, false);\r\n				document.body.addEventListener("online", function() {\r\n					CheckOnlineStatus("online")\r\n				}, false);\r\n			}\r\n		</script>\r\n\r\n	</head>\r\n	<body onload="initLoop()">\r\n\r\n	</body>\r\n</html>', 'loop to check online offline connection status', 'javascript'),
