(667, '<html>	\r\n<script src="voucher_print.js"></script>\r\n<script>\r\n	function VoucherSourcetoPrint(source) {\r\n	return "<html><head><script>function step1(){\\n" + "setTimeout(''step2()'', 10);}\\n" + "function step2(){window.print();window.close()}\\n" + "</scri" + "pt></head><body onload=''step1()''>\\n" + "<img src=''" + source + "'' /></body></html>";\r\n}\r\n\r\nfunction VoucherPrint(source) {\r\n	Pagelink = "about:blank";\r\n	var pwa = window.open(Pagelink, "_new");\r\n	pwa.document.open();\r\n	pwa.document.write(VoucherSourcetoPrint(source));\r\n	pwa.document.close();\r\n}\r\n</script>\r\n<body>\r\n	<div style="width: 400px; float: left;">\r\n		<!-- <img src="lighthouse_545x249.jpg" style="width: 545px; height: 249px;" alt="Voucher 1" /> -->\r\n		<a href="#" onclick="VoucherPrint(''lighthouse_545x249.jpg''); return false;" class="myButton"> Print Voucher</a> \r\n	</div>\r\n</body>\r\n</html>', 'javascript printer voucher print browser printer', 'javascript'),
