(678, '<?php\r\n$to= "admin@forteworks.com";\r\n$from= "bayon@gocodigo.com";\r\n$subject="basic mail from php";\r\n$msg="foof!";\r\n$file = "kioskfiles.xml";\r\n//mail($to,$subject,$msg);\r\n\r\n\r\nmail_attachment($to, $subject, $msg, $from, $file);\r\n\r\n function mail_attachment($to, $subject, $message, $from, $file) {\r\n  // $file should include path and filename\r\n  $filename = basename($file);\r\n  $file_size = filesize($file);\r\n  $content = chunk_split(base64_encode(file_get_contents($file)));\r\n  $uid = md5(uniqid(time()));\r\n  $from = str_replace(array("\\r", "\\n"), '''', $from); // to prevent email injection\r\n  $header = "From: ".$from."\\r\\n"\r\n      ."MIME-Version: 1.0\\r\\n"\r\n      ."Content-Type: multipart/mixed; boundary=\\"".$uid."\\"\\r\\n\\r\\n"\r\n      ."This is a multi-part message in MIME format.\\r\\n"\r\n      ."--".$uid."\\r\\n"\r\n      ."Content-type:text/plain; charset=iso-8859-1\\r\\n"\r\n      ."Content-Transfer-Encoding: 7bit\\r\\n\\r\\n"\r\n      .$message."\\r\\n\\r\\n"\r\n      ."--".$uid."\\r\\n"\r\n      ."Content-Type: application/octet-stream; name=\\"".$filename."\\"\\r\\n"\r\n      ."Content-Transfer-Encoding: base64\\r\\n"\r\n      ."Content-Disposition: attachment; filename=\\"".$filename."\\"\\r\\n\\r\\n"\r\n      .$content."\\r\\n\\r\\n"\r\n      ."--".$uid."--";\r\n  return mail($to, $subject, "", $header);\r\n }\r\n\r\n\r\n\r\n\r\n\r\n/*\r\nrequire_once(''../class.phpmailer.php'');\r\n\r\n$mail             = new PHPMailer(); // defaults to using php "mail()"\r\n\r\n$body             = file_get_contents(''contents.html'');\r\n$body             = eregi_replace("[\\]",'''',$body);\r\necho($body);\r\n$mail->AddReplyTo(''bayon@gocodigo.com'',"First Last");\r\n\r\n$mail->SetFrom(''bayon@gocodigo.com'', ''First Last'');\r\n\r\n$mail->AddReplyTo(''admin@gocodigo.com'',"First Last");\r\n\r\n$address = "admin@forteworks.com";\r\n$mail->AddAddress($address, "John Doe");\r\n\r\n$mail->Subject    = "PHPMailer Test Subje